void	set_torus(float t1)
{
    vec3	p1, ang1, p2, ang2, p3, ang3;
    vec2	s1, s2, ret;

    float dd = 3.0;
    float ddpp = 7.0;
    p1  = vec3(0, dd / 2.0, ddpp);
    p2  = vec3(-dd, -dd / 2.0, ddpp);
    p3  = vec3(dd, -dd / 2.0, ddpp);

    ang1 = vec3(t1, t1 * 0.3, t1 * 0.4);
    ang2 = vec3(t1 * 0.3, t1, t1 * 0.4);
    ang3 = vec3(t1 * 0.3, t1 * 0.4, t1);

    s1  = vec2(0.6, 0.3);
    s2  = vec2(0.0, 0.2);

    tt1  = set_torus(p1, ang1, s1, s2);
    tt2  = set_torus(p2, ang2, s1, s2);
    tt3  = set_torus(p3, ang3, s1, s2);
}


void	set_bezier_path(float t2)
{
    if      (mod(t2, 3.0) <= 1.0)
        bb = set_bezier_coef(tt1, tt2);
    else if (mod(t2, 3.0) <= 2.0)
        bb = set_bezier_coef(tt2, tt3);
    else if (mod(t2, 3.0) <= 3.0)
        bb = set_bezier_coef(tt3, tt1);

    bbd = bezier_derivate(bb);
}

