//defining a simple primitive - circle
float circle(in vec2 position, in vec2 centre, in float radius)
{
    vec2 pc = position-centre;
    return pc.x*pc.x + pc.y*pc.y - radius*radius;
}

float ellipse(in vec2 position, in vec2 centre, in float radius1, in float radius2)
{
    vec2 pc = position-centre;
    return (pc.x*pc.x)/(radius1*radius1) + (pc.y*pc.y)/(radius2*radius2) - 1.0;
}

float tanglecube(in vec2 position, in float z)
{
    float x = position.x; 
    float y = position.y;
    return x*x*x*x - 5.0*x*x + y*y*y*y - 5.0*y*y + z*z*z*z - 5.0*z*z + 11.8;
}


void mainImage( out vec4 fragColor, in vec2 fragCoord ) {
    //taking fragment coordinates, range [0,1]
	vec2 uv = fragCoord.xy / iResolution.xy;

    //map fragment coordinates to [-1,1]
    vec2 c = 2.0 * uv - 1.0;


	//time mapped to [0.5,1]- not used at the moment
	float time = 0.5+0.5*abs(sin(iTime));

    //adjust coordinates given an aspect ratio
    float aspect_ratio = iResolution.x/iResolution.y;
    c.x *= aspect_ratio;

    //setup parameters of the shape
    vec2 circle_centre = vec2(cos(iTime),sin(iTime)); //centre of the circle
    float radius = time/2.0; //radius of the circle, note it is dynamic depending on time parameter

    vec3 colour = vec3(1.0, 1.0, 1.0); //background colour

    //find the function for circle
    float function_value = circle(c, circle_centre, radius); 
    //the function for the implicit curve is 0 on the surface, here we are drawing points which are "close" to 0
    float thickness = 0.01; //note that thickness is not in geometric sense, but in the function value
    if (abs(function_value) < thickness) colour = vec3(0.0,0.0,0.0); //circle is drawn in black

    //note polar coordinates for the centre here and everywhere
    circle_centre = vec2(cos(iTime+1.57),sin(iTime+1.57));
    function_value = circle(c, circle_centre, radius); 
    if (abs(function_value) < thickness) colour = vec3(0.0,0.0,0.0); //circle is drawn in black

    circle_centre = vec2(cos(iTime+3.14),sin(iTime+3.14));
    function_value = circle(c, circle_centre, radius); 
    if (abs(function_value) < thickness) colour = vec3(0.0,0.0,0.0); //circle is drawn in black

    circle_centre = vec2(cos(iTime+4.71),sin(iTime+4.71));
    function_value = circle(c, circle_centre, radius); 
    if (abs(function_value) < thickness) colour = vec3(0.0,0.0,0.0); //circle is drawn in black


    //repeat for ellipse
    vec2 ellipse_centre = vec2(0,0); //centre of the circle
    function_value = ellipse(c, ellipse_centre, (2.0-time)/2.0, time/2.0); 
    if (abs(function_value) < 0.1) colour = vec3(0.0,0.0,1.0); //ellipse is drawn in blue

    float z = 2.0*sin(iTime);
    function_value = tanglecube(c*2.0, z);
    if (abs(function_value) < 0.1) colour = vec3(0.0,1.0,1.0); //ellipse is drawn in blue

	fragColor = vec4(colour,1.0); //set the colour of the fragment
}

//defining a blob with three points
float blob3(in vec2 position, in vec2 p1, in vec2 p2, in vec2 p3)
{
    float time = 0.5+0.5*sin(iTime);


    float a1 = 2.0, a2 = 2.0, a3 = 2.0; //parameters A for each skeletal element
    float b1 = 2.0, b2 = 2.0, b3 = 2.0; //parameters B for each skeletal element
    float T = 1.0; //threshold value

    float r1 = dot(position-p1, position-p1); //the same as (position.x-p1.x)*(position1.x-p1.x) + (position.y-p1.y)*(position1.y-p1.y)...
    float r2 = dot(position-p2, position-p2);
    float r3 = dot(position-p3, position-p3);

    return b1*exp(-a1*r1)+b2*exp(-a2*r2)+b3*exp(-a3*r3)-T; //sum of fields minus threshold
}

