// This shader is prepared for the first Wroclaw Shader Competition
// organized by Faculty of Physics and Astronomy, Khronos Chapter and SIggraph Chapter from Wroclaw
#define S 0.2

// sphere - distance function
float sphere(vec3 r, float R)
{ return length(r) - R; }

//  distance from the scene
float dist(vec3 r)
{
	float d = 1e10;

    //borders y -2 0    x 1 -2.6, center:
    //d = min(d, sphere(r + vec3(-0.8,-1,3), 0.1));

    float variator1 = iMouse.y/iResolution.y;
    float variator2 = iMouse.x/iResolution.x;    

    //this could be commented
    variator1 = abs(sin(iTime)) * 0.06 + 0.1;
    variator2 = iTime * .01;    

    int n = 5;
    for(int j=0; j<n; ++j){
        float scale = 0.98;
        float radius = variator1;
        float angle = variator2;
        angle = angle * 2.0 * 3.1415926535897932384626433832795;
        angle = angle + angle * float(j);
        vec3 offset = vec3(-0.8 + cos(angle)*radius, -1.0 + sin(angle)*radius, 3.0);
        d = min(d, sphere(r + offset, .4));

        for(int i=0; i<10; ++i){
            angle = variator2;
            angle = angle * 2.0 * 3.1415926535897932384626433832795 * float(i) * 3.0;
            angle = angle + angle * float(j);
            offset = vec3(offset.x + cos(angle)*radius, offset.y + sin(angle)*radius, 3.0);
            d = min(d, sphere(r + offset, .3 * scale));
            scale = scale * scale;
        }   
    }


    int extraBallsNumber = 9;    
    vec3 s[9] = vec3[](
        //vec3(-2.6, -2.0, 3.0),
        //vec3(1.0, 0.0, 3.0),
        vec3(-1.2, -1.6, 6.0),
        vec3(-2.0, -1.2, 5.0),
        vec3(-1.0, -1.6, 4.0),
        vec3(-1.6, -0.8, 2.0),
        vec3(-1.0, 0.0, 3.0),
        vec3(-1.7, -1.1, 3.0),
        vec3(-2.4, 0.0, 5.0),
        vec3(0.8, -0.9, 3.0),
        vec3(1.1, -0.7, 7.0));    
    for(int i=0; i<extraBallsNumber; ++i){
   		d = min(d, sphere(r + s[i] + vec3(0, sin(iTime + float(i))*0.2, 0), .03));
    }

    return d;
}

// shadow is just sum of distances from the scene while marching towards light (l)
float shadow(vec3 p, vec3 l, float d)
{
	float o=0.0;
	for (int i=12; i > 0; i--) 
	{
		o += dist( p+l*float(i)*d );
	}

	return clamp(o, 0.0, 1.0);
}

// normal vector (needed for lighting)
// http://www.pouet.net/topic.php?which=7920&page=10
// rar
vec3 normal(vec3 p)
{
	#define dr 1e-4
	vec3 drx = vec3(dr,0,0);
	vec3 dry = vec3(0,dr,0);
	vec3 drz = vec3(0,0,dr);
	return ( vec3( dist(p+drx), dist(p+dry), dist(p+drz) ) - dist(p)) / dr;
}

#define ITER 90	
#define EPS 0.0001
void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 r = fragCoord.xy / iResolution.xy;
	r.x*=(iResolution.x/iResolution.y);

	vec4 color=vec4(0,0,0,1);

	vec3 camera = vec3(1.0,2.0,1.0);
	vec3 p = vec3(r.x, r.y+1.0, -1.0);
	vec3 dir = normalize(p-camera);

	for(int i=0; i<ITER; i++)
	{
		float d = dist( p );
		if(d < EPS)
		{
		    break;
		}
		p = p + dir * d;
	}

	vec3 n = normal(p);

	//vec3 light_pos = vec3(1.2,4.2,-0.5);
    vec3 light_pos = vec3(cos(iTime)*2.0, 4.2, .0 + sin(iTime)*2.0);

	float light = 15.0 + 1.2*(dot(n,light_pos));
	vec3 lightdir = normalize(light_pos-p);
	light /= 0.2*pow(length(light_pos-p),3.4);

    // uncomment for shadows
	light *= shadow(p, lightdir, 0.01) * (abs(sin(iTime * 0.4)) - 0.2);

	//vec4 color2 = vec4(texture(iChannel0,r.xy ).xyz,1.0);//,light*0.891,light*0.998,1.0);
	color = vec4(light*0.89,light*0.891,light*0.998,1.0)*2.0;

    r.x *= 9.9;
    r.x += iTime * 0.2;
    vec2 uv = r * 6.0;
    vec3 col = texture(iChannel0, uv).rgb;
    color += vec4(max(vec3(col.r - .45), vec3(0)), 1.0);

	fragColor = color;
}

