//#define NOT_MIX_EDG

const float smoothLevel = 0.03;

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    //uv = -1.0 + 2.0 * uv; //
	vec2 uv = ( 2. * fragCoord.xy - iResolution.xy) / min(iResolution.y, iResolution.x);    

    vec3 bgColor =  vec3( 1.0 ) * ( 1.0 - 0.3 * length(uv) );

    float a = atan( uv.y, uv.x ); //
    float r = sqrt( dot( uv, uv ) ); //

    //
    uv.x = cos( a ) / clamp(r, .5, 1.);
    uv.y = sin( a ) / clamp(r, .5, 1.);
    uv += iTime * 0.05;

    // ^_^
    float color = 1.0;
    color += sin(uv.x * cos(iTime/15.) * 80.) + cos(uv.y * cos(iTime/15.) * 10.);
    color += sin( uv.y * sin( iTime / 10.0 ) * 40.0 ) + cos( uv.x * sin( iTime / 25.0 ) * 40.0 );
    color += sin( uv.x * sin( iTime / 5.0 ) * 10.0 ) + sin( uv.y * sin( iTime / 35.0 ) * 80.0 );
    color *= sin( iTime / 10.0 ) * 0.5;

    //
    #ifdef NOT_MIX_EDG

	//
    fragColor = vec4(bgColor, 1.0);
	if( r <= 1.0 ){
   	 	fragColor = vec4(vec3(color, color * 0.5, sin(color + iTime/3.) * .75), 1.);
    }

	#else

    //mix + smoothstep
    vec3 _color = mix( bgColor, vec3(color, color * .5, sin(color + iTime/3.) ), smoothstep(-smoothLevel, smoothLevel, 1.0 - r ) );
    fragColor = vec4(_color, 1.0);

    #endif    
}

#define fdist 0.3
#define iters 100
#define tol 0.005
#define maxdist 8.
#define eps 0.01
#define bevel 0.05
#define innerradius 0.1
#define sidelength 0.25
#define pix (edgeblur/iResolution.x/fdist)
#define lightdir vec3(1., 1., 1.)
#define shadoweps 0.1
#define shadowiters 20
#define shadowstep 0.03
#define sharpness 5.

#define PI 3.1415926

//factor by which to enlarge the solid angle subtended by a pixel in AA
#define edgeblur 2.5

float bevcube(vec3 ro) {
    vec3 rad = clamp(ro, -sidelength, sidelength);
    return length(ro-rad) - bevel;
}

vec2 map(vec3 ro) {

    //beveled cubes
    vec2 disp = vec2(1., 0.);
    float d = bevcube(ro);
    d = min(d, bevcube(ro + disp.xyy));
    d = min(d, bevcube(ro - disp.xyy));
    d = min(d, bevcube(ro + disp.yyx));
    d = min(d, bevcube(ro - disp.yyx));
    d = min(d, bevcube(ro - disp.yxy));

    float d2 = min(d, length(ro + vec3(0., 10., 0.)) - 9.6);
    if (d2 < d) {
        return vec2(d2, 2.0);
    } else {
    	return vec2(d, 1.0);
    }
}

float shadowtrace(vec3 ro, vec3 rd) {
    int i;
    float t = shadoweps;
    float dist = map(ro+t*rd).x;
    float fac = 1.0;
    for (i=0; i<shadowiters; i++) {
        t += shadowstep;
        dist = map(ro + t*rd).x;
        fac = min(fac, dist * sharpness / t);
    }
    return fac > 0. ? mix(0.5, 1., fac) : mix(0.5, 0., -fac);
}

vec4 raytrace(vec3 ro, vec3 rd) {
    vec2 t = vec2(0.);
    vec2 m = map(ro);
    //fac.xyz are consecutive measurements, fac.w is the last local minimum
    vec4 fac = vec4(maxdist);
    int i;
    for (i=0; i<iters; i++) {
        t.x += m.x;
        m = map(ro + rd * t.x);
        fac.x = m.x / t.x;
        //update facmin to last seen local minimum (not counting voxel borders)
        if (fac.y < fac.x && fac.y <= fac.z && fac.y < fac.w) {
            fac.w = fac.y;
            t.y = t.x;
        }
        fac.zy = fac.yx;
        if (abs(m.x) < tol) {
            return vec4(t, m.y, fac.w);
        } else if (t.x > maxdist) {
            break;
        }
    }
    return vec4(t, 0., fac.w);
}

vec4 getnormal(vec3 ro) {
    vec2 d = vec2(eps, 0.0);
    float x1 = map(ro+d.xyy).x;
    float x2 = map(ro-d.xyy).x;
    float y1 = map(ro+d.yxy).x;
    float y2 = map(ro-d.yxy).x;
    float z1 = map(ro+d.yyx).x;
    float z2 = map(ro-d.yyx).x;
    return vec4(normalize(vec3(
        x1-x2,
        y1-y2,
        z1-z2)),
        x1+x2+y1+y2+z1+z2-6.*map(ro).x);
}

vec3 shade(vec3 pos, float mat, vec3 rd) {
    vec3 col;
    if (mat < 0.5) return mix(vec3(0.8, 0.9, 1.), vec3(0.2, 0.2, 0.3), pow(abs(rd.y), 3.5));
    else if (mat < 1.5) col = vec3(0.6, 0.3, 0.8);
    else col = vec3(0.4, 1., 0.4);
    vec4 n = getnormal(pos);
    float fac = dot(n.xyz, lightdir);
    fac = min(fac, shadowtrace(pos + lightdir * shadoweps, lightdir));
    fac = max(0.8, fac);

    return fac * col;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    float h = iMouse.y < 1. ? 0.2 : (iMouse.y/iResolution.y - 0.5) * PI;
    float ww = (iMouse.x < 1. ? 0. : (iMouse.x/iResolution.x - 0.5) * PI) + iTime/2.;
    vec3 ro = 2.*vec3(sin(ww)*cos(h), sin(h), cos(ww)*cos(h));
    vec3 w = -normalize(ro);
    vec3 u = normalize(cross(w, vec3(0., 10.0, 0.)));
    vec3 v = cross(u, w);
    vec3 rd = normalize(w*fdist+(fragCoord.x/iResolution.x-0.5)*u+(fragCoord.y-iResolution.y/2.0)/iResolution.x*v);
    vec4 d = raytrace(ro, rd);
    float mat = map(ro + d.y*rd).y;
    vec3 col1 = shade(ro + rd*d.x, d.z, rd);
    vec3 col2 = shade(ro + rd*d.y, mat, rd);
    //fac is an approximation of x*sqrt(1-x^2)+arcsin(x), the proportion of area of a
    //circle that's covered by an edge at position x
    //float fac = (sin((clamp(d.w/pix, 0., 1.)-0.5)*PI)+1.)/2.;

    //or just a linear ramp... I don't see the difference
    float fac = clamp(d.w/pix, 0., 1.);
    fragColor = vec4(mix(col2, col1, fac), 1.0);
}

#define pi  3.14159
#define tau 6.28318
#define t iTime

#define p0 0.5, 0.5, 0.5,  0.5, 0.5, 0.5,  1.0, 1.0, 1.0,  0.0, 0.33, 0.67	

// source: http://iquilezles.org/www/articles/palettes/palettes.htm
// cosine based palette, 4 vec3 params
vec3 palette( in float t, in float a0, in float a1, in float a2, in float b0, in float b1, in float b2,
              in float c0, in float c1, in float c2,in float d0, in float d1, in float d2)
{
    return vec3(a0,a1,a2) + vec3(b0,b1,b2)*cos( tau*(vec3(c0,c1,c2)*t+vec3(d0,d1,d2)) );
}

const float epsilon = .0001;

vec3 rotateZ(vec3 p, float rads) {
    return mat3(vec3(cos(rads), sin(rads), 0.), vec3(-sin(rads), cos(rads), 0.), vec3(0., 0., 1.)) * p;
}

// IQ distance functions
float roundBoxSDF(vec3 p, vec3 size, float r)
{
	return length(max(abs(p) - size, 0.0))-r;
}

float opRep(vec3 p, vec3 c)
{
	vec3 q = mod(p, c) - .5 * c;
    return roundBoxSDF(q, vec3(.15), .1);
}

float sceneSDF(vec3 p)
{
    return opRep(p, vec3(1.));
}

vec3 gradient(vec3 p)
{
    vec3 dx = vec3(epsilon, 0., 0.);
    vec3 dy = vec3(0., epsilon, 0.);
    vec3 dz = vec3(0., 0., epsilon);
    return normalize(vec3(
    	sceneSDF(p + dx) - sceneSDF(p - dx),
        sceneSDF(p + dy) - sceneSDF(p - dy),
        sceneSDF(p + dz) - sceneSDF(p - dz)
    ));
}

vec3 phong(vec3 p, vec3 eye, vec3[1] lights, vec3 color)
{
    const vec3 specularColor = vec3(1.);
    const float ambientStrength = .5;

    vec3 norm = gradient(p);
    vec3 col = vec3(0.);
    vec3 view = normalize(eye - p);

    for (int i = 0; i < lights.length(); i++)
    {
        float kd = dot(lights[i], norm);
        vec3 reflection = reflect(normalize(p - lights[i]), norm);
        float ks = pow(max(dot(view, reflection), 0.), 16.);

        col += color * kd + ks * specularColor;
    }

    return col + color * ambientStrength;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = (2. * fragCoord - iResolution.xy) / iResolution.y;
	fragColor = vec4(0.);
    vec3 ray = normalize(vec3(uv, -1.));
    vec3 eye = vec3(sin(iTime), sin(iTime), -iTime * 3.);
    vec3[1] lights;
    lights[0] = vec3(1., 2., 5.);

    vec3 pos = eye;

    float dist = 0.;
    for (float i = 0.; i < 70.; i++)
    {
    	dist = sceneSDF(rotateZ(pos, iTime));
        pos += ray * dist;
    }

    if (dist < epsilon) {
        pos = rotateZ(pos, iTime);
        float colorIndex = floor(pos.x) + floor(pos.y) + floor(pos.z);
        colorIndex *= .2;
        colorIndex += iTime;

        // https://www.shadertoy.com/view/MdXGDr
		float fogFact = clamp(exp(-distance(eye, pos) * 0.3), 0.0, 1.0);

    	fragColor.rgb = fogFact * phong(pos, eye, lights, .5 * palette(colorIndex, p0));
    }
}

vec2 getUVCoords(in vec2 fragCoords)
{
    vec2 standardUVs = (fragCoords - 0.5) / (iResolution.xy - 1.0);
    return standardUVs;
}

float getRandomValue(vec2 uvCoords)
{
    return fract(sin(fract(iTime) / 10.0 * dot(uvCoords, vec2(12.9898, 78.233))) * 43758.5453123);
}

void mainImage(out vec4 fragColor, in vec2 fragCoord)
{
    vec2 uv = getUVCoords(fragCoord);
    vec4 image = texture(iChannel0, uv);

    fragColor = getRandomValue(uv) + 0.2 * image;
}

