#define pi 3.14159265358979

// Hue. https://www.laurivan.com/rgb-to-hsv-to-rgb-for-shaders/
vec3 hsv2rgb(vec3 c) {
    vec4 K = vec4(1.0, 2.0 / 3.0, 1.0 / 3.0, 3.0);
    vec3 p = abs(fract(c.xxx + K.xyz) * 6.0 - K.www);
    return c.z * mix(K.xxx, clamp(p - K.xxx, 0.0, 1.0), c.y);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord ) {
    // Black background
	vec3 col = vec3(0.0, 0.0, 0.0);

    // Set the moose (xmouse)
    float moose = iMouse.x/iResolution.x;
    if(iMouse.x == 0.0 && iMouse.y == 0.0) moose = .4;

    // Make the X coordinates the same distance as the Y
	vec2 uv = fragCoord.xy / iResolution.yy;
    uv.xy -= .5;
	uv.x += (1.0-iResolution.x/iResolution.y)/2.0;

    // Rotate the camera a bit, then draw grid
    float gridSize = sin(iTime/5.0)*.01+.07;
    uv.xy = vec2(uv.x*cos(.1) - uv.y*sin(.1), uv.x*sin(.1) + uv.y*cos(.1));
    float d2E = smoothstep(0.0, 1.0, min(min(mod(uv.x, gridSize), mod(uv.y, gridSize)), min(gridSize-mod(uv.x, gridSize), gridSize-mod(uv.y, gridSize)))*200.0);
    col = vec3(d2E*.04+.49, d2E*.04+.66, d2E*.05+.73);

    // Move mobius thing around
    uv.x += sin(iTime/5.0)*.4;
    uv.y += cos(iTime/4.0)*.1;

    float globalRotationTime = iTime/10.0;
    uv.xy = vec2(uv.x*cos(globalRotationTime) - uv.y*sin(globalRotationTime), uv.x*sin(globalRotationTime) + uv.y*cos(globalRotationTime));

    // Now many weaves there are (.5 though, because Mobius <3)
    float cuts = 1.5 + floor(moose*10.0);

    // Radians of the first loop   
    float rotA = atan(uv.y, uv.x)+pi;

    // Radius of overal circle - fun to change per pixel
    float rawRadius = .30 + sin(iTime/2.0)*.07;

	// How thick the strands are
    float separation = sin(iTime+rotA*3.0)*.01 + .02;

	// Distance between the two strands
	float dist = sqrt(uv.x*uv.x + uv.y*uv.y);

	// Radius of first loop
	float radiusA = sin(rotA*cuts)*.039+rawRadius;

    // Radians and radius of the second loop
	float rotB = rotA + pi*2.0/cuts/2.0;
	float radiusB = sin(rotB*cuts)*.02+rawRadius;

	// Draw black border around shape
    col = mix(col, vec3(.34, .37, .43), 
              smoothstep(0.0, 1.0, 1.0-200.0*min(abs(dist - radiusA) - separation, abs(dist - radiusB) - separation)));

	// Draw the first loops
    if(abs(dist - radiusA) < separation) {
        col = hsv2rgb(mix(vec3(0.0, 1.0, 0.7), vec3(.5, 1.0, 0.7), rotA/2.0/pi + iTime/20.0));
    }

    // Draw the second loop
    // If it's going behind the first loop, only draw it if the pixel isn't colliding with that region
	if(fract((rotB/2.0/pi+1.0/(cuts*4.0))*cuts)>.5 || abs(dist - radiusA) > separation) {
        if(abs(dist - radiusB) < separation) {
            col = hsv2rgb(mix(vec3(.5, 1.0, 0.7), vec3(1.0, 1.0, 0.7), rotA/2.0/pi + iTime/20.0));
        }
	}

    // Boop!
	fragColor.rgb = col;
}

