mat3 SetCamera( vec3 ro, vec3 ta, float cr )
{
	vec3 cw = normalize(ta - ro);
	vec3 cp = vec3(sin(cr), cos(cr), 0.0);
	vec3 cu = normalize(cross(cw, cp));
	vec3 cv = normalize(cross(cu, cw));
    return mat3(cu, cv, cw);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 q = fragCoord.xy / iResolution.xy;
    vec2 p = q * 2.0 - 1.0;
	p.x *= iResolution.x / iResolution.y;
    vec2 mouse = iMouse.xy / iResolution.xy;

    vec3 camPos = vec3(3.0 * cos(7.0 * mouse.x), 1.0 + 3.0 * mouse.y, 3.0 * sin(7.0 * mouse.x));
    vec3 camLookAt = vec3(0.0, 1.0, 0.0);

    mat3 camMatrix = SetCamera(camPos, camLookAt, 0.0);
    vec3 rayDir = normalize(camMatrix * vec3(p.xy, 1.5));

    vec3 col = Render(camPos, rayDir, fragCoord.xy);
    col = pow(col, vec3(1.0 / 2.2));
    fragColor = vec4(col, 1.0);
}

float sd(vec3 p)
{
    vec3 pos = p;
    float x;
    float s1 = length(sqrt(abs(pos)))-0.5;
    float s2 = length(floor(abs(pos)))+0.3;
    x = s1*sqrt(sqrt(s2));
    return x;
}
float trace(vec3 ro, vec3 rd)
{
    float t = 0.0;
    for(int i = 0; i < 24; i++)
    {
        vec3 p = ro+t*rd;
        float shape = sd(p);
        t += shape * 0.5;
    }
    return t;
}
mat2 rot(float t)
{
    return mat2(-cos(t), sin(t), sin(t), cos(t));
}
