    //rd.yx *= mat2(cos(time+Y+X), sin(time+Y+X), -sin(time+Y+X), cos(time+Y+X));
    vec3 ro = vec3(-sin(time), 0, -cos(time));
    float t = trace(ro, rd);
    float fog = 1.0/(1.0+t*t*0.1);
