const float PI = 3.131593;
const float smoothlevel = 0.1;

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    //background
    vec2 p = ( 2. * fragCoord.xy - iResolution.xy) / min(iResolution.y, iResolution.x);

    p.y -= 0.25;

    vec3 bcol = vec3( 1.0, 0.8, 0.7 - 0.07 * p.y ) * ( 1.0 - 0.25 * length(p) );

    //animate
    float tt = mod(iTime, 1.5) / 1.5;
    float ss = pow(tt, 0.2) * 0.5 + 0.5;
    ss = 1.0 + ss * 0.5 * sin(tt * PI * 2.0 * 3.0 + p.y * 0.5) * exp(-tt * 4.0);
    p *= vec2(0.5, 1.5) + ss * vec2(0.5, -0.5);

    //shape
    float a = atan(p.x, p.y)/PI;
    float r = length(p);
    float h = abs(a);
    float d = ( 13. * h - 22. * h * h + 10. * h * h * h ) / ( 6. - 5. * h ); //

    //color sshape
    float s = 1.0 - 0.5 * clamp(r/h, 0.0, 1.0); //s
    s = 1.0 + 0.75 * p.x;
    s *= 1.0 - 0.25 * r;
    s = 0.5 + 0.6 * s;
   	s *= 0.5 + 0.5 * pow( 1.0 - clamp(r/d, 0.0, 1.0) , 0.1);


    vec3 hcol = vec3(1.0, 0.5 * r, 0.3) * s; 
    vec3 col = mix( bcol, hcol, smoothstep( -smoothlevel , smoothlevel , d - r) );

	fragColor = vec4( col,1.0 );
}

float Circle( vec2 uv, vec2 p, float r, float blur )
{

	float d = length(uv - p);
    float c = smoothstep(r, r-blur, d);
    return c;

}

float Hash( float h )
{

    return h = fract(cos(h) * 5422.2465);

}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy / iResolution.xy;
    float c = 0.0;

    uv -= .5;
    uv.x *= iResolution.x / iResolution.y;
    float sizer = 1.0;
    float steper = .1;
    for(float i = -sizer; i<sizer; i+=steper)
        for(float j = -sizer; j<sizer; j+=steper){
    {	
        float timer = .5;
        float resetTimer = 7.0;
    	//c = Circle(uv, vec2(0.0,0), sin(iTime*timer), 0.1);
    	//c -= Circle(uv, vec2(0.0,0.0), sin(iTime*timer+.2), 0.1);
        if(c<=1.0){

            c += Circle(uv, vec2(i, j),sin(Hash(i))*cos(Hash(j))*(mod(iTime*timer, resetTimer)), sin(Hash(j)));

        }
        else if(c>=1.0)
        {
            c -= Circle(uv, vec2(i, j),sin(Hash(i))*cos(Hash(j))*(mod(iTime*timer, resetTimer)), sin(Hash(j)));     
        }
        }
    }
    fragColor = vec4( vec3(c),1.0);
}

