vec3 getRand3d(vec3 pos)
{
    vec4 r1=texelFetch(iChannel1,(ivec2(pos.xy+.5)%ivec2(64)+ivec2(floor(pos.z+.5)*vec2(27,13))+1024)%ivec2(256),0);
    return r1.xyz;
}

vec4 voronoiDist(in vec3 texc, float voronoiStrength, float voronoiScale)
{
    voronoiStrength=clamp(voronoiStrength,0.,2.);

    vec3 p = texc*voronoiScale;
    vec2 o;
    vec3 g = floor(p);
    p -= g;

    vec3 d = vec3(1); // 1.4, etc. "d.z" holds the distance comparison value.
    float mindist = 10.;
    float mindist2 = 10.;
    float mindist3 = 10.;
    float mindist4 = 10.;
    vec3 minPos=p;
    vec3 minPos2=p;
    vec3 minPos3=p;
    vec3 minPos4=p;

    int sampWidth=max(1,int(floor(voronoiStrength)));

    vec3 pos0 = voronoiStrength*(getRand3d( g ));
    for(int z = -sampWidth; z <= sampWidth; z++)
    {
        for(int y = -sampWidth; y <= sampWidth; y++)
        {
            for(int x = -sampWidth; x <= sampWidth; x++)
            {
                //vec3 hexOffs = 1.0*vec2(0.5,0.0)*(int(g.y+y)&1);
                vec3 pos = vec3(x,y,z)+voronoiStrength*getRand3d( g + vec3(x,y,z) );
                //vec2 pos = hexOffs + vec2(x,y)+voronoiStrength*(getVoronoiOffs((g+vec2(x,y))/voronoiScale/vec2(aspect,1.0))*(1.0-hexOffs));
                float dist = dot(pos-p, pos-p);

                if(dist<mindist)
                {
                    mindist=dist; minPos=pos;
                }
            }
        }
    }
    vec3 minhdir=vec3(0.0);
    float minhdist=0.0;
    float sum=0.;
    float softSize=0.05;
    for(int z = -sampWidth; z <= sampWidth; z++)
    {
        for(int y = -sampWidth; y <= sampWidth; y++)
        {
            for(int x = -sampWidth; x <= sampWidth; x++)
            {
                vec3 pos = vec3(x,y,z)+voronoiStrength*getRand3d( g + vec3(x,y,z) );
                if(dot(pos-minPos,pos-minPos) > 0.00001)
                {
                    float hdist=length(dot(p-(pos+minPos)*0.5,normalize(pos-minPos)))-0.1;
                    minhdist += exp( -hdist/softSize );
                }
            }
        }
    }
    minhdist=-log(minhdist)*softSize/*+0.07*/;

    return vec4(minhdist/voronoiScale,normalize(minhdir));
}

// smoothed minimum - copied from iq's site (http://iquilezles.org/www/articles/smin/smin.htm)
float smin2( float a, float b, float k )
{
    float res = exp( -k*a ) + exp( -k*b );
    return -log( res )/k;
}

// some distance primitives - from iq's site (http://iquilezles.org/www/articles/smin/smin.htm)
float maxcomp2(in vec3 p ) { return max(p.x,max(p.y,p.z));}

float getDistanceBoxS(vec3 rpos, vec3 size)
{
    vec3 di = abs(rpos) - size;
    return min( maxcomp2(di), length(max(di,0.0)) );
}

float getDistanceBoxRounded( vec3 p, vec3 b, float r )
{
  return length(max(abs(p)-b,0.0))-r;
}

float getDistanceTorusS(vec3 rpos,float r1,float r2)
{
    vec3 ptor = normalize(vec3(rpos.xy,0))*r1;
    return length(rpos-ptor)-r2;
}

float getDistanceSphereS(vec3 pos, float r)
{
    return length(pos)-r;
}

#define WheelFR vec3( 0.8, 1.2,-0.1)
#define WheelFL vec3(-0.8, 1.2,-0.1)
#define WheelBR vec3( 0.8,-1.2,-0.1)
#define WheelBL vec3(-0.8,-1.2,-0.1)
#define WheelRadius 0.45
#define ObjBoundRadius 3.5

float distCar(vec3 pos)
{
    //return vec4(length(pos)-ObjBoundRadius*0.5,0,0,0);
    if(dot(pos,pos)>ObjBoundRadius*ObjBoundRadius) return length(pos)-ObjBoundRadius*0.5;
    float dist = 100000.0;
    dist = min(dist, getDistanceBoxRounded(pos-vec3(0.0, 0.0,0.3),vec3(0.8-0.1, 1.8-0.1,0.35-0.1),0.1));
    dist = smin2(dist, getDistanceBoxRounded(pos-vec3(0.0,-0.5,0.7),vec3(0.75-0.15,1.1-0.15,0.5-0.15),0.15),10.0);
    dist = max(dist, -getDistanceSphereS((pos-WheelFL).yzx,WheelRadius*1.2));
    dist = max(dist, -getDistanceSphereS((pos-WheelFR).yzx,WheelRadius*1.2));
    dist = max(dist, -getDistanceSphereS((pos-WheelBL).yzx,WheelRadius*1.2));
    dist = max(dist, -getDistanceSphereS((pos-WheelBR).yzx,WheelRadius*1.2));
    dist = min(dist, getDistanceTorusS((pos-WheelFR).yzx,WheelRadius-0.15,0.15));
    dist = min(dist, getDistanceTorusS((pos-WheelFL).yzx,WheelRadius-0.15,0.15));
    dist = min(dist, getDistanceTorusS((pos-WheelBR).yzx,WheelRadius-0.15,0.15));
    dist = min(dist, getDistanceTorusS((pos-WheelBL).yzx,WheelRadius-0.15,0.15));
    return dist;
}


float maxcomp(vec3 v) { return max(max(v.x,v.y),v.z); }
float mincomp(vec3 v) { return min(min(v.x,v.y),v.z); }

vec2 mdist(vec3 pos)
{
    float m=1.;
    vec3 tpos=clamp(pos,-.95,.95);
    #ifdef SHADEROO
    float d=texture(iChannel0,tpos*.5+.5).x;
    #else
    float d=distCar(tpos*2.5-vec3(0,0,-.3))/2.5;
    #endif
    //d+=maxcomp(abs(tpos-pos));
    /*if (fract(iTime)>0.5)
        d-=.005;
    else*/
        d=abs(d)-.005;

    d+=length(tpos-pos)*.5;
    float dp=d;
    d=max(d,voronoiDist(pos,.5,15.).x);
    d=min(d,pos.z+.33);
    if (d<dp) m=2.;
    return vec2(d,m);
}

float dist(vec3 pos)
{
    return mdist(pos).x;
}

#define PI2 6.2832
#define LNUM 5

vec3 getGrad(vec3 p)
{
    float eps=.01;
    vec3 d = vec3(eps,0,0);
    return vec3(
        dist(p+d.xyz)-dist(p-d.xyz),
        dist(p+d.zxy)-dist(p-d.zxy),
        dist(p+d.yzx)-dist(p-d.yzx)
        )/eps;
}

vec4 inverseQuat(vec4 q)
{
    //return vec4(-q.xyz,q.w)/length(q);
    // if already normalized this is enough
    return vec4(-q.xyz,q.w);
}

vec4 multQuat(vec4 a, vec4 b)
{
    return vec4(cross(a.xyz,b.xyz) + a.xyz*b.w + b.xyz*a.w, a.w*b.w - dot(a.xyz,b.xyz));
}

vec3 transformVecByQuat( vec3 v, vec4 q )
{
    return v + 2.0 * cross( q.xyz, cross( q.xyz, v ) + q.w*v );
}

vec4 axAng2Quat(vec3 ax, float ang)
{
    return vec4(normalize(ax),1)*sin(vec2(ang*.5)+vec2(0,PI2*.25)).xxxy;
}

vec3 refl(vec3 dir)
{
    return mix(vec3(.2,.3,.4),vec3(.7,.85,1)*1.5,clamp(dir.z*3.+.5,0.,1.));
	//return texture(iChannel0,dir.yzx).zyx*1.2+.1;
}


float march(inout vec3 pos, vec3 dir)
{
    float rval=0.0;
    float R = 1.01;
    float d0=length(pos);

    // do some bounding check for better performance
    bool inside = false;
    // bounding sphere
    vec3 pn = pos-dir*dot(pos,dir);
    float d=length(pn);
    inside = inside || (d<R);
    inside=true;
    if(!inside) return 0.0;

    float eps=.001;
    for(int i=0;i<80;i++)
    {
       	float d=dist(pos);
        if(d<eps) { rval=mdist(pos).y; break; }
        if(d>d0+R) { rval=0.0; break; }
        pos+=dir*d*1.;
    }
    return rval;
}

vec4 getRand(vec2 coord)
{
    vec4 c=vec4(0);
    c+=texture(iChannel1,coord+.003*iTime);
    c+=texture(iChannel1,coord/2.+.003*iTime)*2.;
    c+=texture(iChannel1,coord/4.+.003*iTime)*4.;
    c+=texture(iChannel1,coord/8.+.003*iTime)*8.;
    return c/(1.+2.+4.+8.);
}

#define FloorZ -.33
vec4 myenv(vec3 pos, vec3 dir, float period)
{
    vec3 colHor=vec3(.5,.55,.6);
    vec3 colSky=mix(vec3(1.5,.75,0.)*1.3,vec3(.8,.9,1)*1.3,clamp(7.*dir.z,0.,1.));
    vec3 skyPos=pos+dir/abs(dir.z)*(20.-pos.z);
    float cloudPat=(1.+.4*(getRand(skyPos.xy*.001).x-.5));
    colSky*=mix(1.,cloudPat,step(0.,dir.z));
    vec3 colFloor=vec3(1.,.95,.9);
    vec3 colScale=vec3(.0,.3,.5);
    vec3 floorPos=pos-dir/dir.z*(pos.z-FloorZ);
    vec2 s;
    float scale=1.;
    s=sin(floorPos.xy*PI2*.5*period);
    scale*=(1.-.3*exp(-s.x*s.x/.01))*(1.-.3*exp(-s.y*s.y/.01));
    s=sin(floorPos.xy*PI2*.5/10.*period);
    scale*=(1.-.5*exp(-s.x*s.x/.001))*(1.-.5*exp(-s.y*s.y/.001));
    colFloor=mix(colFloor,colScale,1.-scale)*(1.+.4*(getRand(floorPos.xy*.001).x-.5));
    vec3 col=mix(colSky,colFloor,1.-smoothstep(-.01,.01,dir.z));
    col=mix(colHor,col,clamp(abs(dir.z*6.)-.1,0.,1.));
    return vec4(col,1);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
 	vec3 pos = vec3(0,0,2.5);
    #ifdef SHADEROO
 	pos *= 1.-iMouseData.z/1000.;
    #endif
 	vec3 dir = normalize(vec3((fragCoord.xy-.5*iResolution.xy)/iResolution.x,-1.));
    vec2 ang=PI2*(iMouse.xy/iResolution.xy*vec2(-1,.3)-vec2(0,.02));
    if(iMouse.x==0. && iMouse.y==0.) ang = vec2(1.+.3*iTime,1.4);
    vec4 q = vec4(0,0,0,1);
    q = multQuat(q,axAng2Quat(vec3(0,0,1),ang.x));
    q = multQuat(q,axAng2Quat(vec3(1,0,0),ang.y));
    pos=transformVecByQuat(pos,q);
   	dir=transformVecByQuat(dir,q);
   	vec3 camDir=transformVecByQuat(vec3(0,0,-1),q);
   	vec3 camPos=pos;
    float m = march(pos,dir);
    vec3 n = normalize(getGrad(pos));

    vec3 bg=mix(vec3(1),vec3(.3,.3,1),length(fragCoord.xy-.5*iResolution.xy)/iResolution.x);

    float fres=abs(dot(dir,n));
    fres=1.-fres;
    //fres*=fres;
    fres=.7+.3*fres;
    vec3 col=vec3(.75,.8,.85); // material color of car
    if(int(m+.1)!=1) col=vec3(1);  // set to 1 if bg or floor
    if(int(m+.1)==1) col*=1.-fres;
    if(int(m+.1)==1) col+=.9*fres*myenv(pos,reflect(dir,n),10.).xyz;
    col=.1+col*.9;
    bg = refl(dir);
    bg=myenv(pos,dir,10.).xyz;
    // calc ao by stepping along normal
    float ao=1.;
    ao*=.2+.8*dist(pos+n.xyz*.12)/.12;
    ao*=.4+.6*dist(pos+n.xyz*.25)/.25;
    ao*=.6+.4*dist(pos+n.xyz*.5)/.5;
    col*=clamp(ao*.7+.3,0.,1.);

    float vign = (1.1-.2*dot((fragCoord.xy/iResolution.xy)*2.-1.,(fragCoord.xy/iResolution.xy)*2.-1.));    
    fragColor.xyz=col;
    if(int(m+.1)!=1) fragColor.xyz*=bg;
    fragColor.xyz*=vign;
    pos=mix(pos,pos+dir*10000.,1.-m);
    fragColor.w=1.;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    float t = iTime + 13.1;
	vec2 uv = fragCoord.xy / iResolution.xy;
    uv *= 10.0*atan(t);
    uv = fract(uv+iTime);
    vec2 uv2 = fract(uv + t *0.23);
	vec4 P = vec4(uv2,0.5+0.5*cos(t),1.0);
    vec4 O = vec4(uv,0.5+0.5*cos(t),1.0);

    float M = step( (O.r / P.r), abs(sin(t * uv.y)));
    fragColor.rgb = vec3(M);
;

}

