    }
}

// Used to have https://soundcloud.com/twistedmusic/younger-brother-crumblenaut but stopped working
#define M_PI radians(180.0)
#define t (iTime + 1.0)

vec3 surface(vec2 uv, float size) {
    float hSize = size / 2.0;
    float h2Size = hSize / 2.0;
    float s = size / 20.0;
    float o = size / (7.0 + 2.0 * sin(t * 3.0));
    vec2 uv2 = abs(mod(uv, size) - hSize);
    float c = max(smoothstep(h2Size + o - s, h2Size + o + s, uv2.x),
                  smoothstep(h2Size + o - s, h2Size + o + s, uv2.y));    
    float m1 = texture(iChannel0, vec2(mod(uv.x / 100.0, 1.0), 0.)).x + 0.5;
    float m2 = texture(iChannel0, vec2(0.1, 0.)).x + 0.5;
    return mix(
        vec3(1.0, 0.5, 0.0) * m1,
        vec3(0.9, 0.3 + 0.3 * sin(t * 2.0), 0.1) * m2,
        c);
}

float wave(vec2 pos) {
    return (sin(pos.y * 17.0 - t * 2.0) + 1.0) / (2.0);    
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = 2.0 * fragCoord.xy / iResolution.xy - 1.0;
    uv.y *= (iResolution.y / iResolution.x);

    float dy = 5.0 / length(uv);
    vec2 hit = vec2(
       dy * sin(t * 0.937), 
       floor(10.0 + 6.0 * sin(t * 0.5)) * -atan(uv.x, uv.y) / M_PI
    );

    float m1 = texture(iChannel0, vec2(0.1, 0.)).x;
    hit.y += (hit.x / (4.0 - (m1 * 3.0))) * sin(t);

    float fade = min(1.0, 2000.0 / (hit.x * hit.x));

    hit.x += t * 2.0;

    float m2 = texture(iChannel0, vec2(0.6, 0.)).x + 0.7;
    fragColor = vec4(fade * surface(hit, 1.0), 1.0);
    fragColor += vec4(fade * vec3(0.6 + sin(t * 0.6), 1.0, 0.6 + sin(t)) * wave(-hit.yx / 20.0), 1.0) * m2;
}

