// by iq

float opU( float d1, float d2 )
{
    return min(d1,d2);
}

float smin( float a, float b, float k ){
    float h = clamp( 0.5+0.5*(b-a)/k, 0.0, 1.0 );
    return mix( b, a, h ) - k*h*(1.0-h);
}

float length6( vec3 p )
{
	p = p*p*p; p = p*p;
	return pow( p.x + p.y + p.z, 1.0/6.0 );
}

// ------

// from hg_sdf 

float fPlane(vec3 p, vec3 n, float distanceFromOrigin) {
	return dot(p, n) + distanceFromOrigin;
}

void pR(inout vec2 p, float a) {
	p = cos(a)*p + sin(a)*vec2(p.y, -p.x);
}

// -------


float fractal(vec3 p)
{
    const int iterations = 20;

    float d = iTime*5. - p.z;
   	p=p.yxz;
    pR(p.yz, 1.570795);
    p.x += 6.5;

    p.yz = mod(abs(p.yz)-.0, 20.) - 10.;
    float scale = 1.25;

    p.xy /= (1.+d*d*0.0005);

	float l = 0.;

    for (int i=0; i<iterations; i++) {
		p.xy = abs(p.xy);
		p = p*scale + vec3(-3. + d*0.0095,-1.5,-.5);

		pR(p.xy,0.35-d*0.015);
		pR(p.yz,0.5+d*0.02);

        l =length6(p);
	}
	return l*pow(scale, -float(iterations))-.15;
}

vec2 map(vec3 pos) 
{
    float dist = 10.; 
    dist = opU(dist, fractal(pos));
    dist = smin(dist, fPlane(pos,vec3(0.0,1.0,0.0),10.), 4.6);
    return vec2(dist, 0.);
}

vec3 vmarch(Ray ray, float dist)
{   
    vec3 p = ray.ro;
    vec2 r = vec2(0.);
    vec3 sum = vec3(0);
    vec3 c = hue(vec3(0.,0.,1.),5.5);
    for( int i=0; i<20; i++ )
    {
        r = map(p);
        if (r.x > .01) break;
        p += ray.rd*.015;
        vec3 col = c;
        col.rgb *= smoothstep(.0,0.15,-r.x);
        sum += abs(col)*.5;
    }
    return sum;
}

vec2 march(Ray ray) 
{
    const int steps = 50;
    const float prec = 0.001;
    vec2 res = vec2(0.);

    for (int i = 0; i < steps; i++) 
    {        
        vec2 s = map(ray.ro + ray.rd * res.x);

        if (res.x > MAXDIST || s.x < prec) 
        {
        	break;    
        }

        res.x += s.x;
        res.y = s.y;

    }

    return res;
}

vec3 calcNormal(vec3 pos) 
{
	const vec3 eps = vec3(0.005, 0.0, 0.0);

    return normalize(
        vec3(map(pos + eps).x - map(pos - eps).x,
             map(pos + eps.yxz).x - map(pos - eps.yxz).x,
             map(pos + eps.yzx).x - map(pos - eps.yzx).x ) 
    );
}

vec4 render(Ray ray) 
{
    vec3 col = vec3(0.);
	vec2 res = march(ray);

    if (res.x > MAXDIST) 
    {
        return vec4(col, 50.);
    }

    vec3 pos = ray.ro+res.x*ray.rd;
    ray.ro = pos;
   	col = vmarch(ray, res.x);

    col = mix(col, vec3(0.), clamp(res.x/50., 0., 1.));
   	return vec4(col, res.x);
}

mat3 camera(in vec3 ro, in vec3 rd, float rot) 
{
	vec3 forward = normalize(rd - ro);
    vec3 worldUp = vec3(sin(rot), cos(rot), 0.0);
    vec3 x = normalize(cross(forward, worldUp));
    vec3 y = normalize(cross(x, forward));
    return mat3(x, y, forward);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy / iResolution.xy;
    uv = uv * 2.0 - 1.0;
    uv.x *= iResolution.x / iResolution.y;
    uv.y -= uv.x*uv.x*0.15;
    vec3 camPos = vec3(3., -1.5, iTime*5.);
    vec3 camDir = camPos+vec3(-1.25,0.1, 1.);
    mat3 cam = camera(camPos, camDir, 0.);
    vec3 rayDir = cam * normalize( vec3(uv, .8));

    Ray ray;
    ray.ro = camPos;
    ray.rd = rayDir;

    vec4 col = render(ray);

	fragColor = vec4(1.-col.xyz,clamp(1.-col.w/MAXDIST, 0., 1.));
}

float random (in vec2 st) { 
    return fract(sin(dot(st.xy,
                         vec2(1.9898,78.233)))
                 * 4443478.5453123);
}

float noise (in vec2 st) {
    vec2 i = floor(st);
    vec2 f = fract(st);

    // Four corners in 2D of a tile
    float a = random(i);
    float b = random(i + vec2(1.0, 0.0));
    float c = random(i + vec2(0.0, 1.0));
    float d = random(i + vec2(1.0, 1.0));

    // Smooth Interpolation

    // Cubic Hermine Curve.  Same as SmoothStep()
    vec2 u = f*f*(3.0-2.0*f);
    // u = smoothstep(0.,1.,f);

    // Mix 4 coorners porcentages
    return mix(a, b, u.x) + 
            (c - a)* u.y * (1.0 - u.x) + 
            (d - b) * u.x * u.y;
}

float fbm(in vec2 p){
    float result = noise(p*vec2(1.0,1.0));
	result += 0.5*noise(p*vec2(3.0,2.0));
    result += 0.25*noise(p*vec2(-8.0,8.0));
     result += 0.25*noise(p*vec2(12.0,12.0));

    return result;
}


 float pattern( in vec2 p )
  {
      vec2 q = vec2( fbm( p + vec2(iTime/10.0,0.0) ),
                     fbm( p + vec2(iMouse.x/iResolution.x*5.2,iMouse.x/iResolution.x*5.2) ) );

      vec2 r = vec2( fbm( p + 4.0*q + vec2(iMouse.x/iResolution.x*5.2,iMouse.x/iResolution.x*5.2) ),
                     fbm( p + 4.0*q + vec2(6.3,2.8) ) );

      return fbm( p + 5.0*r );
  }








void mainImage( out vec4 fragColor, in vec2 fragCoord )
{

    float p=pattern(fragCoord.xy/1000.);
    float p2=pattern(fragCoord.xy*vec2(-1,-1)/100.);
	fragColor = vec4(p,0.1*(p2+p),pow(p,2.0)/5.0,0);

}

#define l 120
void mainImage(out vec4 FragColor,vec2 FragCoord){
	vec2 v = (FragCoord.xy - iResolution.xy/2.) / min(iResolution.y,iResolution.x) * 30.;
	vec2 vv = v;// vec2 vvv = v;
	float ft = iTime+360.1;
	float tm = ft*0.1;
	float tm2 = ft*0.3;
	vec2 mspt = (vec2(
			sin(tm)+cos(tm*0.2)+sin(tm*0.5)+cos(tm*-0.4)+sin(tm*1.3),
			cos(tm)+sin(tm*0.1)+cos(tm*0.8)+sin(tm*-1.1)+cos(tm*1.5)
			)+1.0)*0.35; //5x harmonics, scale back to [0,1]
	float R = 0.0;
	float RR = 0.0;
	float RRR = 0.0;
	float a = (1.-mspt.x)*0.5;
	float C = cos(tm2*0.03+a*0.01)*1.1;
	float S = sin(tm2*0.033+a*0.23)*1.1;
	float C2 = cos(tm2*0.024+a*0.23)*3.1;
	float S2 = sin(tm2*0.03+a*0.01)*3.3;
	vec2 xa=vec2(C, -S);
	vec2 ya=vec2(S, C);
	vec2 xa2=vec2(C2, -S2);
	vec2 ya2=vec2(S2, C2);
	vec2 shift = vec2( 0.033, 0.14);
	vec2 shift2 = vec2( -0.023, -0.22);
	float Z = 0.4 + mspt.y*0.3;
	float m = 0.99+sin(iTime*0.03)*0.003;
	for ( int i = 0; i < l; i++ ){
		float r = dot(v,v);
		float r2 = dot(vv,vv);
		if ( r > 1.0 )
		{
			r = (1.0)/r ;
			v.x = v.x * r;
			v.y = v.y * r;
		}
		if ( r2 > 1.0 )
		{
			r2 = (1.0)/r2 ;
			vv.x = vv.x * r2;
			vv.y = vv.y * r2;
		}
		R *= m;
		R += r;
		R *= m;
		R += r2;
		if(i < l-1){
			RR *= m;
			RR += r;
			RR *= m;
			RR += r2;
			if(i < l-2){
				RRR *= m;
				RRR += r;
				RRR *= m;
				RRR += r2;
			}
		}

		v = vec2( dot(v, xa), dot(v, ya)) * Z + shift;
		vv = vec2( dot(vv, xa2), dot(vv, ya2)) * Z + shift2;
	}

	float c = ((mod(R,2.0)>1.0)?1.0-fract(R):fract(R));
	float cc = ((mod(RR,2.0)>1.0)?1.0-fract(RR):fract(RR));
	float ccc = ((mod(RRR,2.0)>1.0)?1.0-fract(RRR):fract(RRR));
	FragColor = vec4(ccc, cc, c, 1.0); 
}

