		vec3	light_color = vec3(1., .5, .2)*1.+0.;
        vec3	vl = normalize( (light_pos - v) );
		float	diffuse  = max(0., dot(vl, n));
		float	specular = pow(max(0., dot(vl, ref_ev)), 10.8 );
        col.xyz = light_color * (specular) + diffuse * base;
	#else
    	col.xyz = 1.*( +vec3(.65, .4, .2)*inter.w * .3-inter.x*.1 * vec3(.3, .2, .15) );
	#endif
    }
	c_out =  vec4(col, 1.);
}

float	scene(vec3 p)
{
    float	balls = 1e5;
    float	lumos = 1e5;
	vec3	pr;

    vec2	q;

    pr = p;

    rotate(pr.xz , iTime*1.);

    q = vec2(length(pr.xy)-2., pr.z);

    rotate(q.xy, +iTime*.5+atan(pr.x, pr.y)*6. );
    q.xy = abs(q.xy)-.25;
    rotate(q.xy, -iTime*.5+atan(pr.x, pr.y)*.75 );
    q.xy = abs(q.xy)-.19; // .25 == butterflys

    balls = mylength(q.xy)-.025;//mylength(q)-.02305;

    balls = max(balls, max((length(vec2(length(pr.xy)-2., pr.z))-.3), (length(vec2(length(pr.xy)-2., pr.z))-.7)) );

	#ifdef	FUDGE
    balls *= .5;
    #endif

    return(balls);
}

vec4	march(vec3 pos, vec3 dir)
{
    vec2	dist = vec2(0.0, 0.0);
    vec3	p = vec3(0.0, 0.0, 0.0);
    vec4	step = vec4(0.0, 0.0, 0.0, 0.0);

    for (int i = -1; i < I_MAX; ++i)
    {
    	p = pos + dir * dist.y;
        dist.x = scene(p);
        dist.y += dist.x*1.;
        // log trick by aiekick
        if (log(dist.y*dist.y/dist.x/1e5)>0. || dist.x < E || dist.y >= FAR)
        {
            if (dist.x < E)
	            step.y = 1.;
            break;
        }
        step.x++;
    }
    step.w = dist.y;
    return (step);
}

// Utilities

float	mylength(vec3 p)
{
	float	ret = 1e5;

    p = p*p;
    p = p*p;
    p = p*p;

    ret = p.x + p.y + p.z;
    ret = pow(ret, 1./8.);

    return ret;
}

float	mylength(vec2 p)
{
	float	ret = 1e5;

    p = p*p;
    p = p*p;
    p = p*p;

    ret = p.x + p.y;
    ret = pow(ret, 1./8.);

    return ret;
}

void rotate(inout vec2 v, float angle)
{
