vec3 visualizerBar(sampler2D audio, vec2 uv, int barIndex)
{
    float intensity = texture(iChannel0, vec2(float(barIndex)/BARCOUNT , 0.1)).x;
    if(uv.y > 0.0 && uv.y < (abs(intensity) - abs((uv.x) - (float(barIndex) + sin(iTime)*sin(iTime))/BARCOUNT ) ))
        return mix(vec3(0.0,0.0,0.0), vec3(1.0-uv.x, 0.1, 0.6 + (uv.x - (float(barIndex)/BARCOUNT ))/(1.0/BARCOUNT )*0.4), 0.25+0.5*uv.y);
    return vec3(uv.y-abs(intensity), 0.0, uv.y-abs(intensity)*0.4);
