    c = saturate(c);
    #endif


    vec4 col = vec4(c);
    //col.rgb *=.8; //original
    float moonMult = 0.8; 
    #ifdef NEWMOON
    moonMult -= (texture(iChannel0, uv).x + texture(iChannel1, uv).x); //*max(0.5,abs(sin(iTime)))*4.0;
    #endif
    col.rgb *= moonMult;
