float plot(vec2 st, float pct){
  return  smoothstep( pct-0.02, pct, st.y) - smoothstep( pct, pct+0.02, st.y);  
}

float rand (float i) {
    return fract(sin(i)*100000.0);
}

float circle(in vec2 _st, in float _radius){
    vec2 dist = _st-vec2(0.5);
	return 1.-smoothstep(_radius-(_radius*0.01),
                         _radius+(_radius*0.01),
                         dot(dist,dist)*4.0);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy / iResolution.xy;

    //uv *=2.0;

    float i = floor(uv.x + iTime); 
	float f = fract(uv.x + iTime); 
    float u = f * f * (3.0 - 2.0 * f );
	float y = mix(rand(i), rand(i + 1.0), u);
    y = y / 5.0 + 0.3;
    float ly = plot(uv,y);

    vec3 color = vec3(ly);

    vec2 st = uv;
    st.x *= iResolution.x / iResolution.y;

    vec2 translate = vec2(sin(iTime),0);
    st += translate*0.35;

    float yellow = circle(vec2(st.x, st.y-0.3), 0.05);
    color +=  vec3(yellow,yellow,0);

    float kill_yellow = circle(vec2(st.x-0.1, st.y-0.3), 0.05);
    color -=  vec3(kill_yellow,kill_yellow,0);

    color.b += smoothstep(0.0,1.0-y, 1.0-uv.y) - step(1.0-y, 1.0-uv.y)*(1.0-uv.y);

	fragColor = vec4(color,1.0);
}

