vec3 VaryNf (vec3 p, vec3 n, float f);

vec3 sunDir, qHit;
float dstFar, tCur, tWav;
int idObj;
const int idStr = 1, idRail = 2, idFlr = 3, idPil = 4;
const float pi = 3.14159;

float ObjDf (vec3 p)
{
  vec3 q, qs;
  float dMin, d, s, aq, a, db;
  const float flGap = 4., flShift = 3.9, flRad = 9.85, flWid = 1.7, bcRadV = 0.04,
     bcRadH = 0.07, bcLen = 1.1;
  dMin = dstFar;
  q = p;
  aq = (length (q.xz) > 0.) ? atan (q.z, - q.x) : 0.;
  q.xz = Rot2D (q.xz, 2. * pi * (floor (4. * aq / (2. * pi) + 0.5) / 4.));
  qs.y = q.y;
  qs.xz = Rot2D (q.xz + vec2 (11.3, 6.), -0.25 * pi);
  db = PrBox2Df (qs.yz + vec2 (-8.9, 0.), vec2 (1.4, 0.5));
  qs.xz = Rot2D (q.xz + vec2 (11.3, -6.), 0.25 * pi);
  db = min (db, PrBox2Df (qs.yz + vec2 (-1.1, 0.), vec2 (1.2, 0.5)));
  qs.y = q.y - flGap;
  qs.xz = Rot2D (vec2 (q.x + 11.3, abs (q.z) - 6.), -0.25 * pi);
  q.xz = vec2 (- q.z, q.x + 6.);
  a = (length (q.xz) > 0.) ? atan (q.z, - q.x) : 0.;
  q.xz = vec2 (28. * a / (2. * pi), length (q.xz) - 8.);
  q.y += 3.;
  q.xy = Rot2D (q.xy, 0.25 * pi);
  s = mod (q.x, 0.5 * sqrt (2.));
  d = 0.3 * max (max (max (q.y - min (s, 0.5 * sqrt (2.) - s), abs (q.z) - 1.5), -0.07 - q.y), qs.x - 0.1);
  if (d < dMin) { dMin = d;  idObj = idStr;  qHit = q; }
  q.z = abs (q.z);
  q -= vec3 (-11.3, 1.4, 1.43);
  d = max (min (PrBoxDf (q, vec3 (5.8, 0.9 * bcRadH, bcRadH)),
     PrBoxDf (vec3 (0.5 * mod (17.8 * a + 0.5, 1.) - 0.25, q.y + 0.7, q.z),
     vec3 (bcRadV, 0.7, bcRadV))), qs.x - 0.1);
  if (d < dMin) { dMin = d;  idObj = idRail; }
  q = p;  q.y = abs (qs.y + 0.05) - flShift;
  d = PrCylAnDf (q.xzy, flRad, flWid, 0.15);
  q.xz = qs.xz + vec2 (-2., 0.2);
  d = min (d, PrBoxDf (q, vec3 (2.1, 0.15, 1.7)));
  if (d < dMin) { dMin = d;  idObj = idFlr;  qHit = q; }
  q = vec3 (p.x, abs (qs.y - 2.1) - flShift, p.z);
  d = min (PrCylAnDf (q.xzy, flRad - flWid + 0.15, bcRadH, bcRadH),
     max (PrCylAnDf (q.xzy, flRad + flWid - 0.15, bcRadH, bcRadH), 2. - abs (qs.x - 2.)));
  q.y = abs (qs.y - 1.) - flShift;
  q.xz = Rot2D (p.xz, 2. * pi * (floor (128. * aq / (2. * pi) + 0.5) / 128.));
  d = min (min (d, PrBoxDf (vec3 (q.x + flRad - flWid + 0.15, q.yz), vec3 (bcRadV, bcLen, bcRadV))),
     max (PrBoxDf (vec3 (q.x + flRad + flWid - 0.15, q.yz), vec3 (bcRadV, bcLen, bcRadV)), 2. - abs (qs.x - 2.)));
  d = min (d, max (min (PrBoxDf (vec3 (qs.x - 2., abs (qs.y - 2.1) - flShift, qs.z - 1.45),
     vec3 (2., bcRadH, bcRadH)), PrBoxDf (vec3 (mod (qs.x + 0.25, 0.5) - 0.25,
     abs (qs.y - 1.) - flShift, qs.z - 1.45), vec3 (bcRadV, bcLen, bcRadV))), abs (qs.x - 2.) - 2.));
  d = min (d, max (min (PrBoxDf (vec3 (qs.x, abs (qs.y - 2.1) - flShift, qs.z), vec3 (bcRadH, bcRadH, 1.45)),
     max (PrBoxDf (vec3 (qs.x, abs (qs.y - 1.) - flShift,
     mod (qs.z + 0.25, 0.5) - 0.25), vec3 (bcRadV, bcLen, bcRadV)), abs (qs.z) - 1.45)), - db));
  if (d < dMin) { dMin = d;  idObj = idRail; }
  d = min (PrCapsDf (vec3 (qs.x, abs (qs.y - bcLen) - flShift, qs.z - 1.43).xzy, 0.15, 1.2),
     PrCapsDf (vec3 (qs.x, qs.y - bcLen, qs.z + 1.43).xzy, 0.15, flShift + 1.2));
  if (d < dMin) { dMin = d;  idObj = idPil;  qHit = q; }
  return dMin;
}

float ObjRay (vec3 ro, vec3 rd)
{
  float dHit, d;
  dHit = 0.;
  for (int j = 0; j < 160; j ++) {
    d = ObjDf (ro + dHit * rd);
    dHit += d;
    if (d < 0.0005 || dHit > dstFar) break;
  }
  return dHit;
}

vec3 ObjNf (vec3 p)
{
  vec4 v;
  vec3 e = vec3 (0.001, -0.001, 0.);
  v = vec4 (ObjDf (p + e.xxx), ObjDf (p + e.xyy), ObjDf (p + e.yxy), ObjDf (p + e.yyx));
  return normalize (vec3 (v.x - v.y - v.z - v.w) + 2. * v.yzw);
}

float ObjSShadow (vec3 ro, vec3 rd)
{
  float sh, d, h;
  sh = 1.;
  d = 0.05;
  for (int j = 0; j < 32; j ++) {
    h = ObjDf (ro + rd * d);
    sh = min (sh, smoothstep (0., 0.05 * d, h));
    d += max (0.01, h);
    if (sh < 0.05) break;
  }
  return sh;
}

vec3 BgCol (vec3 ro, vec3 rd)
{
  vec3 col, vn;
  float f;
  if (rd.y > 0.) {
    ro.xz += 2. * tCur;
    col = vec3 (0.2, 0.3, 0.6) + 0.2 * (1. - max (rd.y, 0.)) +
       0.1 * pow (max (dot (rd, sunDir), 0.), 16.);
    f = Fbm2 (0.02 * (ro.xz + rd.xz * (100. - ro.y) / rd.y));
    col = mix (col, vec3 (1.), 0.2 + 0.8 * f * rd.y);
  } else {
    col = mix (0.6 * mix (vec3 (0.3, 0.4, 0.1), vec3 (0.4, 0.5, 0.2), Fbm2 (ro.xz)) *
       (1. - 0.2 * Noisefv2 (32. * ro.xz)), vec3 (0.18, 0.28, 0.48), pow (1. + rd.y, 5.));
    f = 1. - smoothstep (0.1, 1., length (ro.xz) / dstFar);
    vn = VaryNf (ro, vec3 (0., 1., 0.), 2. * f);
    col = col * (0.1 + 0.1 * max (vn.y, 0.) + 0.8 * max (dot (vn, sunDir), 0.));
  }
  return col;
}

float WaveHt (vec2 p)
{
  mat2 qRot = mat2 (0.8, -0.6, 0.6, 0.8);
  vec4 t4, v4;
  vec2 t;
  float wFreq, wAmp, ht;
  wFreq = 1.;
  wAmp = 1.;
  ht = 0.;
  for (int j = 0; j < 3; j ++) {
    p *= qRot;
    t = tWav * vec2 (1., -1.);
    t4 = (p.xyxy + t.xxyy) * wFreq;
    t = vec2 (Noisefv2 (t4.xy), Noisefv2 (t4.zw));
    t4 += 2. * t.xxyy - 1.;
    v4 = (1. - abs (sin (t4))) * (abs (sin (t4)) + abs (cos (t4)));
    ht += wAmp * dot (pow (1. - sqrt (v4.xz * v4.yw), vec2 (8.)), vec2 (1.));
    wFreq *= 2.;
    wAmp *= 0.5;
  }
  return ht;
}

vec3 WaveNf (vec3 p, float d)
{
  vec3 vn;
  vec2 e;
  e = vec2 (max (0.01, 0.005 * d * d), 0.);
  p *= 0.5;
  vn.xz = 3. * (WaveHt (p.xz) - vec2 (WaveHt (p.xz + e.xy),  WaveHt (p.xz + e.yx)));
  vn.y = e.x;
  return normalize (vn);
}

vec3 ShowScene (vec3 ro, vec3 rd)
{
  vec4 vc;
  vec3 vn, col;
  float dstObj, r, s, sh;
  bool isBg;
  HexVorInit ();
  tWav = 0.2 * tCur;
  isBg = true;
  sh = 1.;
  dstObj = ObjRay (ro, rd);
  if (dstObj < dstFar) {
    isBg = false;
    ro += rd * dstObj;
    vn = ObjNf (ro);
    if (idObj == idFlr) {
      col = vec3 (1.);
      r = length (ro.xz);
      if (abs (vn.y) > 0.99) col *= 0.8 + 0.2 * SmoothBump (0.1, 0.9, 0.03,
         ((r < 11.6) ? mod (2.42 * r, 1.) : mod (2. * qHit.x, 1.)));
      else if (abs (vn.y) < 0.01) col *= 0.8 +
         0.2 * SmoothBump (0.2, 0.8, 0.03, mod (12. * ro.y, 1.));
      vn = VaryNf (100. * ro, vn, 0.2); 
    } else if (idObj == idStr) {
      col = vec3 (0.95, 0.95, 1.);
    } else if (idObj == idRail) {
      col = vec3 (0.8, 0.8, 1.);
    } else if (idObj == idPil) {
      col = vec3 (0.8, 0.8, 1.);
    }
    sh = 0.5 + 0.5 * ObjSShadow (ro, sunDir);
  } else if (rd.y < 0.) {
    ro += (- ro.y / rd.y) * rd;
    sh = 0.5 + 0.5 * ObjSShadow (ro, sunDir);
    if (length (ro.xz) < 8.1) {
      isBg = false;
      rd = refract (rd, WaveNf (ro, 20.), 1./1.333);
      ro += rd;
      vc = HexVor (2. * ro.xz);
      vn.xz = - 0.9 * vc.yz;
      vn.y = 1.;
      vn = normalize (vn);
      s = mod (16. * vc.w, 1.);
      col = HsvToRgb (vec3 (0.1 + 0.3 * step (2. * s, 1.) + 0.1 * mod (5. * s, 1.),
        0.5 + 0.5 * mod (17. * s, 1.), 0.7 + 0.3 * mod (12. * s, 1.))) *
        (0.6 + 0.4 * smoothstep (0., 0.2, vc.x)) * (1. - 0.2 * Noisefv2 (128. * ro.xz));
    }
  }
  if (! isBg) col = col * (0.3 + 0.1 * max (dot (- sunDir.xz, vn.xz), 0.) +
     0.7 * sh * max (dot (sunDir, vn), 0.)) +
     0.1 * sh * pow (max (dot (normalize (sunDir - rd), vn), 0.), 64.);
  else col = sh * BgCol (ro, rd);
  return clamp (col, 0., 1.);
}

void mainImage (out vec4 fragColor, in vec2 fragCoord)
{
  mat3 vuMat;
  vec4 mPtr;
  vec3 ro, rd;
  vec2 canvas, uv, ori, ca, sa;
  float el, az, zmFac;
  canvas = iResolution.xy;
  uv = 2. * fragCoord.xy / canvas - 1.;
  uv.x *= canvas.x / canvas.y;
  tCur = iTime;
  mPtr = iMouse;
  mPtr.xy = mPtr.xy / canvas - 0.5;
  dstFar = 120.;
  az = 0.;
  el = -0.2 * pi;
  if (mPtr.z > 0.) {
    az += 3. * pi * mPtr.x;
    el += 1. * pi * mPtr.y;
  } else {
    az += 2.5 * pi * sin (0.003 * pi * tCur);
    el += 0.1 * pi * cos (0.03 * pi * tCur);
  }
  el = clamp (el, -0.4 * pi, -0.1 * pi);
  ori = vec2 (el, az);
  ca = cos (ori);
  sa = sin (ori);
  vuMat = mat3 (ca.y, 0., - sa.y, 0., 1., 0., sa.y, 0., ca.y) *
          mat3 (1., 0., 0., 0., ca.x, - sa.x, 0., sa.x, ca.x);
  zmFac = 7. - 2. * cos (az);
  rd = vuMat * normalize (vec3 (uv, zmFac));
  ro = vuMat * vec3 (0., 5., -70.);
  sunDir = vuMat * normalize (vec3 (1., 1., -1.));
  fragColor = vec4 (ShowScene (ro, rd), 1.);
}

float PrBoxDf (vec3 p, vec3 b)
{
  vec3 d;
  d = abs (p) - b;
  return min (max (d.x, max (d.y, d.z)), 0.) + length (max (d, 0.));
}

float PrBox2Df (vec2 p, vec2 b)
{
  vec2 d;
  d = abs (p) - b;
  return min (max (d.x, d.y), 0.) + length (max (d, 0.));
}

float PrCylDf (vec3 p, float r, float h)
{
  return max (length (p.xy) - r, abs (p.z) - h);
}

float PrCapsDf (vec3 p, float r, float h)
{
  return length (p - vec3 (0., 0., clamp (p.z, - h, h))) - r;
}

float PrCylAnDf (vec3 p, float r, float w, float h)
{
  return max (abs (length (p.xy) - r) - w, abs (p.z) - h);
}

vec2 gVec[7], hVec[7];
#define SQRT3 1.7320508

vec2 PixToHex (vec2 p)
{
  vec3 c, r, dr;
  c.xz = vec2 ((1./SQRT3) * p.x - (1./3.) * p.y, (2./3.) * p.y);
  c.y = - c.x - c.z;
  r = floor (c + 0.5);
  dr = abs (r - c);
  r -= step (dr.yzx, dr) * step (dr.zxy, dr) * dot (r, vec3 (1.));
  return r.xz;
}

vec2 HexToPix (vec2 h)
{
  return vec2 (SQRT3 * (h.x + 0.5 * h.y), (3./2.) * h.y);
}

void HexVorInit ()
{
  vec3 e = vec3 (1., 0., -1.);
  gVec[0] = e.yy;
  gVec[1] = e.xy;
  gVec[2] = e.yx;
  gVec[3] = e.xz;
  gVec[4] = e.zy;
  gVec[5] = e.yz;
  gVec[6] = e.zx;
  for (int k = 0; k < 7; k ++) hVec[k] = HexToPix (gVec[k]);
}

vec4 HexVor (vec2 p)
{
  vec4 sd, udm;
  vec2 ip, fp, d, u;
  float amp, a;
  amp = 0.7;
  ip = PixToHex (p);
  fp = p - HexToPix (ip);
  sd = vec4 (4.);
  udm = vec4 (4.);
  for (int k = 0; k < 7; k ++) {
    u = Hashv2v2 (ip + gVec[k]);
    a = 2. * pi * (u.y - 0.5);
    d = hVec[k] + amp * (0.4 + 0.6 * u.x) * vec2 (cos (a), sin (a)) - fp;
    sd.w = dot (d, d);
    if (sd.w < sd.x) {
      sd = sd.wxyw;
      udm = vec4 (d, u);
    } else sd = (sd.w < sd.y) ? sd.xwyw : ((sd.w < sd.z) ? sd.xyww : sd);
  }
  sd.xyz = sqrt (sd.xyz);
  return vec4 (SmoothMin (sd.y, sd.z, 0.3) - sd.x, udm.xy, Hashfv2 (udm.zw));
}

float SmoothMin (float a, float b, float r)
{
  float h;
  h = clamp (0.5 + 0.5 * (b - a) / r, 0., 1.);
  return mix (b, a, h) - r * h * (1. - h);
}

float SmoothBump (float lo, float hi, float w, float x)
{
  return (1. - smoothstep (hi - w, hi + w, x)) * smoothstep (lo - w, lo + w, x);
}

vec2 Rot2D (vec2 q, float a)
{
  return q * cos (a) + q.yx * sin (a) * vec2 (-1., 1.);
}

vec3 HsvToRgb (vec3 c)
{
  vec3 p;
  p = abs (fract (c.xxx + vec3 (1., 2./3., 1./3.)) * 6. - 3.);
  return c.z * mix (vec3 (1.), clamp (p - 1., 0., 1.), c.y);
}

const float cHashM = 43758.54;

float Hashfv2 (vec2 p)
{
  return fract (sin (dot (p, vec2 (37., 39.))) * cHashM);
}

vec2 Hashv2v2 (vec2 p)
{
  vec2 cHashVA2 = vec2 (37., 39.);
  return fract (sin (vec2 (dot (p, cHashVA2), dot (p + vec2 (1., 0.), cHashVA2))) * cHashM);
}

float Noisefv2 (vec2 p)
{
  vec2 t, ip, fp;
  ip = floor (p);  
  fp = fract (p);
  fp = fp * fp * (3. - 2. * fp);
  t = mix (Hashv2v2 (ip), Hashv2v2 (ip + vec2 (0., 1.)), fp.y);
  return mix (t.x, t.y, fp.x);
}

float Fbm2 (vec2 p)
{
  float f, a;
  f = 0.;
  a = 1.;
  for (int i = 0; i < 5; i ++) {
    f += a * Noisefv2 (p);
    a *= 0.5;
    p *= 2.;
  }
  return f * (1. / 1.9375);
}

float Fbmn (vec3 p, vec3 n)
{
  vec3 s;
  float a;
  s = vec3 (0.);  
  a = 1.;
  for (int i = 0; i < 5; i ++) {
    s += a * vec3 (Noisefv2 (p.yz), Noisefv2 (p.zx), Noisefv2 (p.xy));
    a *= 0.5;  
    p *= 2.;
  }
  return dot (s, abs (n));
}

vec3 VaryNf (vec3 p, vec3 n, float f)
{
  vec3 g;
  vec2 e = vec2 (0.1, 0.);
  g = vec3 (Fbmn (p + e.xyy, n), Fbmn (p + e.yxy, n), Fbmn (p + e.yyx, n)) - Fbmn (p, n);
  return normalize (n + f * (g - n * dot (n, g)));
}

// Aliasing and antialiasing demo
// infinitely scrolling checkerboard
// change parameters below to adjust kernel, jitter, sample pattern, etc.

// worst: samples=1, DIST=GRID, FILT=BOX
// good: samples=8, DIST=QUASIRANDOM, FILT=LANCZOS

// sample distribution, options for dist[]
#define GRID 0			/* uniform grid */
#define JITTER 1		/* random jitter */
#define QUASIRANDOM 2	/* Hammersley quasi-random */

// filter kernel, options for filt[]
#define BOX 0				/* Equal-weight spatial box filter */
#define LANCZOS 1			/* Lanczos filter of order given by lanczosOrder */
#define LANCZOS_RADIAL 2	/* Radially symmetric Lanczos filter */
#define GAUSSIAN 3			/* Gaussian weights */
#define GAUSS_SAMP 4		/* Gaussian-distributed importance samples */

// sampling parameters
struct Parameters {
    int samples;	// samples * samples per pixel
    int dist;		// sample distribution
    int filt;		// filter kernel
    int order;		// Lanczos order (for Lanczos) integer >= 1
};


///////////////////////////////////////////////////////////////////////
// begin changable parameters

// scrolling speed
const float speed = 0.1;

// number of options to compare
const int splits = 2;

const Parameters param[splits] = Parameters[splits](
    Parameters(8, GRID, BOX, 1),
    Parameters(8, GRID, GAUSSIAN, 1)
    );

// end of parameters
///////////////////////////////////////////////////////////////////////

// Epic PCG3D generator
uvec3 PCG3D(uvec3 v) {
    v = 1664525u * v + 1013904223u;
    v.x += v.y*v.z; v.y += v.z*v.x; v.z += v.x*v.y;
    v.x += v.y*v.z; v.y += v.z*v.x; v.z += v.x*v.y;
    return v>>16u;
}

// jitter for sample i,j
vec2 jitter(int dist, int i, int j) {
    // random jitter within cell
    if (dist == JITTER)
    	return vec2(PCG3D(uvec3(gl_FragCoord.xy,0)).xy)/float(0xffff);

	// jitter based on Hammersley bit-reversal sequence
    if (dist == QUASIRANDOM) {
    	vec2 offset = vec2(0), scale = vec2(0.5);
        for(int x = j;  x > 0;  x>>=2, scale.x*=0.5)
            offset.x += float(x&1) * scale.x;
        for(int y = i;  y > 0;  y>>=2, scale.y*=0.5)
            offset.y += float(y&1) * scale.y;
        return offset;
    }

    // no jitter
    return vec2(0.5);
}

// compute new location for importance sampling
vec2 distribution(int filt, vec2 offset) {
    if (filt == GAUSS_SAMP) {
        // Box-Mueller transform to get Gaussian-distributed sample locations
        // assumes components of offset are distributed between -1 and 1
        float radius = sqrt(-2.0 * log(offset.x*.5 + .5));
        float theta = 3.14159265359 * offset.y;
        return vec2(radius * cos(theta), radius * sin(theta));
    }

    // all others use weights
    return offset;
}

// Lanczos kernel of order a
float lanczos(float x, float a) {
    // basic Lanczos function: sinc(x) * sinc(x/a)
    float f = a * sin(x) * sin(x/a) / (x*x);

    if (abs(x)>a) f = 0.0;        // Lanczos is 0 for x past window
    if (f != f) f = 1.0;          // NaN for x=0, in the limit = 1
    return f;
}

// filter weight
float weight(int filt, vec2 offset, float order) {
    // Gaussian with standard deviation width/2:
    if (filt == GAUSSIAN)
        return exp(-2. * dot(offset,offset));

    if (filt == LANCZOS)
   		return lanczos(offset.x, order)*lanczos(offset.y, order);

    if (filt == LANCZOS_RADIAL)
    	return lanczos(length(offset), order);

    // equal-weight for box filter or Gaussian importance sampling
    return 1.0;
}

// filter width in pixels
float filterWidth(int filt, float order) {
    if (filt == GAUSSIAN)
    	return 1.66;

    if (filt == LANCZOS || filt == LANCZOS_RADIAL)
   		return order;

    // equal-weight box filter
    return 1.;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // which set of options?
    int s = splits * int(fragCoord.x) / int(iResolution.x);
    int samples = param[s].samples;
    int dist = param[s].dist;
    int filt = param[s].filt;
    float order = float(param[s].order);

    vec2 pix = 2.0 * fragCoord.xy / iResolution.xy - 1.0;
    vec2 kernelSize = filterWidth(filt, order) / iResolution.xy;

    vec4 color = vec4(0);
	for(int i=0; i < samples; ++i) {
	    for(int j=0; j < samples; ++j) {
    	    // sample offset (possibly with jitter)
        	vec2 offset = (vec2(i,j) + jitter(dist, i,j)) / float(samples) - 0.5;
            offset = distribution(filt, offset);

	        // ray direction
    	    vec3 ray = vec3(pix + offset*kernelSize,2);

        	vec3 samp = vec3(0.5, 0.7, 0.9); // background color
            if (ray.y < 0.0) { // ray intersects x/z plane through 0,-1,0
	            // where is intersection
    	        vec2 p = -ray.xz / ray.y + speed * iTime;
                samp = vec3((int(p.x)&1) == (int(p.y)&1));
            }

	        // accumulate with weight
    	    color += vec4(samp,1) * weight(filt, offset, order);
 	   }
	}

	fragColor = color / color.a;
}

const float PI = 3.14159265359;
const float TAU = 6.28318530718;

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 points[15];
    points[0] = vec2(.1, .1);
    points[1] = vec2(.15, -.2);
    points[2] = vec2(-.3, .05);
    points[3] = vec2(-.25, -.1);
    points[4] = vec2(-.12, .23);
    points[5] = vec2(.3, .28);
    points[6] = vec2(.11, .35);
    points[7] = vec2(.4, -.4);
    points[8] = vec2(-.223, .3);
    points[9] = vec2(.4, -.18);
    points[10] = vec2(.32, -.1);
    points[11] = vec2(.2, -.32);
    points[12] = vec2(-.13, .15);
    points[13] = vec2(-.102, -.17);
    points[14] = vec2(-.25, -.31);

	vec2 uv = fragCoord.xy / iResolution.xy;
    uv -= .5;
    uv.x *= iResolution.x / iResolution.y;
    float dist = length(uv);

    float speed = .75;
    float angle = mod(-iTime * speed, 2. * PI);
    float clippedGreen = 0.;

    // draw outer ring
    float containerRadius = .475;
    float clipToRadius = clamp(floor(containerRadius / dist), 0., 1.);
    float containerThickness = max(.01, .75 * (pow(clamp(texture(iChannel0, abs(uv)).r, .1, 2.), 4.)
                                   + pow(clamp(texture(iChannel0, abs(vec2(uv.y, uv.x))).r, .1, 2.), 4.)));
    float container = smoothstep(containerRadius + containerThickness / 2., containerRadius, dist)
        * smoothstep(containerRadius - containerThickness / 2., containerRadius, dist);

    // draw blips
    float blipSpeed = .075;
    float ringThickness = .01;
    for (int x = 0; x < 15; x++) {
    	float blipDist = distance(uv, points[x]);

    	float blipAngle = mod(atan(points[x].y, points[x].x) + PI * 2., PI * 2.) - PI / 3.;
    	float angleDiff = mod(angle - blipAngle, 2. * PI);

    	float blipRadius = (1. - angleDiff) * blipSpeed;

    	float addend = smoothstep(blipRadius, blipRadius - ringThickness / 2., blipDist)
        	* pow(smoothstep(0., blipRadius - ringThickness / 2., blipDist), 3.);
        clippedGreen += max(0., mix(addend, 0., blipRadius / blipSpeed));
    }

    // draw line from center
    float lineThickness = .015;
    vec2 line = normalize(vec2(cos(angle), sin(angle)));
    float multiply = clamp(sign(dot(uv, line)), 0., 1.);
    float distFromLine = sqrt(pow(dist, 2.) - pow(dot(uv, line), 2.));
    clippedGreen += pow(smoothstep(lineThickness / 2., 0., distFromLine), 3.) * multiply;

    // draw grid
    float gridIncrement = .1;
    float gridLineThickness = 1. /iResolution.y;
    float gridAddend = (1. - step(gridLineThickness, mod(uv.x, gridIncrement)))
        + (1. - step(gridLineThickness, mod(uv.y, gridIncrement)));
    clippedGreen += gridAddend;

    // draw gradient
    float gradientAngleAmount = PI / 2.;
    float uvAngle = mod(atan(uv.y, uv.x) + PI * 2., PI * 2.);
    float angleDiff = mod(uvAngle - angle, 2. * PI);
    clippedGreen += smoothstep(gradientAngleAmount, 0., angleDiff);

    // why doesn't changing the alpha value do anything?
    // color.a = 0.;
    uv.x /= iResolution.x / iResolution.y;
    uv += .5;
    vec4 color = texture(iChannel1, uv);
    color.g += clippedGreen * clipToRadius + container;
    fragColor = color;
}

