// References:
//		Physically-Based Shading at Disney 2012;
// 		Understanding the Masking-Shadowing Function in Microfacet-Based BRDFs;
//	
// Single directional light source;
// No ambient lighting;
// No anti-aliasing (solved only sphere jagged edges);
// One can change light's position in func called 'init_light_view_directions_ts'.
// One can change material's params in func called 'generate_material'.
//
// Prefixis:
// 		is 	- image space;
// 		ndc - normalized device coordinates space;
// 		ts	- tangent space;


const float	c_1_over_pi = 1.0 / 3.1415926535;

struct material {
    vec3 	base_color;
    vec3 	reflect_color;
   	vec2 	linear_roughness; 	// [0.07, 1], ggx is zero when roughness is zero.
    float 	metallic_mask;		
};


vec3 calc_diffuse_term(float dot_nl, float dot_nv, float dot_lh, vec3 base_color, float rough_s)
{
    float fd_90_minus_1 = 2.0 * dot_lh * dot_lh * rough_s - 0.5;

    return base_color * c_1_over_pi 
        * (1.0 + fd_90_minus_1 * pow(1.0 - dot_nl, 5.0))
        * (1.0 + fd_90_minus_1 * pow(1.0 - dot_nv, 5.0));
}

// anisotropic GGX / Trowbridge-Reitz
//
float calc_distribution_ggx(float dot_nh, float dot_ht, float dot_hb, vec2 linear_roughness)
{
    float rought_x 	= linear_roughness.x * linear_roughness.x;
    float rought_y 	= linear_roughness.y * linear_roughness.y;
    float rough_x_s = rought_x * rought_x;
	float rough_y_s = rought_y * rought_y;

	float d = (dot_nh * dot_nh
        + dot_ht * dot_ht * (1.0 / rough_x_s)
    	+ dot_hb * dot_hb * (1.0 / rough_y_s));

    return c_1_over_pi * (1.0 / (rought_x * rought_y * d * d));
}

vec3 calc_fresnel_schlick(vec3 f0, float dot_vn)
{
	return f0 + (1.0 - f0) * pow(1.0 - dot_vn, 5.0);
}

float calc_smith_lambda(float a2, float cos_angle)
{
    if (cos_angle < 0.01) return 0.0;

    float sin_angle = sqrt(1.0 - cos_angle * cos_angle);
	float tan_angle = sin_angle * (1.0 * cos_angle);

	return sqrt(1.0 + a2 * tan_angle * tan_angle) * 0.5 - 0.5;
}

float calc_masking_shadow_factor(float dot_nl, float dot_nv, float rought_s)
{
    // smith correlated
	float a2 		= rought_s * 0.5;
	float lambda_l 	= calc_smith_lambda(a2, dot_nl);
	float lambda_v 	= calc_smith_lambda(a2, dot_nv);
	return 1.0f / (1.0 + lambda_l + lambda_v);
}

vec3 calc_specular_term(vec3 fresnel, float dot_nl, float dot_nv, float dot_nh, float dot_lh, 
                        float dot_ht, float dot_hb, vec2 linear_roughness, float rough_s)
{
    float v_1_over_denom = 1.0 / (4.0 * dot_nl * dot_nv);

	return fresnel
        * calc_distribution_ggx(dot_nh, dot_ht, dot_hb, linear_roughness)
        * calc_masking_shadow_factor(dot_nl, dot_nv, rough_s)
        * v_1_over_denom;
}

// Performs tone mapping and gamma corretion.
//
vec3 convert_hdr_to_srgb(vec3 hdr)
{
    const float gamma = 1.0 / 2.2;

    vec3 ldr = hdr * (1.0 / (hdr + 1.0));	// hell of a tone mapping
    return vec3(pow(ldr.x, gamma), 
                pow(ldr.y, gamma), 
                pow(ldr.z, gamma));			// for some reason I could not write pow(ldr, gamma)
}

material generate_material(int time_base)
{
    //material m = material(/* base_color */		vec3(0.5),
    //                      /* reflect_color */		vec3(1.0),
    //                      /* linear_roughness */	vec2(0.15, 0.15),
    //                      /* metallic_mask */		0.8);

    vec3 fct_bc		= cos(vec3(time_base) * vec3(0.002, 0.0013, 0.0015));
    vec4 fct_pack	= sin(vec4(time_base) * vec4(0.005, 0.012, 0.0188, 0.0057));

    material m;
    m.base_color 			= mix(vec3(0.1), vec3(1.0), fct_bc * fct_bc);
    m.reflect_color 		= mix(vec3(0.4), vec3(1.0), pow(fct_pack.x, 4.0));
    m.linear_roughness.x 	= mix(0.11, 1.0, pow(fct_pack.y, 6.0));
    m.linear_roughness.y 	= mix(0.11, 1.0, pow(fct_pack.z, 6.0));
   	m.metallic_mask 		= mix(0.0, 0.8, pow(fct_pack.w, 4.0));

    return m;
}

// Sets directions to the light and to the view in the tangent space 
// using the given position of a fragment.
//
void init_light_view_directions_ts(vec3 pos_ndc, int time_base, out vec3 to_light_ts, out vec3 to_view_ts)
{
    float x = cos(float(time_base) * 0.02);
    float y = sin(float(time_base) * 0.01);
    vec3 direction_to_light_ndc = normalize(vec3(x, y, 1.0));

    // compose matrix ndc -> ts.
    vec3 t = normalize(dFdx(pos_ndc));
    vec3 b = normalize(dFdy(pos_ndc));
    vec3 n = normalize(cross(t, b));
    mat3 ndc_to_ts_matrix = transpose(mat3(t, b, n));

    to_light_ts = ndc_to_ts_matrix * direction_to_light_ndc;
    to_view_ts 	= ndc_to_ts_matrix * normalize(vec3(0, 0, 1) - pos_ndc);
}

void mainImage( out vec4 frag_color, in vec2 fragCoord)
{
	const float c_radius_ndc = 0.2;

    // discard a fragment if it does not belong to the spehre.
    // xy_is is relative to the center of the render target.
    vec2 xy_is 		= gl_FragCoord.xy * (1.0 / iResolution.xy) + vec2(-0.5);
    vec2 xy_ndc 	= xy_is * vec2(1, iResolution.y * (1.0 / iResolution.x));
    float dist_ndc 	= length(xy_ndc);

    if (dist_ndc > c_radius_ndc) discard;

    // reconstruct fragment's position on a sphere in the ndc space.
    float z_ndc 		= sqrt(c_radius_ndc * c_radius_ndc + dot(xy_ndc, -xy_ndc));
    vec3 position_ndc 	= vec3(xy_ndc.xy, z_ndc);

    // light equation's directions
    vec3 l_ts;
    vec3 v_ts;
    init_light_view_directions_ts(position_ndc, iFrame, l_ts, v_ts);

    vec3 h_ts 		= normalize(l_ts + v_ts);
    float dot_nl 	= clamp(l_ts.z, 0.0, 1.0);
    float dot_nv 	= clamp(v_ts.z, 0.0, 1.0);
    float dot_nh	= clamp(h_ts.z, 0.0, 1.0);
    float dot_lh 	= clamp(dot(l_ts, h_ts), 0.0, 1.0); // same as dot(v_ts, h_ts)
    float dot_ht	= h_ts.x;
    float dot_hb	= h_ts.y;

    // material params
    material m 			= generate_material(iFrame);
    float rough_s		= dot_ht * dot_ht * m.linear_roughness.x * m.linear_roughness.x
        				+ dot_hb * dot_hb * m.linear_roughness.y * m.linear_roughness.y; // uniform roughness squared
    vec3 reflect_color 	= 0.16 * m.reflect_color * m.reflect_color;
    vec3 f0				= mix(reflect_color, m.base_color, m.metallic_mask);
    vec3 fresnel		= calc_fresnel_schlick(f0, dot_nv);

    // lighting
    vec3 diffuse_factor		= (1.0 - fresnel) * (1.0 - m.metallic_mask);
    vec3 diffuse_term 		= diffuse_factor * calc_diffuse_term(dot_nl, dot_nv, dot_lh, m.base_color, rough_s);
   	vec3 specular_term 		= calc_specular_term(fresnel, dot_nl, dot_nv, dot_nh, dot_lh, 
                                             dot_ht, dot_hb, m.linear_roughness, rough_s);    

    vec3 hdr	= (diffuse_term + specular_term) * dot_nl;
    vec3 srgb 	= convert_hdr_to_srgb(hdr);

    // interpolate between black color and srgb value to get rid of those nasty jagged edges.
    const float c_radius_falloff_start_ndc = c_radius_ndc - 0.007;
    float mix_factor	= (c_radius_falloff_start_ndc - dist_ndc) / (c_radius_ndc - dist_ndc);
    vec3 final_color	= mix(vec3(0.0), srgb, mix_factor);

	frag_color = vec4(final_color, 1.0);
}

