float map(vec3 p)
{
    float time = iTime;
    vec2 uv = gl_FragCoord.xy / iResolution.xy;
    // length(max(vec3(dot(cos(p), sin(p))), cos(p))*2.0-1.0) - 3.0;
    return length(sin(p)*dot(cos(p),tan(p))*2.0-1.0)-3.0;
}
vec3 trace(vec2 uv)
{
    float time = iTime;
    float t = 0.0;
    uv = uv * 2.0 - 1.0;
    uv.x *= iResolution.x / iResolution.y;
    vec3 rd = normalize(vec3(uv, 1.0));
    rd.xz *= mat2(cos(time/2.0), -sin(time/2.0), sin(time/2.0), cos(time/2.0));
    vec3 ro = vec3(cos(time)+iTime/2., sin(6.0)-3., sin(time));
    for(int i = 0; i < 24; i++)
    {
        vec3 p = ro+t*rd;
        float d = map(p);
        t += d * 0.5;
    }
    float fog = 1.0/(1.0+t*t*0.1);
    vec3 fc = vec3(fog);
    return fc;
}
void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    float time = iTime;
	vec2 uv = fragCoord.xy / iResolution.xy;
    vec3 p = normalize(vec3(uv, 1.0));
    vec3 x = trace(uv);
	fragColor = vec4( cos(uv.y) ) +
        sin(p.z)/2.
        *vec4(.5,.2,0.,1.)
        +vec4(0.25,0.25,0.,1.)
        -vec4(x, 1.0);
}

// Referencing the tutorial here: http://fabricecastel.github.io/blog/2016-02-11/main.html

// ray computation vars
const float PI = 3.14159265359;
const float fov = 50.0;
const float fovx = PI * fov / 360.0;
const float S = 0.01;
const vec3 deltax = vec3(S ,0, 0);
const vec3 deltay = vec3(0 ,S, 0);
const vec3 deltaz = vec3(0 ,0, S);

float distanceToNearestSurface(vec3 p){
    float s = 1.0;
    vec3 q = abs(p) - vec3(s);
    float dist = max(max(q.x, q.y), q.z);
    if(q.x > 0.0 && q.y > 0.0 && q.z > 0.0){
        dist = length(q);
    }
    return dist;
}

vec3 computeSurfaceNormal(vec3 p){
    float d = distanceToNearestSurface(p);
    return normalize(vec3(
    	distanceToNearestSurface(p + deltax)-d,
        distanceToNearestSurface(p + deltay)-d,
        distanceToNearestSurface(p + deltaz)-d
    ));
}

vec3 computeLambert(vec3 p, vec3 n, vec3 l){
    return vec3(dot(normalize(l-p), n));
}

vec3 intersectsWithWorld(vec3 p, vec3 dir){
  	float dist = 0.0;
    float nearest = 0.0;
    bool hit = false;
    for(int i = 0; i < 20; i++){
        float nearest = distanceToNearestSurface(p + dir*dist);
        if(nearest < 0.01){
            vec3 hit = p + dir * dist;
            vec3 light = vec3(100.0*sin(iTime),
                             	30.0 * cos(iTime),
                              	30.0 * cos(iTime));
            return computeLambert(hit, computeSurfaceNormal(hit), light);
        }
        dist += nearest;
    }
    return vec3(0.0);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy;

  	// camera attributes
    // cameraDirection and cameraUp MUST be normalized
    // (ie. their length must be equal to 1)
    float cameraDistance = 10.0;
    vec3 cameraPosition = vec3(cameraDistance * sin(iTime), 0.2 * cameraDistance * cos(iTime), cameraDistance * cos(iTime));
    vec3 cameraDirection = vec3(-1.0*sin(iTime), 0.0, -1.0 * cos(iTime));
    vec3 cameraUp = vec3(0.0, 1.0, 0.0);

    // generate the ray for this pixel
    float fovy = fovx * iResolution.y/iResolution.x;
    float ulen = tan(fovx);
	float vlen = tan(fovy);
    vec2 camUV = uv*2.0 - vec2(1.0, 1.0);
    vec3 nright = normalize(cross(cameraUp, cameraDirection));
    vec3 pixel = cameraPosition + cameraDirection + nright*camUV.x*ulen + cameraUp*camUV.y*vlen;
    vec3 rayDirection = normalize(pixel - cameraPosition);

	vec3 pixelColor = intersectsWithWorld(cameraPosition, rayDirection);
    fragColor = vec4(pixelColor, 1.0);
}

#define SPEED 1.0
// #define SMOOTH 1
#define NUM_NOISE_OCTAVES 4

float hash(float n) { return fract(sin(n) * 1e4); }
float hash(vec2 p) { return  /*(sin(iTime*3.0*SPEED)*0.02) +*/ fract(1e4 * sin(17.0 * p.x + p.y * 0.1) * (0.1 + abs(sin(p.y * 13.0 + p.x)))); }

float noise(float x) {
    float i = floor(x);
    float f = fract(x);
    float u = f * f * (3.0 - 2.0 * f);
    return mix(hash(i), hash(i + 1.0), u);
}
float noise(vec2 x) {
    vec2 i = floor(x);
    vec2 f = fract(x);

	// Four corners in 2D of a tile
	float a = hash(i);
    float b = hash(i + vec2(1.0, 0.0));
    float c = hash(i + vec2(0.0, 1.0));
    float d = hash(i + vec2(1.0, 1.0));

    // Simple 2D lerp using smoothstep envelope between the values.
	// return vec3(mix(mix(a, b, smoothstep(0.0, 1.0, f.x)),
	//			mix(c, d, smoothstep(0.0, 1.0, f.x)),
	//			smoothstep(0.0, 1.0, f.y)));

	// Same code, with the clamps in smoothstep and common subexpressions
	// optimized away.
    vec2 u = f * f * (3.0 - 2.0 * f);
	return mix(a, b, u.x) + (c - a) * u.y * (1.0 - u.x) + (d - b) * u.x * u.y;
}


float fbm(float x) {
	float v = 0.0;
	float a = 0.5;
	float shift = float(100);
	for (int i = 0; i < NUM_NOISE_OCTAVES; ++i) {
		v += a * noise(x);
		x = x * 2.0 + shift;
		a *= 0.5;
	}
	return v;
}
float fbm(vec2 x) {
	float v = 0.0;
	float a = 0.5;
	vec2 shift = vec2(100);
	// Rotate to reduce axial bias
    mat2 rot = mat2(cos(0.5), sin(0.5), -sin(0.5), cos(0.50));
	for (int i = 0; i < NUM_NOISE_OCTAVES; ++i) {
		v += a * noise(x);
		x = rot * x * 2.0 + shift;
		a *= 0.5;
	}
	return v;
}



float voronoi2D(vec2 uv) {
    vec2 fl = floor(uv);
    vec2 fr = fract(uv);
    float res = 1.0;
    for( int j=-1; j<=1; j++ ) {
        for( int i=-1; i<=1; i++ ) {
            vec2 p = vec2(i, j);
            float h = hash(fl+p);
            vec2 vp = p-fr+h;
            float d = dot(vp, vp);

            res +=1.0/pow(d, 8.0);
        }
    }
    return pow( 1.0/res, 1.0/16.0 );
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    float size = 0.2;
    float limit = 0.5 + (sin(iTime*10.0)*0.001);
    float border = 0.02;
    float t = iTime*0.8;
	vec2 uv = fragCoord.xy / iResolution.xy;

    float vo = voronoi2D(((1.0/size) * uv ));

    vec2 coord = fragCoord.xy / 50.0;

    float c = fbm(vec2(vo/1.0 - coord.x + t,  coord.y  ));
    vec3 color = vec3(step(limit-border,c), step(limit+border, c), 1);
    if (color.x == 1.0 && color.y == 1.0 && color.z == 1.0)  { color = vec3(0.8, 0.05, 0.05); }
    else if (color.x == 1.0 && color.y != 1.0 && color.x == 1.0) { color = vec3(0.5, 0.05, 0.05); }
    else { color = vec3(0.2, 0.05, 0.05); }

#ifdef SMOOTH
    c = smoothstep(limit - border, limit, c) - smoothstep(limit, limit + border, c);
    fragColor = vec4(c * c * c, 0.25 + 0.75 * c * c, 0.5 + 0.5 * c, 1.0);
#else
    fragColor.rgb = clamp(color, 0.0, 1.0);
  //  fragColor.rgb = clamp(vec3(c,c,c), 0.0, 1.0);
#endif

}

#define MAX_STEPS 50

// undef to disable hole, then parameter s.w not needed
// #define CONVEX

// sdUberprim with precomputed constants
float sdUnterprim(vec3 p, vec4 s, vec3 r, vec2 ba, float sz2) {
    vec3 d = abs(p) - s.xyz;
    float q = length(max(d.xy, 0.0)) + min(0.0,max(d.x,d.y)) - r.x;
    // hole support: without this line, all results are convex
#ifndef CONVEX    
    q = abs(q) - s.w;
#endif

    vec2 pa = vec2(q, p.z - s.z);
    vec2 diag = pa - vec2(r.z,sz2) * clamp(dot(pa,ba), 0.0, 1.0);
    vec2 h0 = vec2(max(q - r.z,0.0),p.z + s.z);
    vec2 h1 = vec2(max(q,0.0),p.z - s.z);

    return sqrt(min(dot(diag,diag),min(dot(h0,h0),dot(h1,h1))))
        * sign(max(dot(pa,vec2(-ba.y, ba.x)), d.z)) - r.y;
}

// s: width, height, depth, thickness
// r: xy corner radius, z corner radius, bottom radius offset
float sdUberprim(vec3 p, vec4 s, vec3 r) {
    // these operations can be precomputed
    s.xy -= r.x;
#ifdef CONVEX  
    r.x -= r.y;
#else
    r.x -= s.w;
    s.w -= r.y;
#endif
    s.z -= r.y;
    vec2 ba = vec2(r.z, -2.0*s.z);
    return sdUnterprim(p, s, r, ba/dot(ba,ba), ba.y);
}

// example parameters
#define SHAPE_COUNT 9.0
void getfactor (int i, out vec4 s, out vec3 r) {
    //i = 4;
    if (i == 0) { // cube
        s = vec4(1.0);
        r = vec3(0.0);
    } else if (i == 1) { // cylinder
        s = vec4(1.0);
        r = vec3(1.0,0.0,0.0);
    } else if (i == 2) { // cone
        s = vec4(0.0,0.0,1.0,1.0);
        r = vec3(0.0,0.0,1.0);
	} else if (i == 3) { // pill
        s = vec4(1.0,1.0,2.0,1.0);
        r = vec3(1.0,1.0,0.0);
    } else if (i == 4) { // sphere
        s = vec4(1.0);
        r = vec3(1.0,1.0,0.0);
    } else if (i == 5) { // pellet
        s = vec4(1.0,1.0,0.25,1.0);
        r = vec3(1.0,0.25,0.0);
    } else if (i == 6) { // torus
        s = vec4(1.0,1.0,0.25,0.25);
        r = vec3(1.0,0.25,0.0);
    } else if (i == 7) { // pipe
        s = vec4(vec3(1.0),0.25);
        r = vec3(1.0,0.1,0.0);
    } else if (i == 8) { // corridor
        s = vec4(vec3(1.0),0.25);
        r = vec3(0.1,0.1,0.0);
	}
}

void doCamera( out vec3 camPos, out vec3 camTar, in float time, in float mouseX )
{
    float an = 1.5 + sin(time * 0.1) * 0.7;
	camPos = vec3(4.5*sin(an),2.0,4.5*cos(an));
    camTar = vec3(0.0,0.0,0.0);
}

vec3 doBackground( void )
{
    return vec3( 0.0, 0.0, 0.0);
}

// polynomial smooth min (k = 0.1);
float smin( float a, float b, float k )
{
    float h = clamp( 0.5+0.5*(b-a)/k, 0.0, 1.0 );
    return mix( b, a, h ) - k*h*(1.0-h);
}

float doobject (vec3 p, float k) {
    float u = smoothstep(0.0,1.0,smoothstep(0.0,1.0,fract(k)));
    int s1 = int(mod(k,SHAPE_COUNT));
    int s2 = int(mod(k+1.0,SHAPE_COUNT));

    vec4 sa,sb;
    vec3 ra,rb;
    getfactor(s1,sa,ra);
    getfactor(s2,sb,rb);

    return  sdUberprim(p.zyx, mix(sa,sb,u), mix(ra,rb,u));
}

bool interior;

float doModel( vec3 p ) {
    float k = iTime*0.5;
    float d = doobject(p - vec3(0.0,0.0,-0.5), k);
    float d2 = doobject(p - vec3(0.0,0.0,0.5), k + 1.0);
    if (interior)
    	d = min(d, d2);
   	else
    	d = smin(d, d2, 0.4);

    return d;
}

float calcIntersection( in float t, in vec3 ro, in vec3 rd, vec2 pixel, float bias, inout int steps )
{
	const float maxd = 20.0;           // max trace distance
	float aperture = 0.7071067811866 * max(pixel.y,pixel.x);        // aperture of cone
    float C = sqrt(aperture*aperture + 1.0);
    // radius of sphere at t=1
    float R = aperture/C;
    // constant adjustment factor for t so that the cone sphere touches the distance radius
    float A = C / (C + bias*aperture);
    // for a better demo of how the above factors work, see https://www.shadertoy.com/view/4lfBWH

	float res = -1.0;
    float tc = (bias > 0.0)?0.0:1.0;
    t = t * C;
    for( int i=0; i<MAX_STEPS; i++ ) {
        steps = steps + 1;
		float limit = bias*R*t;
        // add small bias to reduce iteration count
        limit += 1e-03*t;
	    float h = doModel( ro+rd*t );
        t = (t + h)*A;
    	if((h <= limit) || (t > maxd)) {
            break;
        }
    }

    if( t<maxd ) res = t;
    return res / C;
}

vec3 calcNormal( in vec3 pos )
{
    const float eps = 0.002;             // precision of the normal computation

    const vec3 v1 = vec3( 1.0,-1.0,-1.0);
    const vec3 v2 = vec3(-1.0,-1.0, 1.0);
    const vec3 v3 = vec3(-1.0, 1.0,-1.0);
    const vec3 v4 = vec3( 1.0, 1.0, 1.0);

	return normalize( v1*doModel( pos + v1*eps ) + 
					  v2*doModel( pos + v2*eps ) + 
					  v3*doModel( pos + v3*eps ) + 
					  v4*doModel( pos + v4*eps ));
}

mat3 calcLookAtMatrix( in vec3 ro, in vec3 ta, in float roll )
{
    vec3 ww = normalize( ta - ro );
    vec3 uu = normalize( cross(ww,vec3(sin(roll),cos(roll),0.0) ) );
    vec3 vv = normalize( cross(uu,ww));
    return mat3( uu, vv, ww );
}

bool dorender( inout float dist, inout int steps, out vec3 position, in vec2 p, in vec2 pixel, in float bias)
{
    //-----------------------------------------------------
    // camera1
    //-----------------------------------------------------
    // camera movement
    vec3 ro, ta;
    doCamera( ro, ta, iTime, 0.0 );

    // camera matrix
    mat3 camMat = calcLookAtMatrix( ro, ta, 0.0 );  // 0.0 is the camera roll

	// create view ray
	vec3 rd = normalize( camMat * vec3(p.xy,2.0) ); // 2.0 is the lens length

    //-----------------------------------------------------
	// render
    //-----------------------------------------------------

	vec3 col = doBackground();

	// raymarch
    float t = calcIntersection( dist, ro, rd, pixel, bias, steps );
    dist = t;
    if( t>-0.5 )
    {
        // geometry
        position = ro + t*rd;
        return true;
	}
    return false;
}

vec3 hue2rgb (float hue) {
    return clamp(abs(mod(hue * 6.0 + vec3(0.0,4.0,2.0),6.0) - 3.0) - 1.0,0.0,1.0);
}

// maps n=0 to blue, n=1 to red, n=0.5 to green
vec3 normhue (float n)  {
    return hue2rgb((1.0 - clamp(n,0.0,1.0)) * 0.66667);
}


void mainImage( out vec4 fragColor, in vec2 fragCoord)
{
	vec2 p = (2.0*fragCoord.xy - iResolution.xy)/iResolution.y;
    vec2 c0 = vec2(-2.0,-2.0);
    vec2 c1 = vec2(2.0,2.0);
    const int N = 9;
    vec3 color = vec3(0.1);
    int steps = 0;
    float dist = 0.0;
    for (int i = 0; i <= N; ++i) {
        vec2 c = (c0 + c1)*0.5;
        vec2 h = (c1 - c0)*0.5;
        if (p.x < c.x) {
            c1.x = c.x;
        } else {
            c0.x = c.x;
        }
        if (p.y < c.y) {
            c1.y = c.y;
        } else {
            c0.y = c.y;
        }
        vec2 u = vec2(c0.x,c1.x);
        vec2 v = vec2(c0.y,c1.y);

        vec2 center = vec2(u.y + u.x, v.y + v.x)*0.5;
        vec2 radius = vec2(u.y - u.x, v.y - v.x)*0.5;

        vec3 pos;
        bool outer_hit = dorender(dist, steps, pos, center, radius, 1.0);
        float dist2 = dist;
        bool inner_hit = dorender(dist2, steps, pos, center, radius, -1.0);
        if (!outer_hit || inner_hit || (i == N)) {
            color = normhue(float(steps)/float(MAX_STEPS * N));
            float q = abs(max(abs(p.x - center.x), abs(p.y - center.y)) - radius.x)-0.0001;
            q = clamp(q*200.0, 0.0, 1.0);
            color *= vec3(q);
            break;
        }
    }    
    fragColor = vec4(color, 1.0);
}

float distanceToLine (vec2 s, vec2 p, vec2 q)
{
    return abs ((q.y - p.y) * s.x - (q.x - p.x) * s.y 
        + q.x * p.y - q.y * p.x) / distance (p, q);
}

float triangle (vec2 pos, float t, float val, float step)
{
    float t1 = t * 0.523;
    float t2 = t * 0.645;
    float t3 = t * 0.779;

    vec2 p1 = 0.5 + 0.5 * vec2 (cos (t1      ), sin (t2      ));
    vec2 p2 = 0.5 + 0.5 * vec2 (cos (t2 + 1.0), sin (t3 + 1.0));
    vec2 p3 = 0.5 + 0.5 * vec2 (cos (t3 + 2.0), sin (t1 + 2.0));

    float d = distanceToLine (pos, p1, p2);
    val += d < 0.01 ? step : 0.0;

    d = distanceToLine (pos, p2, p3);
    val += d < 0.01 ? step : 0.0;

    d = distanceToLine (pos, p3, p1);
    val += d < 0.01 ? step : 0.0;

    return val;
}

vec3 Red     = vec3 (1.0, 0.0, 0.0);
vec3 Yellow  = vec3 (1.0, 1.0, 0.0);
vec3 Green   = vec3 (0.0, 1.0, 0.0);
vec3 Cyan    = vec3 (0.0, 1.0, 1.0);
vec3 Blue    = vec3 (0.0, 0.0, 1.0);
vec3 Magenta = vec3 (1.0, 0.0, 1.0);

vec3 hue (float t)
{
    float f = 1.0 / 6.0;

    if (t < f)
    {
        return mix (Red, Yellow, t / f);
    }
    else if (t < 2.0 * f)
    {
        return mix (Yellow, Green, (t - f) / f);
    }
    else if (t < 3.0 * f)
    {
        return mix (Green, Cyan, (t - 2.0 * f) / f);
    }
    else if (t < 4.0 * f)
    {
        return mix (Cyan, Blue, (t - 3.0 * f) / f);
    }
    else if (t < 5.0 * f)
    {
        return mix (Blue, Magenta, (t - 4.0 * f) / f);
    }
    else
    {
        return mix (Magenta, Red, (t - 5.0 * f) / f);
    }
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 res = iResolution.xy;
    vec2 pos = (fragCoord - 0.5 * res) / res.y + 0.5;

    float val = 0.0;

    for (float f = 0.0; f < 10.0; f++)
    {
        val += triangle (pos, iTime + f*0.05, val, 0.01 * f / 10.0);
    }

    val = min (1.0, val);
    val = 1.0 - (1.0 - val) * (1.0 - val);

	fragColor = vec4(val * hue (0.5 + 0.5 * sin (iTime + val)), 1.0);
}

