const float PI = 3.14159265359;

//float sideCount = 1.0;  // min. number of sides

//----------------------------------------------------------------
// equal to koleidoscope, but more compact 
//----------------------------------------------------------------
void smallKoleidoscope(inout vec2 uv, float ka)
{
  float angle = abs (mod (atan (uv.x, uv.y), 2.0 * ka) - ka) + 0.01*iTime;
  uv = length(uv) * vec2(cos(angle), sin(angle));
}

//----------------------------------------------------------------
void mainImage (out vec4 fragColor, in vec2 fragCoord)
{
    vec2 p = 2.0*(fragCoord.xy - iResolution.xy*0.5) / iResolution.y;
    vec2 mp = iMouse.xy / iResolution.xy;

    float sideCount = 1.0 + round(mp.y * 11.0);

    smallKoleidoscope(p, PI / sideCount);    

    vec3 ro = rotateY(vec3(3.), iTime*0.2 + iMouse.x / iResolution.x);
    vec3 uu = normalize(cross(ro, vec3(1.0, 0.0, 0.0)));
    vec3 vv = normalize(cross(uu, ro));
    vec3 rd = normalize(p.x*uu + p.y*vv - ro*0.5);
    fragColor.rgb = log(raymarch(ro,rd) + 1.0)*0.5;
    fragColor.a = 1.0;
}

const int MAX_MARCHING_STEPS = 255;
const float MIN_DIST = 0.0;
const float MAX_DIST = 100.0;
const float EPSILON = 0.001;

float sdSphere(vec3 p, float r);


float opRepSphere( vec3 p, vec3 c )
{
    vec3 q = mod(p,c) - .5 * c;
    return sdSphere(q + vec3(0, sin(iTime + length(q)), 0), 1.0);
}

float smin( float a, float b, float k )
{
    float h = clamp( 0.5+0.5*(b-a)/k, 0.0, 1.0 );
    return mix( b, a, h ) - k*h*(1.0-h);
}

float udBox(vec3 p, vec3 b) {
  return length(max(abs(p)-b, 0.0));
}

float sdSphere(vec3 p, float r) {
    vec3 dist = vec3(sin(0.5 * p.x), sin(3. * p.y), sin(0.5 * p.z));
    return length(p) + length(dist) - r;
}

float sdPlane(vec3 p)
{
    return p.y;
}

float sceneSDF(vec3 p) {
    vec3 b = vec3(0.2, 0.2, 0.2);
    float res = sdPlane(p);
    res = smin(res, opRepSphere(p, vec3(2.0, 0.0, 2.0)), 0.9);
    //res = max(res, -opRepSphere(p, vec3(4.0, 0.0, 4.0)));
    return res;
}

float shortestDistanceToSurface(vec3 eye, vec3 marchingDirection, float start, float end) {
    float depth = start;
    for (int i = 0; i < MAX_MARCHING_STEPS; i++) {
        float dist = sceneSDF(eye + depth * marchingDirection);
        if (dist < EPSILON) {
			return depth;
        }
        depth += dist;
        if (depth >= end) {
            return end;
        }
    }
    return end;
}


vec3 rayDirection(float fieldOfView, vec2 size, vec2 fragCoord) {
    vec2 xy = fragCoord - size / 2.0;
    float z = size.y / tan(radians(fieldOfView) / 2.0);
    return normalize(vec3(xy, -z));
}

vec3 estimateNormal(vec3 p) {
    return normalize(vec3(
        sceneSDF(vec3(p.x + EPSILON, p.y, p.z)) - sceneSDF(vec3(p.x - EPSILON, p.y, p.z)),
        sceneSDF(vec3(p.x, p.y + EPSILON, p.z)) - sceneSDF(vec3(p.x, p.y - EPSILON, p.z)),
        sceneSDF(vec3(p.x, p.y, p.z  + EPSILON)) - sceneSDF(vec3(p.x, p.y, p.z - EPSILON))
    ));
}

vec3 phongContribForLight(vec3 k_d, vec3 k_s, float alpha, vec3 p, vec3 eye,
                          vec3 lightPos, vec3 lightIntensity) {
    vec3 N = estimateNormal(p);
    vec3 L = normalize(lightPos - p);
    vec3 V = normalize(eye - p);
    vec3 R = normalize(reflect(-L, N));

    float dotLN = dot(L, N);
    float dotRV = dot(R, V);

    if (dotLN < 0.0) {
        // Light not visible from this point on the surface
        return vec3(0.0, 0.0, 0.0);
    } 

    if (dotRV < 0.0) {
        // Light reflection in opposite direction as viewer, apply only diffuse
        // component
        return lightIntensity * (k_d * dotLN);
    }
    return lightIntensity * (k_d * dotLN + k_s * pow(dotRV, alpha));
}

vec3 phongIllumination(vec3 k_a, vec3 k_d, vec3 k_s, float alpha, vec3 p, vec3 eye) {
    const vec3 ambientLight = 0.5 * vec3(1.0, 1.0, 1.0);
    vec3 color = ambientLight * k_a;

    vec3 light1Pos = vec3(4.0 * sin(iTime),
                          2.0,
                          4.0 * cos(iTime));
    vec3 light1Intensity = vec3(0.4, 0.4, 0.4);

    color += phongContribForLight(k_d, k_s, alpha, p, eye,
                                  light1Pos,
                                  light1Intensity);

    vec3 light2Pos = vec3(2.0 * sin(0.37 * iTime),
                          2.0 * cos(0.37 * iTime),
                          2.0);
    vec3 light2Intensity = vec3(0.4, 0.4, 0.4);

    color += phongContribForLight(k_d, k_s, alpha, p, eye,
                                  light2Pos,
                                  light2Intensity);    
    return color;
}

mat4 viewMatrix(vec3 eye, vec3 center, vec3 up) {
    // Based on gluLookAt man page
    vec3 f = normalize(center - eye);
    vec3 s = normalize(cross(f, up));
    vec3 u = cross(s, f);
    return mat4(
        vec4(s, 0.0),
        vec4(u, 0.0),
        vec4(-f, 0.0),
        vec4(0.0, 0.0, 0.0, 1)
    );
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec3 viewDir = rayDirection(45.0, iResolution.xy, fragCoord);
    vec3 eye = vec3(10.0, 2.0, 7.0);

    mat4 viewToWorld = viewMatrix(eye, vec3(0.0, 0.0, 0.0), vec3(0.0, 1.0, 0.0));

    vec3 worldDir = (viewToWorld * vec4(viewDir, 0.0)).xyz;

	float dist = shortestDistanceToSurface(eye, worldDir, MIN_DIST, MAX_DIST);

    if (dist > MAX_DIST - EPSILON) {
        fragColor = vec4(0.0, 0.0, 0.0, 0.0);
		return;
    }

    vec3 p = eye + dist * worldDir;

    vec3 K_a = vec3(0, 0, 0);
    vec3 K_d = vec3(1, 0.2, 0.2);
    vec3 K_s = vec3(1.0, 0.0, 1.0);
    float shininess = 10.0;

    vec3 color = phongIllumination(K_a, K_d, K_s, shininess, p, eye);

    fragColor = vec4(color, 1.0);

}

