	vec3 RBZ = p + vec3(1.0, 0.0, 0.0);
	vec3 RTZ = p + vec3(1.0, 1.0, 0.0);

	vec3 LBF = p + vec3(0.0, 0.0, 1.0);
	vec3 LTF = p + vec3(0.0, 1.0, 1.0);
	vec3 RBF = p + vec3(1.0, 0.0, 1.0);
	vec3 RTF = p + vec3(1.0, 1.0, 1.0);

	float l0candidate1 = oct(LBZ);
