void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy / iResolution.xy;	
	vec2 position = (uv) * 100.0;

	float color = 0.0;

	if(uv.x < 0.5 && uv.y < 0.5){
		color = achnoise(position.x);	
	}
	else if(uv.x > 0.5 && uv.y < 0.5){
		color = achnoise(position);	
	}
	else if(uv.x < 0.5 && uv.y > 0.5){
		color = achnoise(vec3(position, iTime));	
	}
	else if(uv.x > 0.5 && uv.y > 0.5){
		color = achnoise(vec4(position, iMouse.x / iResolution.x, iTime));	
	}
	fragColor = vec4(vec3(color),1.0);
}

