const int idStr = 1, idRail = 2, idFlr = 3, idPil = 4, idBrl = 5;
const float pi = 3.14159;

#if TREE

mat3 bRot, trRot;
float trAge, trBloom, szTree, nCyc, flRad, brLen, brRad, brRadMax;
const float sScale = 0.726;
const int idBrnch = 101, idFlwr = 120;

float TreeDf (vec3 p, float dMin)
{
  vec3 q;
  float rFac, d, fr;
  p.y -= 2.;
  q = p / szTree;
  q.xz = Rot2D (q.xz, 1.3 * nCyc);
  q *= trRot;
  rFac = 1.;
  for (int j = 0; j < 8; j ++) {
    rFac *= sScale;
    brRad = max (brRadMax - 0.02 * q.y, 0.02);
    d = PrCapsDf ((q + vec3 (0., -0.5 * brLen, 0.)).xzy, brRad, 0.5 * brLen) * rFac;
    if (d < dMin) {
      dMin = SmoothMin (dMin, d, 0.1 * brRad * rFac);
      idObj = idBrnch + j;
      qHit = q;
    }
    q = vec3 (abs (q.x), q.y - brLen, q.z) * bRot;
  }
  fr = 0.8 * flRad;
  for (int j = 0; j < 4; j ++) {
    rFac *= sScale;
    d = PrCylDf (q + vec3 (brRad, brLen, 0.), fr, 0.03 * brLen) * rFac;
    if (d < dMin) {
      dMin = d;
      idObj = idFlwr + j;
      qHit = q;
    }
    q = vec3 (abs (q.x), q.y - brLen, q.z) * bRot;
    fr += 0.05 * flRad;
  }
  return dMin;
}

float ShTreeDf (vec3 p, float dMin)
{
  vec3 q;
  float rFac, fr;
  p.y -= 2.;
  q = p / szTree;
  q.xz = Rot2D (q.xz, 1.3 * nCyc);
  q *= trRot;
  rFac = 1.;
  for (int j = 0; j < 5; j ++) {
    rFac *= sScale;
    brRad = max (brRadMax - 0.02 * q.y, 0.02);
    dMin = min (dMin, PrCapsDf ((q + vec3 (0., -0.5 * brLen, 0.)).xzy, brRad, 0.5 * brLen) * rFac);
    q = vec3 (abs (q.x), q.y - brLen, q.z) * bRot;
  }
  fr = 0.8 * flRad;
  for (int j = 0; j < 2; j ++) {
    rFac *= sScale;
    dMin = min (dMin, PrCylDf (q + vec3 (brRad, brLen, 0.), fr, 0.03 * brLen) * rFac);
    q = vec3 (abs (q.x), q.y - brLen, q.z) * bRot;
    fr += 0.05 * flRad;
  }
  return dMin;
}

void SetupTree ()
{
  float t;
  nCyc = floor (tCur / 90.);
  trAge = tCur / 90. - nCyc;
  trBloom = mod (5. * trAge, 1.);
  szTree = 1.5 * (2.3 - 2.2 * smoothstep (0.93, 0.98, trAge));
  t = min (trAge, 0.8);
  brLen = min (0.95, 0.03 + 1.3 * sqrt (t));
  brRadMax = 0.01 + 0.12 * sqrt (t);
  flRad = (0.2 + 6. * t) * (0.05 + 0.8 * clamp (6. * trBloom * (1. - trBloom) - 0.1, 0., 1.));
  trRot = RMat3D (vec3 (0.2, 0.5, -0.2));
  bRot = RMat3D (vec3 (-0.5 + 0.3 * t, -1.5, 0.7 + 0.2 * t) + 0.05 * sin (0.5 * nCyc)) / sScale;
}

#endif

float ObjDf (vec3 p)
{
  vec3 q, qs;
  float dMin, d, s, aq, a, db;
  const float flGap = 4., flShift = 3.9, flRad = 9.85, flWid = 1.7, bcRadV = 0.05,
     bcRadH = 0.08, bcLen = 1.1;
  dMin = dstFar;
  p.x = -p.x;
  q = p;
  aq = (length (q.xz) > 0.) ? atan (q.z, - q.x) : 0.;
  q.xz = Rot2D (q.xz, 2. * pi * (floor (4. * aq / (2. * pi) + 0.5) / 4.));
  qs.y = q.y;
  qs.xz = Rot2D (q.xz + vec2 (11.3, 6.), -0.25 * pi);
  db = PrBox2Df (qs.yz + vec2 (-8.9, 0.), vec2 (1.4, 0.5));
  qs.xz = Rot2D (q.xz + vec2 (11.3, -6.), 0.25 * pi);
  db = min (db, PrBox2Df (qs.yz + vec2 (-1.1, 0.), vec2 (1.2, 0.5)));
  qs.y = q.y - flGap;
  qs.xz = Rot2D (vec2 (q.x + 11.3, abs (q.z) - 6.), -0.25 * pi);
  q.xz = vec2 (- q.z, q.x + 6.);
  a = (length (q.xz) > 0.) ? atan (q.z, - q.x) : 0.;
  q.xz = vec2 (28. * a / (2. * pi), length (q.xz) - 8.);
  q.y += 3.;
  q.xy = Rot2D (q.xy, 0.25 * pi);
  s = mod (q.x, 0.5 * sqrt (2.));
  d = 0.3 * max (max (max (q.y - min (s, 0.5 * sqrt (2.) - s), abs (q.z) - 1.5), -0.07 - q.y), qs.x - 0.1);
  if (d < dMin) { dMin = d;  idObj = idStr;  qHit = q; }
  q.z = abs (q.z);
  q -= vec3 (-11.3, 1.4, 1.43);
  d = max (min (PrCylDf (q.yzx, bcRadH, 5.8),
     PrCylDf (vec3 (0.5 * mod (17.8 * a + 0.5, 1.) - 0.25, q.y + 0.7, q.z).xzy, bcRadV, 0.7)), qs.x - 0.1);
  if (d < dMin) { dMin = d;  idObj = idRail; }
  q = p;  q.y = abs (qs.y + 0.05) - flShift;
  d = PrCylAnDf (q.xzy, flRad, flWid, 0.15);
  q.xz = qs.xz + vec2 (-2., 0.2);
  d = min (d, PrBoxDf (q, vec3 (2.1, 0.15, 1.7)));
  if (d < dMin) { dMin = d;  idObj = idFlr;  qHit = q; }
  q = vec3 (p.x, abs (qs.y - 2.1) - flShift, p.z);
  s = length (q.xz) - flRad;
  d = min (length (vec2 (s + flWid - 0.15, q.y)) - bcRadH,
     max (length (vec2 (s - flWid + 0.15, q.y)) - bcRadH, 2. - abs (qs.x - 2.)));
  q.y = abs (qs.y - 1.) - flShift;
  q.xz = Rot2D (p.xz, 2. * pi * (floor (128. * aq / (2. * pi) + 0.5) / 128.));
  d = min (min (d, PrCylDf (vec3 (q.x + flRad - flWid + 0.15, q.yz).xzy, bcRadV, bcLen)),
     max (PrCylDf (vec3 (q.x + flRad + flWid - 0.15, q.yz).xzy, bcRadV, bcLen), 2. - abs (qs.x - 2.)));
  d = min (d, max (min (PrCylDf (vec3 (qs.x - 2., abs (qs.y - 2.1) - flShift, qs.z - 1.45).yzx, bcRadH, 2.),
     PrCylDf (vec3 (mod (qs.x + 0.25, 0.5) - 0.25, abs (qs.y - 1.) - flShift, qs.z - 1.45).xzy, bcRadV, bcLen)),
     abs (qs.x - 2.) - 2.));
  d = min (d, max (min (PrCylDf (vec3 (qs.x, abs (qs.y - 2.1) - flShift, qs.z), bcRadH, 1.45),
     max (PrCylDf (vec3 (qs.x, abs (qs.y - 1.) - flShift,
     mod (qs.z + 0.25, 0.5) - 0.25).xzy, bcRadV, bcLen), abs (qs.z) - 1.45)), - db));
  if (d < dMin) { dMin = d;  idObj = idRail; }
  d = min (PrCapsDf (vec3 (qs.x, abs (qs.y - bcLen) - flShift, qs.z - 1.43).xzy, 0.15, 1.2),
     PrCapsDf (vec3 (qs.x, qs.y - bcLen, qs.z + 1.43).xzy, 0.15, flShift + 1.2));
  if (d < dMin) { dMin = d;  idObj = idPil;  qHit = q; }
  d = PrCylDf ((p + vec3 (0., -1., 0.)).xzy, 1.5, 1.);
  if (d < dMin) { dMin = d;  idObj = idBrl; }
#if TREE
  dMin = isShad ? ShTreeDf (p, dMin) : TreeDf (p, dMin);
#endif
  return dMin;
}

float ObjRay (vec3 ro, vec3 rd)
{
  float dHit, d;
  dHit = 0.;
  for (int j = 0; j < 200; j ++) {
    d = ObjDf (ro + dHit * rd);
    dHit += d;
    if (d < 0.0005 || dHit > dstFar) break;
  }
  return dHit;
}

vec3 ObjNf (vec3 p)
{
  vec4 v;
  vec3 e = vec3 (0.001, -0.001, 0.);
  v = vec4 (ObjDf (p + e.xxx), ObjDf (p + e.xyy), ObjDf (p + e.yxy), ObjDf (p + e.yyx));
  return normalize (vec3 (v.x - v.y - v.z - v.w) + 2. * v.yzw);
}

float ObjSShadow (vec3 ro, vec3 rd)
{
  float sh, d, h;
  isShad = true;
  sh = 1.;
  d = 0.05;
  for (int j = 0; j < 32; j ++) {
    h = ObjDf (ro + rd * d);
    sh = min (sh, smoothstep (0., 0.05 * d, h));
    d += max (0.01, h);
    if (sh < 0.05) break;
  }
  isShad = false;
  return sh;
}

vec3 BgCol (vec3 ro, vec3 rd)
{
  vec3 col, vn;
  float f;
  if (rd.y > 0.) {
    ro.xz += 2. * tCur;
    col = vec3 (0.2, 0.3, 0.6) + 0.2 * (1. - max (rd.y, 0.)) +
       0.1 * pow (max (dot (rd, sunDir), 0.), 16.);
    f = Fbm2 (0.02 * (ro.xz + rd.xz * (100. - ro.y) / rd.y));
    col = mix (col, vec3 (1.), 0.2 + 0.8 * f * rd.y);
  } else {
    col = mix (0.6 * mix (vec3 (0.3, 0.4, 0.1), vec3 (0.4, 0.5, 0.2), Fbm2 (ro.xz)) *
       (1. - 0.2 * Noisefv2 (32. * ro.xz)), vec3 (0.18, 0.28, 0.48), pow (1. + rd.y, 5.));
    f = 1. - smoothstep (0.1, 1., length (ro.xz) / dstFar);
    vn = VaryNf (ro, vec3 (0., 1., 0.), 2. * f);
    col = col * (0.1 + 0.1 * max (vn.y, 0.) + 0.8 * max (dot (vn, sunDir), 0.));
  }
  return col;
}

#if ! TREE

float BallHit (vec3 ro, vec3 rd)
{
  vec3 p, u;
  float b, d, w, dMin, rad;
  dMin = dstFar;
  rad = 3.;
  p = vec3 (0., 2. + rad, 0.);
  u = ro - p;
  b = dot (rd, u);
  w = b * b - dot (u, u) + rad * rad;
  if (w > 0.) {
    d = - b - sqrt (w);
    if (d > 0. && d < dMin) {
      dMin = d;
      vnBall = (u + d * rd) / rad;
    }
  }
  return dMin;
}

#endif

vec3 ShowScene (vec3 ro, vec3 rd)
{
  vec4 vc;
  vec3 vn, col;
  float dstObj, dstBall, r, s, sh;
  bool isBg;
#if TREE
  SetupTree ();
#endif
  HexVorInit ();
  isShad = false;
  isBg = true;
  sh = 1.;
  dstObj = ObjRay (ro, rd);
#if ! TREE
  dstBall = BallHit (ro, rd);
  if (dstBall < min (dstObj, dstFar)) {
    ro += dstBall * rd;
    rd = reflect (rd, vnBall);
    ro += 0.01 * rd;
    dstObj = ObjRay (ro, rd);
  }
#endif
  if (dstObj < dstFar) {
    isBg = false;
    ro += rd * dstObj;
    vn = ObjNf (ro);
    r = length (ro.xz);
    if (idObj == idFlr) {
      col = vec3 (1., 0.8, 0.6);
      if (abs (vn.y) > 0.99) {
        if (r < 11.6) {
          s = mod (2.42 * r, 2.);
          col *= (0.8 + 0.2 * SmoothBump (0.1, 0.9, 0.03, mod (s, 1.))) *
             (0.8 + 0.2 * SmoothBump (0.05, 0.95, 0.02,
             mod (64. * atan (ro.z, - ro.x) / (2. * pi) + 0.5 * step (s, 1.), 1.)));
        } else {
          col *= 0.8 + 0.2 * SmoothBump (0.1, 0.9, 0.03, mod (2. * qHit.x, 1.));
        }
      } else if (abs (vn.y) < 0.01) col *= 0.8 +
         0.2 * SmoothBump (0.2, 0.8, 0.03, mod (12. * ro.y, 1.));
      vn = VaryNf (100. * ro, vn, 0.2); 
    } else if (idObj == idStr) {
      col = vec3 (0.9, 0.7, 0.5);
    } else if (idObj == idRail) {
      col = vec3 (0.8, 0.8, 0.9);
    } else if (idObj == idPil) {
      col = vec3 (0.7, 0.7, 0.8);
    } else if (idObj == idBrl) {
      if (vn.y < 0.99 || r > 1.4) {
        col = vec3 (0.7, 0.5, 0.2) * (0.5 + 0.5 * SmoothBump (0.05, 0.95, 0.02,
           mod (32. * ((r > 0.) ? atan (ro.z, - ro.x) / (2. * pi) : 0.), 1.))) *
           (0.5 + 0.5 * SmoothBump (0.05, 0.95, 0.02, mod (4. * ro.y + 0.5, 1.)));
        vn = VaryNf (20. * ro, vn, 1.);   
      } else col = vec3 (0.2, 0.05, 0.) * (0.5 + 0.5 * Fbm2 (64. * ro.xz));
#if TREE
    } else if (idObj >= idBrnch && idObj < idFlwr + 4) {
      if (idObj < idFlwr) {
        col = 1.5 * mix (vec3 (0.3, 0.7, 0.3), vec3 (0.5, 0.3, 0.1),
           smoothstep (0.02, 0.05, trAge));
        col *= (0.7 + 0.3 * clamp (0.7 + 0.6 * cos (11. * qHit.y), 0., 1.));
        vn.xz = Rot2D (vn.xz, 0.5 * sin (pi * mod (20. * (atan (qHit.z, qHit.x) / (2. * pi) + 0.5), 1.)));
        vn = VaryNf (50. * ro, vn, 2. * smoothstep (0.03, 0.08, trAge));
      } else {
        col = HsvToRgb (vec3 (0.35 * max (0.05 * float (idObj - idFlwr) +
           1. - 1.2 * trBloom, 0.),
           0.05 + 0.95 * smoothstep (0.15, 0.2, trBloom), 1.)); 
        if (idObj == idFlwr + 3) col = mix (vec3 (1., 0., 0.), col,
           smoothstep (0.05, 0.2, trBloom));
        col = mix (col, vec3 (0.5, 0.3, 0.1), smoothstep (0.9, 1., trBloom));
      }
      col = mix (col, vec3 (0.8), smoothstep (0.93, 0.98, trAge));
#endif
    }
#if SHAD
#if TREE
    if (idObj < idBrnch)
#endif
    sh = 0.6 + 0.4 * ObjSShadow (ro, sunDir);
#endif
  } else if (rd.y < 0.) {
    ro += (- ro.y / rd.y) * rd;
#if SHAD
    sh = 0.7 + 0.3 * ObjSShadow (ro, sunDir);
#endif
    r = length (ro.xz);
    if (r < 8.1) {
      isBg = false;
      vc = HexVor (8. * ro.xz);
      vn.xz = - 0.9 * vc.yz;
      vn.y = 1.;
      vn = normalize (vn);
      s = mod (16. * vc.w, 1.);
      col = HsvToRgb (vec3 (0.1 + 0.4 * step (2. * s, 1.) + 0.1 * mod (5. * s, 1.),
         0.4 + 0.2 * mod (17. * s, 1.), 0.3 + 0.3 * mod (8. * s, 1.))) *
         (0.6 + 0.4 * smoothstep (0., 0.2, vc.x)) * (0.7 + 0.3 * smoothstep (1.5, 2.5, r));
    }
  }
  if (! isBg) col = col * (0.3 + 0.1 * max (dot (- sunDir.xz, vn.xz), 0.) +
     0.7 * sh * max (dot (sunDir, vn), 0.)) +
     0.1 * sh * pow (max (dot (normalize (sunDir - rd), vn), 0.), 64.);
  else col = sh * BgCol (ro, rd);
  return clamp (col, 0., 1.);
}

void mainImage (out vec4 fragColor, in vec2 fragCoord)
{
  mat3 vuMat;
  vec4 mPtr;
  vec3 ro, rd;
  vec2 canvas, uv, ori, ca, sa;
  float el, az, zmFac;
  canvas = iResolution.xy;
  uv = 2. * fragCoord.xy / canvas - 1.;
  uv.x *= canvas.x / canvas.y;
  tCur = iTime;
  mPtr = iMouse;
  mPtr.xy = mPtr.xy / canvas - 0.5;
  dstFar = 120.;
  az = 0.;
  el = -0.2 * pi;
  if (mPtr.z > 0.) {
    az += 3. * pi * mPtr.x;
    el += 1. * pi * mPtr.y;
  } else {
    az -= 2.5 * pi * sin (0.003 * pi * tCur);
    el += 0.1 * pi * cos (0.03 * pi * tCur);
  }
  el = clamp (el, -0.4 * pi, -0.1 * pi);
  ori = vec2 (el, az);
  ca = cos (ori);
  sa = sin (ori);
  vuMat = mat3 (ca.y, 0., - sa.y, 0., 1., 0., sa.y, 0., ca.y) *
          mat3 (1., 0., 0., 0., ca.x, - sa.x, 0., sa.x, ca.x);
  zmFac = 7. - 2. * cos (az);
  rd = vuMat * normalize (vec3 (uv, zmFac));
  ro = vuMat * vec3 (0., 5., -70.);
  sunDir = vuMat * normalize (vec3 (1., 3., -1.));
  fragColor = vec4 (ShowScene (ro, rd), 1.);
}

float PrBoxDf (vec3 p, vec3 b)
{
  vec3 d;
  d = abs (p) - b;
  return min (max (d.x, max (d.y, d.z)), 0.) + length (max (d, 0.));
}

float PrBox2Df (vec2 p, vec2 b)
{
  vec2 d;
  d = abs (p) - b;
  return min (max (d.x, d.y), 0.) + length (max (d, 0.));
}

float PrCylDf (vec3 p, float r, float h)
{
  return max (length (p.xy) - r, abs (p.z) - h);
}

float PrCapsDf (vec3 p, float r, float h)
{
  return length (p - vec3 (0., 0., clamp (p.z, - h, h))) - r;
}

float PrCylAnDf (vec3 p, float r, float w, float h)
{
  return max (abs (length (p.xy) - r) - w, abs (p.z) - h);
}

vec2 gVec[7], hVec[7];
#define SQRT3 1.7320508

vec2 PixToHex (vec2 p)
{
  vec3 c, r, dr;
  c.xz = vec2 ((1./SQRT3) * p.x - (1./3.) * p.y, (2./3.) * p.y);
  c.y = - c.x - c.z;
  r = floor (c + 0.5);
  dr = abs (r - c);
  r -= step (dr.yzx, dr) * step (dr.zxy, dr) * dot (r, vec3 (1.));
  return r.xz;
}

vec2 HexToPix (vec2 h)
{
  return vec2 (SQRT3 * (h.x + 0.5 * h.y), (3./2.) * h.y);
}

void HexVorInit ()
{
  vec3 e = vec3 (1., 0., -1.);
  gVec[0] = e.yy;
  gVec[1] = e.xy;
  gVec[2] = e.yx;
  gVec[3] = e.xz;
  gVec[4] = e.zy;
  gVec[5] = e.yz;
  gVec[6] = e.zx;
  for (int k = 0; k < 7; k ++) hVec[k] = HexToPix (gVec[k]);
}

vec4 HexVor (vec2 p)
{
  vec4 sd, udm;
  vec2 ip, fp, d, u;
  float amp, a;
  amp = 0.7;
  ip = PixToHex (p);
  fp = p - HexToPix (ip);
  sd = vec4 (4.);
  udm = vec4 (4.);
  for (int k = 0; k < 7; k ++) {
    u = Hashv2v2 (ip + gVec[k]);
    a = 2. * pi * (u.y - 0.5);
    d = hVec[k] + amp * (0.4 + 0.6 * u.x) * vec2 (cos (a), sin (a)) - fp;
    sd.w = dot (d, d);
    if (sd.w < sd.x) {
      sd = sd.wxyw;
      udm = vec4 (d, u);
    } else sd = (sd.w < sd.y) ? sd.xwyw : ((sd.w < sd.z) ? sd.xyww : sd);
  }
  sd.xyz = sqrt (sd.xyz);
  return vec4 (SmoothMin (sd.y, sd.z, 0.3) - sd.x, udm.xy, Hashfv2 (udm.zw));
}

float SmoothMin (float a, float b, float r)
{
  float h;
  h = clamp (0.5 + 0.5 * (b - a) / r, 0., 1.);
  return mix (b, a, h) - r * h * (1. - h);
}

float SmoothBump (float lo, float hi, float w, float x)
{
  return (1. - smoothstep (hi - w, hi + w, x)) * smoothstep (lo - w, lo + w, x);
}

vec2 Rot2D (vec2 q, float a)
{
  return q * cos (a) + q.yx * sin (a) * vec2 (-1., 1.);
}

mat3 RMat3D (vec3 a)
{
  vec3 cr, sr;
  cr = cos (a);
  sr = sin (a);
  return mat3 (cr.z, - sr.z, 0., sr.z, cr.z, 0., 0., 0., 1.) *
         mat3 (1., 0., 0., 0., cr.x, - sr.x, 0., sr.x, cr.x) *
         mat3 (cr.y, 0., - sr.y, 0., 1., 0., sr.y, 0., cr.y);
}

vec3 HsvToRgb (vec3 c)
{
  vec3 p;
  p = abs (fract (c.xxx + vec3 (1., 2./3., 1./3.)) * 6. - 3.);
  return c.z * mix (vec3 (1.), clamp (p - 1., 0., 1.), c.y);
}

const float cHashM = 43758.54;

float Hashfv2 (vec2 p)
{
  return fract (sin (dot (p, vec2 (37., 39.))) * cHashM);
}

vec2 Hashv2v2 (vec2 p)
{
  vec2 cHashVA2 = vec2 (37., 39.);
  return fract (sin (vec2 (dot (p, cHashVA2), dot (p + vec2 (1., 0.), cHashVA2))) * cHashM);
}

float Noisefv2 (vec2 p)
{
  vec2 t, ip, fp;
  ip = floor (p);  
  fp = fract (p);
  fp = fp * fp * (3. - 2. * fp);
  t = mix (Hashv2v2 (ip), Hashv2v2 (ip + vec2 (0., 1.)), fp.y);
  return mix (t.x, t.y, fp.x);
}

float Fbm2 (vec2 p)
{
  float f, a;
  f = 0.;
  a = 1.;
  for (int i = 0; i < 5; i ++) {
    f += a * Noisefv2 (p);
    a *= 0.5;
    p *= 2.;
  }
  return f * (1. / 1.9375);
}

float Fbmn (vec3 p, vec3 n)
{
  vec3 s;
  float a;
  s = vec3 (0.);  
  a = 1.;
  for (int i = 0; i < 5; i ++) {
    s += a * vec3 (Noisefv2 (p.yz), Noisefv2 (p.zx), Noisefv2 (p.xy));
    a *= 0.5;  
    p *= 2.;
  }
  return dot (s, abs (n));
}

vec3 VaryNf (vec3 p, vec3 n, float f)
{
  vec3 g;
  vec2 e = vec2 (0.1, 0.);
  g = vec3 (Fbmn (p + e.xyy, n), Fbmn (p + e.yxy, n), Fbmn (p + e.yyx, n)) - Fbmn (p, n);
  return normalize (n + f * (g - n * dot (n, g)));
}

//	Simplex 3D Noise 
//	by Ian McEwan, Ashima Arts
//
vec4 permute(vec4 x){return mod(((x*34.0)+1.0)*x, 289.0);}
vec4 taylorInvSqrt(vec4 r){return 1.79284291400159 - 0.85373472095314 * r;}

float snoise(vec3 v){ 
  const vec2  C = vec2(1.0/6.0, 1.0/3.0) ;
  const vec4  D = vec4(0.0, 0.5, 1.0, 2.0);

// First corner
  vec3 i  = floor(v + dot(v, C.yyy) );
  vec3 x0 =   v - i + dot(i, C.xxx) ;

// Other corners
  vec3 g = step(x0.yzx, x0.xyz);
  vec3 l = 1.0 - g;
  vec3 i1 = min( g.xyz, l.zxy );
  vec3 i2 = max( g.xyz, l.zxy );

  //  x0 = x0 - 0. + 0.0 * C 
  vec3 x1 = x0 - i1 + 1.0 * C.xxx;
  vec3 x2 = x0 - i2 + 2.0 * C.xxx;
  vec3 x3 = x0 - 1. + 3.0 * C.xxx;

// Permutations
  i = mod(i, 289.0 ); 
  vec4 p = permute( permute( permute( 
             i.z + vec4(0.0, i1.z, i2.z, 1.0 ))
           + i.y + vec4(0.0, i1.y, i2.y, 1.0 )) 
           + i.x + vec4(0.0, i1.x, i2.x, 1.0 ));

// Gradients
// ( N*N points uniformly over a square, mapped onto an octahedron.)
  float n_ = 1.0/7.0; // N=7
  vec3  ns = n_ * D.wyz - D.xzx;

  vec4 j = p - 49.0 * floor(p * ns.z *ns.z);  //  mod(p,N*N)

  vec4 x_ = floor(j * ns.z);
  vec4 y_ = floor(j - 7.0 * x_ );    // mod(j,N)

  vec4 x = x_ *ns.x + ns.yyyy;
  vec4 y = y_ *ns.x + ns.yyyy;
  vec4 h = 1.0 - abs(x) - abs(y);

  vec4 b0 = vec4( x.xy, y.xy );
  vec4 b1 = vec4( x.zw, y.zw );

  vec4 s0 = floor(b0)*2.0 + 1.0;
  vec4 s1 = floor(b1)*2.0 + 1.0;
  vec4 sh = -step(h, vec4(0.0));

  vec4 a0 = b0.xzyw + s0.xzyw*sh.xxyy ;
  vec4 a1 = b1.xzyw + s1.xzyw*sh.zzww ;

  vec3 p0 = vec3(a0.xy,h.x);
  vec3 p1 = vec3(a0.zw,h.y);
  vec3 p2 = vec3(a1.xy,h.z);
  vec3 p3 = vec3(a1.zw,h.w);

//Normalise gradients
  vec4 norm = taylorInvSqrt(vec4(dot(p0,p0), dot(p1,p1), dot(p2, p2), dot(p3,p3)));
  p0 *= norm.x;
  p1 *= norm.y;
  p2 *= norm.z;
  p3 *= norm.w;

// Mix final noise value
  vec4 m = max(0.6 - vec4(dot(x0,x0), dot(x1,x1), dot(x2,x2), dot(x3,x3)), 0.0);
  m = m * m;
  return 42.0 * dot( m*m, vec4( dot(p0,x0), dot(p1,x1), 
                                dot(p2,x2), dot(p3,x3) ) );
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy / iResolution.xy;
    float scale = .1;
    float rate = 5.0;

    float t = iTime/rate;

    float result = 0.0;

    //octaves
    for (float i = 0.0; i < 5.0; i++){
    	result += snoise(vec3((uv.x*2.0)/scale, (uv.y - t)/scale, t*5.0))/pow(2.0, i);
        scale /= 2.0;
    }
    result = (result + 2.0)/4.0;

    //powers for steeper curves
    float p1 = pow(fragCoord.y/iResolution.y, 1.7);
    float p2 = 8.0*(1.0 - p1);
    result = pow(result, 8.0 - p2);

    //power for coloring
    float g = pow(result, 6.0);
    fragColor = vec4(result, g, 0, 1.0);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 q = fragCoord.xy / iResolution.xy;
    vec2 p = (-1.0+2.0*q)*vec2(iResolution.x/iResolution.y,1.0);
    vec2 uv = p;

   	float d = cos(p.y-.7)+cos(p.x+.7);;
   	uv=vec2(p*20.0)/(d);                         

    uv-=iTime;

    vec3 col = texture(iChannel0, uv+iTime*5.0 ).rgb;

    col*=d*.9;
      float dist = 
        exp(3.0-15.0*length( p-vec2(0.75+0.01*sin(iTime),0.7))); 
	fragColor = vec4(col+dist, 1.0);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy / iResolution.xy;

    float t = uv.x;

  	float m1 = -0.5 + 1.5*sin(1.1*iTime);
    float m2 = 0.2 + 2.2*cos(1.7*iTime);

    float a = m1 + m2;
    float b = -2.0*m1 - m2;
    float c = m1;

    float v = a*t*t*t + b*t*t + c*t;

    float sy = (uv.y - 0.5);

    float err = 0.005;
    if(sy >= (v - err) && sy <= (v + err))
    {
        fragColor = vec4(1.0, 1.0, 1.0, 1.0);
    }
    else if(sy >= (t*m1 - err) && sy <= (t*m1 + err))
    {
     	fragColor = vec4(0.0, 1.0, 0.0, 1.0);   
    }
    else if(-sy >= (1.0-t)*m2 - err && -sy <= (1.0-t)*m2 + err)
    {
        fragColor = vec4(1.0, 0.0, 1.0, 1.0);   
    }
    else
    {
     	fragColor = vec4(0.0, 0.0, 0.0, 1.0);   
    }
}

