float PrCapsDf (vec3 p, float r, float h);
vec2 PixToHex (vec2 p);
vec2 HexToPix (vec2 h);
float SmoothMin (float a, float b, float r);
float SmoothBump (float lo, float hi, float w, float x);
float Hashfv2 (vec2 p);
vec2 Hashv2v2 (vec2 p);
float Fbm2 (vec2 p);
vec3 VaryNf (vec3 p, vec3 n, float f);
vec3 HsvToRgb (vec3 c);

vec3 sunDir, qHit;
vec2 gVec[7], hVec[7];
float tCur, dstFar;
const float pi = 3.14159;

void HexVorInit ()
{
  vec3 e = vec3 (1., 0., -1.);
  gVec[0] = e.yy;
  gVec[1] = e.xy;
  gVec[2] = e.yx;
  gVec[3] = e.xz;
  gVec[4] = e.zy;
  gVec[5] = e.yz;
  gVec[6] = e.zx;
  for (int k = 0; k < 7; k ++) hVec[k] = HexToPix (gVec[k]);
}

vec4 TwHexVor (vec3 p)
{
  vec4 sd, udm;
  vec2 ip, fp, d, u;
  float amp, a, twf, twa;
  amp = 0.7;
  p.xz *= 1.1;
  ip = PixToHex (p.xz);
  fp = p.xz - HexToPix (ip);
  sd = vec4 (4.);
  udm = vec4 (4.);
  for (int k = 0; k < 7; k ++) {
    u = Hashv2v2 (ip + gVec[k]);
    twf = 4. * mod (32. * u.x, 1.) - 2.;
    twa = 1. - 0.25 * abs (twf);
    a = 2. * pi * (u.y - 0.5);
    a += twa * sin (a + twf * p.y);
    d = hVec[k] + amp * (0.4 + 0.6 * u.x) * vec2 (cos (a), sin (a)) - fp;
    sd.w = dot (d, d);
    if (sd.w < sd.x) {
      sd = sd.wxyw;
      udm = vec4 (d, u);
    } else sd = (sd.w < sd.y) ? sd.xwyw : ((sd.w < sd.z) ? sd.xyww : sd);
  }
  sd.xyz = sqrt (sd.xyz);
  return vec4 (SmoothMin (sd.y, sd.z, 0.3) - sd.x, udm.xy, Hashfv2 (udm.zw));
}

float ObjDf (vec3 p)
{
  vec4 vc;
  vec3 q;
  float d, h, r;
  vc = TwHexVor (p);
  h = 2. + vc.w;
  q.xz = vc.yz;
  q.y = p.y - h;
  r = mix (0.02 + 0.12 * (2. - p.y / h), 0.7 * vc.x, 1. - smoothstep (0.1 * h, 0.4 * h, p.y));
  r *= 1. + 0.1 * sin (4. * pi * p.y);
  q.xz = abs (q.xz) - 0.5 * r;
  d = 0.35 * max (PrCapsDf (q.xzy, r, h), - p.y);
  qHit = q;
  return d;
}

float ObjRay (vec3 ro, vec3 rd)
{
  float dHit, d;
  dHit = 0.;
  for (int j = 0; j < 250; j ++) {
    d = ObjDf (ro + dHit * rd);
    dHit += d;
    if (d < 0.0005 || dHit > dstFar) break;
  }
   return dHit;
}

vec3 ObjNf (vec3 p)
{
  vec4 v;
  const vec3 e = vec3 (0.001, -0.001, 0.);
  v = vec4 (ObjDf (p + e.xxx), ObjDf (p + e.xyy), ObjDf (p + e.yxy), ObjDf (p + e.yyx));
  return normalize (vec3 (v.x - v.y - v.z - v.w) + 2. * v.yzw);
}

float ObjSShadow (vec3 ro, vec3 rd)
{
  float sh, d, h;
  sh = 1.;
  d = 0.05;
  for (int j = 0; j < 32; j ++) {
    h = ObjDf (ro + rd * d);
    sh = min (sh, smoothstep (0., 0.05 * d, h));
    d += clamp (h, 0.05, 0.5);
    if (sh < 0.05) break;
  }
  return sh;
}

vec3 SkyCol (vec3 ro, vec3 rd)
{
  vec3 col;
  float f;
  ro.x += 0.5 * tCur;
  f = Fbm2 (0.1 * (rd.xz * (50. - ro.y) / rd.y + ro.xz));
  col = vec3 (0.2, 0.3, 0.55) + 0.1 * pow (1. - max (rd.y, 0.), 4.) +
     0.35 * pow (max (dot (rd, sunDir), 0.), 8.);
  return mix (col, vec3 (0.85), clamp (f * rd.y + 0.1, 0., 1.));
}

vec3 ShowScene (vec3 ro, vec3 rd)
{
  vec4 vc;
  vec3 col, vn;
  float dstObj, sh, a, spec;
  bool isBg;
  HexVorInit ();
  dstObj = ObjRay (ro, rd);
  isBg = true;
  if (dstObj < dstFar) {
    isBg = false;
    ro += dstObj * rd;
    vn = ObjNf (ro);
    vn = VaryNf (32. * ro, vn, 5. - 4.5 * smoothstep (0.5, 2., ro.y));
    a = atan (qHit.z, - qHit.x) / pi;
    vc = TwHexVor (ro);
    col = HsvToRgb (vec3 (0.15 + 0.3 * mod (37. * vc.w, 1.), 1.,
       0.8 + 0.2 * SmoothBump (0.1, 0.9, 0.05, mod (6. * a, 1.))));
    col = mix (col, vec3 (0.4, 0.2, 0.1), 1. - 0.9 * smoothstep (0., 1., ro.y));
    col *= (1. - 0.3 * Fbm2 (64. * vec2 (a, 4. * qHit.y))) * (0.5 + 0.5 * smoothstep (0., 0.5, ro.y));
    spec = 0.3 * smoothstep (2., 4., ro.y);
  } else if (rd.y < 0.) {
    isBg = false;
    ro += (- ro.y / rd.y) * rd;
    vn = VaryNf (32. * ro, vec3 (0., 1., 0.), 10.);
    col = mix (vec3 (0.2, 0.05, 0.), vec3 (0.05, 0.2, 0.), Fbm2 (16. * ro.xz));
    spec = 0.;
  }
  if (! isBg) {
    sh = 0.3 + 0.7 * ObjSShadow (ro, sunDir);
    col = col * (0.2 + 0.8 * sh * max (0., max (dot (vn, sunDir), 0.))) +
       0.1 * max (dot (- normalize (sunDir.xz), normalize (vn.xz)), 0.) +
       spec * sh * pow (max (dot (normalize (sunDir - rd), vn), 0.), 64.);
  } else col = SkyCol (ro, rd);
  return pow (clamp (col, 0., 1.), vec3 (0.8));
}

void mainImage (out vec4 fragColor, in vec2 fragCoord)
{
  mat3 vuMat;
  vec4 mPtr;
  vec3 ro, rd;
  vec2 canvas, uv, ori, ca, sa;
  float az, el;
  canvas = iResolution.xy;
  uv = 2. * fragCoord.xy / canvas - 1.;
  uv.x *= canvas.x / canvas.y;
  tCur = iTime;
  mPtr = iMouse;
  mPtr.xy = mPtr.xy / canvas - 0.5;
  az = 0.;
  el = -0.1 * pi;
  rd = normalize (vec3 (uv, 3.));
  if (mPtr.z > 0.) {
    az += 1.5 * pi * mPtr.x;
    el += 0.5 * pi * mPtr.y;
  } else {
    az += 0.3 * pi * sin (0.05 * pi * tCur);
  }
  el = clamp (el, -0.4 * pi, -0.05 * pi);
  ori = vec2 (el, az);
  ca = cos (ori);
  sa = sin (ori);
  vuMat = mat3 (ca.y, 0., - sa.y, 0., 1., 0., sa.y, 0., ca.y) *
          mat3 (1., 0., 0., 0., ca.x, - sa.x, 0., sa.x, ca.x);
  rd = vuMat * rd;
  ro = vec3 (0., 5., 0.5 * tCur);
  sunDir = normalize (vec3 (0.5, 1., -1.));
  dstFar = 120.;
  fragColor = vec4 (ShowScene (ro, rd), 1.);
}

float PrCapsDf (vec3 p, float r, float h)
{
  return length (p - vec3 (0., 0., clamp (p.z, - h, h))) - r;
}

#define SQRT3 1.7320508

vec2 PixToHex (vec2 p)
{
  vec3 c, r, dr;
  c.xz = vec2 ((1./SQRT3) * p.x - (1./3.) * p.y, (2./3.) * p.y);
  c.y = - c.x - c.z;
  r = floor (c + 0.5);
  dr = abs (r - c);
  r -= step (dr.yzx, dr) * step (dr.zxy, dr) * dot (r, vec3 (1.));
  return r.xz;
}

vec2 HexToPix (vec2 h)
{
  return vec2 (SQRT3 * (h.x + 0.5 * h.y), (3./2.) * h.y);
}

float SmoothMin (float a, float b, float r)
{
  float h;
  h = clamp (0.5 + 0.5 * (b - a) / r, 0., 1.);
  return mix (b, a, h) - r * h * (1. - h);
}

float SmoothBump (float lo, float hi, float w, float x)
{
  return (1. - smoothstep (hi - w, hi + w, x)) * smoothstep (lo - w, lo + w, x);
}

const float cHashM = 43758.54;

float Hashfv2 (vec2 p)
{
  return fract (sin (dot (p, vec2 (37., 39.))) * cHashM);
}

vec2 Hashv2v2 (vec2 p)
{
  const vec2 cHashVA2 = vec2 (37.1, 61.7);
  const vec2 e = vec2 (1., 0.);
  return fract (sin (vec2 (dot (p + e.yy, cHashVA2), dot (p + e.xy, cHashVA2))) * cHashM);
}

float Noisefv2 (vec2 p)
{
  vec2 t, ip, fp;
  ip = floor (p);  
  fp = fract (p);
  fp = fp * fp * (3. - 2. * fp);
  t = mix (Hashv2v2 (ip), Hashv2v2 (ip + vec2 (0., 1.)), fp.y);
  return mix (t.x, t.y, fp.x);
}

float Fbm2 (vec2 p)
{
  float f, a;
  f = 0.;
  a = 1.;
  for (int i = 0; i < 5; i ++) {
    f += a * Noisefv2 (p);
    a *= 0.5;
    p *= 2.;
  }
  return f * (1. / 1.9375);
}

float Fbmn (vec3 p, vec3 n)
{
  vec3 s;
  float a;
  s = vec3 (0.);  
  a = 1.;
  for (int i = 0; i < 5; i ++) {
    s += a * vec3 (Noisefv2 (p.yz), Noisefv2 (p.zx), Noisefv2 (p.xy));
    a *= 0.5;  
    p *= 2.;
  }
  return dot (s, abs (n));
}

vec3 VaryNf (vec3 p, vec3 n, float f)
{
  vec3 g;
  vec2 e = vec2 (0.1, 0.);
  g = vec3 (Fbmn (p + e.xyy, n), Fbmn (p + e.yxy, n), Fbmn (p + e.yyx, n)) - Fbmn (p, n);
  return normalize (n + f * (g - n * dot (n, g)));
}

vec3 HsvToRgb (vec3 c)
{
  vec3 p;
  p = abs (fract (c.xxx + vec3 (1., 2./3., 1./3.)) * 6. - 3.);
  return c.z * mix (vec3 (1.), clamp (p - 1., 0., 1.), c.y);
}

// Constants ----------
#define PI 3.14159265358979
#define P2 6.28318530717959

const int   MAX_TRACE_STEP = 60;
const float MAX_TRACE_DIST = 180.;
const float TRACE_PRECISION = .001;
const float FUDGE_FACTOR = .82;
const vec3  GAMMA = vec3(1.3/2.2);

const float GI_LENGTH = .8;
const float GI_STRENGTH = .4;
const float AO_STRENGTH = .4;
const int   MAX_SHADOW_TRACE_STEP = 110;
const float MAX_SHADOW_TRACE_DIST = 10.;
const float MIN_SHADOW_MARCHING = .2;
const float SHADOW_SHARPNESS = 4.;

const float LENS_BLUR = .3;


// Structures ----------
struct Surface {
  float d;
  vec4 kd;  // should be "kd.w=1" for ao shadow strength
};
Surface _min(Surface s,Surface t) {if(s.d<t.d)return s;return t;}

struct Ray {
  vec3 org, dir;
  float len, stp;
};
vec3 _pos(Ray r) {return r.org+r.dir*r.len;}

struct Hit {
  vec3 pos;
  Ray ray;
  Surface srf;
};

struct Camera {
  vec3 pos, tgt;
  float rol, fcs;
};
mat3 _mat3(Camera c) {
  vec3 w = normalize(c.pos-c.tgt);
  vec3 u = normalize(cross(w,vec3(sin(c.rol),cos(c.rol),0)));
  return mat3(u,normalize(cross(u,w)),w);
}

struct AmbientLight {
  vec3 dir, col;
};
vec3 _lit(vec3 n, AmbientLight l){return clamp((dot(n, l.dir)+1.)*.5,0.,1.)*l.col;}


// Grobal valiables ----------
const float bpm = 144.;
const AmbientLight amb = AmbientLight(vec3(0,1,0), vec3(.7,.7,.7));
const AmbientLight dif = AmbientLight(normalize(vec3(1,2,1)), vec3(.7,.7,.7));
float phase;


// Utilities ----------
vec3 _rgb(vec3 v) {
  return ((clamp(abs(fract(v.x+vec3(0,2./3.,1./3.))*2.-1.)*3.-1.,0.,1.)-1.)*v.y+1.)*v.z;
}

mat3 _sMat(float th, float ph) {
  float x=cos(th), y=cos(ph), z=sin(th), w=sin(ph);
  return mat3(y,w*z,-w*x,0,x,z,w,-y*z,y*x);
}


// Distance Functions ----------
float fPlane(vec3 p, vec3 n, float d) {
  return dot(p,n) + d;
}

float fBox(vec3 p, vec3 b, float r) {
  return length(max(abs(p)-b,0.)) - r;
}

vec3 repXZ(vec3 p, vec2 r){
  vec2 hr = r * .5;
  return vec3(mod(p.x+hr.x, r.x)-hr.x, p.y, mod(p.z+hr.y, r.y)-hr.y);
}

float fHex(vec3 p, vec2 h, float r) {
  vec3 q = abs(p);
  q.x = max(q.x*0.866025+q.z*0.5,q.z);
  return length(max(q.xy-h.xy,0.)) - r;
}

// World Mapping ----------
Surface map(vec3 p){
  float lv = min(1., iTime / 16.);
  float es = exp(sin(phase*.5-length(p)*.2));
  vec2  bs = vec2(.6/es,.6*es);
  vec3  hx = vec3(1.73205081,1,0)*4.;
  vec3  rp =  repXZ(p, hx.xy);
  vec3  rp2 = repXZ(p+hx.xzy*.5, hx.xy);
  vec3  col = _rgb(vec3(phase/256.,1.4,1.4));
  rp.y -= bs.y+.4-length(p)*.01;
  rp2.y -= bs.y+.4-length(p)*.01;

  return _min(
    Surface(fPlane(p, vec3(0,1,0), lv*3.8-3.8), vec4(col,1)), 
    _min(
      Surface(fHex(rp,  bs, .4), vec4(.7,.7,.7,1)),
      Surface(fHex(rp2, bs, .4), vec4(.3,.3,.3,1))
    )
  );
}


// Lighting ----------
vec3 calcNormal(in vec3 p){
  vec3 v=vec3(.001,0,map(p).d);
  return normalize(vec3(map(p+v.xyy).d-v.z,map(p+v.yxy).d-v.z,map(p+v.yyx).d-v.z));
}

float ss(in vec3 pos, in vec3 dir) {
  float sdw=1., len=.01;
  for( int i=0; i<MAX_SHADOW_TRACE_STEP; i++ ) {
    float d = map(pos + dir*len).d;
    sdw = min(sdw, SHADOW_SHARPNESS*d/len);
    len += max(d, MIN_SHADOW_MARCHING);
    if (d<TRACE_PRECISION || len>MAX_SHADOW_TRACE_DIST) break;
  }
  return clamp(sdw, 0., 1.);
}

vec4 gi(in vec3 p, in vec3 n) {
  vec4 col = vec4(0);
  for (int i=0; i<4; i++) {
    float hr = .01 + float(i) * GI_LENGTH / 4.;
    Surface s = map(n * hr + p);
    col += s.kd * (hr - s.d);
  }
  col.rgb *= GI_STRENGTH / GI_LENGTH;
  col.w = clamp(1.-col.w * AO_STRENGTH / GI_LENGTH, 0., 1.);
  return col;
}

vec3 lighting(in Hit h) {
  vec3 n = calcNormal(h.pos);
  vec4 gin = gi(h.pos, n);
  //   lin = ([Ambient]    + [Diffuse]    * [Soft shadow])      * [A.O] + [G.I.]
  vec3 lin = (_lit(n, amb) + _lit(n, dif) * ss(h.pos, dif.dir)) * gin.w + gin.rgb;
  return  h.srf.kd.rgb * lin;
}


// Ray tracing ----------
Ray ray(in vec2 p, in Camera c) {
  return Ray(c.pos, normalize(_mat3(c) * vec3(p.xy, -c.fcs)), .0, .0);
}

Ray bray(in Ray r, Camera c, float b) {
  vec3 p = c.pos + normalize(cross(r.dir, vec3(1))) * b;
  return Ray(p, normalize(r.org + r.dir * length(c.tgt - c.pos) - p), .0, .0);
}

Hit trace(in Ray r) {
  Surface s;
  for(int i=0; i<MAX_TRACE_STEP; i++) {
    s = map(_pos(r));
    r.len += s.d * FUDGE_FACTOR;
    r.stp = float(i);
    if (s.d < TRACE_PRECISION || r.len > MAX_TRACE_DIST) break;
  }
  return Hit(_pos(r), r, s);
}

vec4 render(in Hit h){
  if (h.ray.len > MAX_TRACE_DIST) return vec4(1.8,2.8,.8,h.ray.len);
  return vec4(lighting(h), h.ray.len);
}

vec4 gamma(in vec4 i) {
  return vec4(pow(i.xyz, GAMMA), i.w);
}


// Entry point ----------
void mainImage(out vec4 fragColor, in vec2 fragCoord) {
  phase = iTime * bpm / 60. * P2;

  Camera c = Camera(vec3(sin(phase/32.)*20., exp(cos(phase/64.)*1.2)*40., cos(phase/32.)*20.), vec3(0), .4, 1.73205081);
  Ray    r = ray((fragCoord.xy * 2. - iResolution.xy) / iResolution.x, c);

  vec4 col = render(trace(r));
  col += render(trace(bray(r, c, LENS_BLUR)));
  col += render(trace(bray(r, c, -LENS_BLUR)));
  fragColor = gamma(col/3.);
}

float merge(float d1, float d2)
{
    return min(d1, d2);
}

float sub(float d1, float d2)
{
    return max(-d1, d2);
}

float sdSphere(vec2 p, float r)
{
	return length(p) - r;
}

//***/
float sdTriangle( in vec2 p0, in vec2 p1, in vec2 p2, in vec2 p )
{
	vec2 e0 = p1 - p0;
	vec2 e1 = p2 - p1;
	vec2 e2 = p0 - p2;

	vec2 v0 = p - p0;
	vec2 v1 = p - p1;
	vec2 v2 = p - p2;

	vec2 pq0 = v0 - e0*clamp( dot(v0,e0)/dot(e0,e0), 0.0, 1.0 );
	vec2 pq1 = v1 - e1*clamp( dot(v1,e1)/dot(e1,e1), 0.0, 1.0 );
	vec2 pq2 = v2 - e2*clamp( dot(v2,e2)/dot(e2,e2), 0.0, 1.0 );

    float s = sign( e0.x*e2.y - e0.y*e2.x );
    vec2 d = min( min( vec2( dot( pq0, pq0 ), s*(v0.x*e0.y-v0.y*e0.x) ),
                       vec2( dot( pq1, pq1 ), s*(v1.x*e1.y-v1.y*e1.x) )),
                       vec2( dot( pq2, pq2 ), s*(v2.x*e2.y-v2.y*e2.x) ));

	return -sqrt(d.x)*sign(d.y);
}

float mapleLeafSDF(vec2 p, float r)
{
    p.x = -abs(p.x);
    p.y-=0.045;
    float s = sdSphere(p, 0.65);
    float t = sdTriangle(vec2(0.045,-0.4), vec2(1.0,-0.56), vec2(0.06,-1.0), p) - r; 
    t = merge(t, sdTriangle(vec2(-0.045,-0.4), vec2(-1.0,-0.56), vec2(-0.06,-1.0), p) - r);	
    t = merge(t, sdTriangle(vec2(-0.35,-0.35), vec2(-0.75,0.0), vec2(-1.0,-1.6), p) - r);  	
    t = merge(t, sdTriangle(vec2(-0.57,0.0), vec2(-1.0,1.0), vec2(-1.5,-0.5), p) - r);    
    t = merge(t, sdTriangle(vec2(-0.42,0.15), vec2(-1.2,0.35), vec2(-0.3,1.0), p) - r);   
    t = merge(t, sdTriangle(vec2(-0.21,0.05), vec2(-1.0,1.0), vec2(-0.35,1.0), p) - r);   
    t = merge(t, sdTriangle(vec2(-0.12,0.35), vec2(-1.0,0.75), vec2(0.25,1.0), p) - r);  
    return sub(t,s);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = (fragCoord.xy / iResolution.xy) * 2.0 - 1.0;
    uv.x *= iResolution.x / iResolution.y;
    uv *=1.2;

    float wave = 0.05*sin((iTime+uv.x)*2.0)/2.0*(uv.x+1.75);    
    uv.y += wave;

    vec3 red = vec3(.835, .169, .118);
    vec3 wh = vec3(1.0);
    vec3 bl = vec3(0.2);
    vec3 bg;
    vec3 grey = vec3(0.3);
    vec4 col;

    bg = mix(grey, bl, length(uv)-3.)+ wave*0.8;
    /*** Draw ***/    
    col.rgb = (length(uv.x) > .8 || mapleLeafSDF(uv, 0.03) < 0.0) ? red : wh;
    col.rgb = (length(uv.y) > 1. || length(uv.x) > 1.8) ?  bg : col.rgb + wave*0.7;

    fragColor = col;
}

#define MAXDIST 20.
#define GIFLENGTH 1.570795

struct Ray {
	vec3 ro;
    vec3 rd;
};

void pR(inout vec2 p, float a) {
	p = cos(a)*p + sin(a)*vec2(p.y, -p.x);
}

float length6( vec3 p )
{
	p = p*p*p; p = p*p;
	return pow( p.x + p.y + p.z, 1.0/6.0 );
}

float fractal(vec3 p)
{
   	float len = length(p);
    p=p.yxz;

    float scale = 1.25;
    const int iterations = 28;
    float a = iTime;
	float l = 0.;

    vec2 rotationAnimAmp = vec2(0.05,0.04);
	vec2 rotationPhase = vec2(.45 + sin(iTime*4. + len*0.4) * 0.025,0.15 + cos(-0.2+iTime*4. + len*0.2) * 0.05);

    // uncomment this to find good spots with the mouse :)
    //m = iMouse.xy / iResolution.xy;

    vec3 juliaOffset = vec3(-3.,-1.15,-.5);

    pR(p.xy,.5+sin(-0.25+iTime*4.)*0.1);

    for (int i=0; i<iterations; i++) {
		p = abs(p);
        // scale and offset the position
		p = p*scale + juliaOffset;

        // Rotate the position
        pR(p.xz,rotationPhase.x*3.14 + cos(iTime*4. + len)*rotationAnimAmp.y);
		pR(p.yz,rotationPhase.y*3.14 + sin(iTime*4. + len)*rotationAnimAmp.x);		
        l=length6(p);
	}
	return l*pow(scale, -float(iterations))-.25;
}

vec2 map(vec3 pos) {
    float l = length(pos);

    float dist = fractal(pos);

    return vec2(dist, 0.);
}

vec2 march(Ray ray) 
{
    const int steps = 30;
    const float prec = 0.001;
    vec2 res = vec2(0.);

    for (int i = 0; i < steps; i++) 
    {        
        vec2 s = map(ray.ro + ray.rd * res.x);

        if (res.x > MAXDIST || s.x < prec) 
        {
        	break;    
        }

        res.x += s.x;
        res.y = s.y;

    }

    return res;
}

vec3 calcNormal(vec3 pos) 
{
	const vec3 eps = vec3(0.005, 0.0, 0.0);

    return normalize(
        vec3(map(pos + eps).x - map(pos - eps).x,
             map(pos + eps.yxz).x - map(pos - eps.yxz).x,
             map(pos + eps.yzx).x - map(pos - eps.yzx).x ) 
    );
}

float calcAO( in vec3 pos, in vec3 nor )
{
float occ = 0.0;
    float sca = 1.0;
    for( int i=0; i<5; i++ )
    {
        float hr = 0.2*float(i)/4.0;
        vec3 aopos =  nor * hr + pos;
        float dd = map( aopos ).x;
        occ += -(dd-hr)*sca;
        sca *= .95;
    }
    return clamp( 1.0 - 2.0*occ, 0.0, 1.0 );    
}
vec4 render(Ray ray) 
{
    vec3 col = vec3(0.);
	vec2 res = march(ray);

    if (res.x > MAXDIST) 
    {
        return vec4(col, MAXDIST);
    }

    vec3 p = ray.ro+res.x*ray.rd;
    vec3 normal = calcNormal(p);
    vec3 pos = p;
    ray.ro = pos;
    // color with ambient occlusion
   	col = vec3(pow(calcAO(p, normal), 3.2))*0.5;

    col = mix(col, vec3(0.), clamp(res.x/MAXDIST, 0., 1.));
   	return vec4(col, res.x);
}
mat3 camera(in vec3 ro, in vec3 rd, float rot) 
{
	vec3 forward = normalize(rd - ro);
    vec3 worldUp = vec3(sin(rot), cos(rot), 0.0);
    vec3 x = normalize(cross(forward, worldUp));
    vec3 y = normalize(cross(x, forward));
    return mat3(x, y, forward);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy / iResolution.xy;
    uv = uv * 2.0 - 1.0;
    uv.x *= iResolution.x / iResolution.y;

    vec3 camPos = vec3(9., 6.5, 12.);
    vec3 camDir = camPos + vec3(-.85, -.5, -1. );
    mat3 cam = camera(camPos, camDir, 0.);

    vec3 rayDir = cam * normalize( vec3(uv, 1. + sin(iTime*4.)*0.05) );

    Ray ray;
    ray.ro = camPos;
    ray.rd = rayDir;

    vec4 col = render(ray);
    col.xyz = pow(col.xyz, vec3(0.6));
	fragColor = vec4(col.xyz,clamp(1.-col.w/MAXDIST, 0., 1.));
}

