vec2 rotate(vec2 v, float angle)
{
    float cosA = cos(angle), sinA = sin(angle);
    return v.x * vec2(cosA, sinA) + v.y * vec2(-sinA, cosA);
}

vec2 ortho(vec2 v)
{
    return vec2(v.y, -v.x);
}

void stroke(float dist, vec3 color, inout vec3 fragColor, float thickness, float aa)
{
    float alpha = smoothstep(0.5 * (thickness + aa), 0.5 * (thickness - aa), abs(dist));
    fragColor = mix(fragColor, color, alpha);
}

void fill(float dist, vec3 color, inout vec3 fragColor, float aa)
{
    float alpha = smoothstep(0.5*aa, -0.5*aa, dist);
    fragColor = mix(fragColor, color, alpha);
}

void renderGrid(vec2 pos, out vec3 fragColor)
{
    vec3 background = vec3(1.0);
    vec3 axes = vec3(0.4);
    vec3 lines = vec3(0.7);
    vec3 sublines = vec3(0.95);
    float subdiv = 8.0;

    float thickness = 0.003;
    float aa = length(fwidth(pos));

    fragColor = background;

    vec2 toSubGrid = pos - round(pos*subdiv)/subdiv;
    stroke(min(abs(toSubGrid.x), abs(toSubGrid.y)), sublines, fragColor, thickness, aa);

    vec2 toGrid = pos - round(pos);
    stroke(min(abs(toGrid.x), abs(toGrid.y)), lines, fragColor, thickness, aa);

    stroke(min(abs(pos.x), abs(pos.y)), axes, fragColor, thickness, aa);
}

float sdistLine(vec2 a, vec2 b, vec2 pos)
{
    return dot(pos - a, normalize(ortho(b - a)));
}

float sdistTri(vec2 a, vec2 b, vec2 c, vec2 pos)
{
    return max( sdistLine(a, b, pos),
            max(sdistLine(b, c, pos),
                sdistLine(c, a, pos)));
}

float sdistQuadConvex(vec2 a, vec2 b, vec2 c, vec2 d, vec2 pos)
{
    return max(  sdistLine(a, b, pos),
            max( sdistLine(b, c, pos),
             max(sdistLine(c, d, pos),
                 sdistLine(d, a, pos))));
}

void renderCircle(vec2 center, float radius, vec2 pos, inout vec3 fragColor)
{
    float dist = length(pos - center) - radius;
    stroke(dist, vec3(0, 0, 1), fragColor, 0.005, length(fwidth(pos)));
}

void renderAxes(vec2 origin, vec2 pos, inout vec3 fragColor)
{
    float len = 0.375 - 6.0 * 0.0075;
    float thickness = 0.0075;
    float aa = length(fwidth(pos));

    float xshaft = sdistQuadConvex(origin + vec2(0.5*thickness),
                                   origin - vec2(0.5*thickness),
                                   origin + vec2(len, -0.5*thickness),
                                   origin + vec2(len, 0.5*thickness), pos);
    float xhead = sdistTri(origin + vec2(len, -2.0*thickness),
                           origin + vec2(len + 6.0*thickness, 0),
                           origin + vec2(len, 2.0*thickness), pos);

    fill(min(xshaft, xhead), vec3(1, 0, 0), fragColor, aa);

    float yshaft = sdistQuadConvex(origin - vec2(0.5*thickness),
                                   origin + vec2(0.5*thickness),
                                   origin + vec2(0.5*thickness, len),
                                   origin + vec2(-0.5*thickness, len), pos);
    float yhead = sdistTri(origin + vec2(2.0*thickness, len),
                           origin + vec2(0, len + 6.0*thickness),
                           origin + vec2(-2.0*thickness, len), pos);

    fill(min(yshaft, yhead), vec3(0, 0.75, 0), fragColor, aa);

}

void mainImage(out vec4 fragColor, in vec2 fragCoord)
{
    float aspect = iResolution.x / iResolution.y;
	vec2 pos = (fragCoord / iResolution.y) * 1.0 - vec2((1.0*aspect)/2.0, 1.0/2.0);

    // animate the grid a bit with rotation, shear, and nonuniform scale
    pos *= vec2(sin(iTime*0.72) * 0.5 + 1.0, 1.0);
    pos = rotate(pos, cos(iTime) * 0.1);
    pos.x += pos.y * sin(iTime*0.89) * 0.5;

    fragColor.a = 1.0;
	renderGrid(pos, fragColor.rgb);
    renderCircle(vec2(0), 0.375, pos, fragColor.rgb);
    renderAxes(vec2(0), pos, fragColor.rgb);
}

////
// Global variables

const float PI = 3.1417;
const float PI2 = PI * 2.0;

// sampling constants
const int SAMPLE = 64;
const float SAMPLE_F = float(SAMPLE);

// ray-marching constants
const int MAX_STEP = 20;
const float EPSILON = 1e-3; // small enough, because distance is in pixels

// the disk light
float radius = 50.0; // radius in pixel
const float intensity = 3.0;


////
// Functions

// signed distance function, of the disk
float sdf(float px, float py)
{
    // SDF of a 2D disk, at the center of screen
    float x = px - iResolution.x / 2.0;
    float y = py - iResolution.y / 2.0;
    return sqrt(x * x + y * y) - radius;
}

// ray-marching for a ray; return light intensity if hitted.
float ray_marching(float ox, float oy, float dx, float dy)
{
    // ray: origin (ox, oy), direction (dx, dy)    
    for (int i = 0; i < MAX_STEP; i++)
    {
        float sd = sdf(ox, oy);
        if (sd < EPSILON)
        {
            return intensity;
        }
        ox += dx * sd;
        oy += dy * sd;
    }
    return 0.0;
}

// a fake-randon function; copied from some webpage
float rand(vec2 seed)
{
    return fract(sin(dot(seed ,vec2(12.9898,78.233))) * 43758.5453);
}

// jittered sampling for fragment (ox, oy)
float sampling(float ox, float oy)
{    
    float ret = 0.0;

    float dx = 0.0;
    float dy = 0.0;
    vec2 seed = texture(iChannel0, vec2(ox / iResolution.x, oy / iResolution.y)).xy;
    for (int i = 0; i < SAMPLE; i++)
    {
        // modify the seed each iteration
		seed.x += 0.368;
        seed.y += 0.082;

        // jitterred degree-sampling
        float deg = (float(i) + rand(seed)) / SAMPLE_F * PI2;
        dx = cos(deg);
        dy = sin(deg);

        ret += ray_marching(ox, oy, dx, dy);
    }

    return ret / SAMPLE_F;
}

// main function
void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy / iResolution.xy;
	fragColor = vec4(uv,0.5+0.5*sin(iTime),1.0);

    // compute volumetic lighting
    const vec3 light_color = vec3(1.0, .95, .9);
    float intensity = sampling(fragCoord.x, fragCoord.y);
    float alpha = clamp(intensity, 0.0, 1.0); // saturate

    // blend the volumic lighting with background
    fragColor.xyz = light_color * alpha + fragColor.xyz * (1.0 - alpha);
}

bool c(vec2 uv, vec2 origin, float radius)
{
    float x2 = (uv.x+origin.x) * (uv.x+origin.x);
    float y2 = (uv.y+origin.y) * (uv.y+origin.y);

    if (x2 + y2 < radius)
    {
    	return true;
    }

    return false;
}

bool background(vec2 uv, vec2 origin, float radius)
{
    float x2 = (uv.x+origin.x) * (uv.x+origin.x);
    float y2 = (uv.y+origin.y) * (uv.y+origin.y);

    if (x2 + y2 < radius)
    {
    	return true;
    }

    return false;
}

bool onLine(vec2 uv, vec2 start, vec2 finish)
{   
    if (uv.x > start.x && uv.x < finish.x && uv.y > start.y && uv.y < finish.y)
    {
        return true;
    }

    return false;
}

