bool log_dot = false;
bool anim_on = false;
vec2 win_mouse;
vec2 win_size;
float win_scale = 1.0;

vec4 red = vec4(1.0,0.0,0.0,1.0);
vec4 green = vec4(0.0,1.0,0.0,1.0);
vec4 black = vec4(0.0,0.0,0.0,1.0);

float pingpong(float f, float n)
{
    return abs(mod(f, n*2.0) - n);
}

float steppy(float f)
{
    float t = mod(f, 1.0);
    return floor(f)
        + 1.5 * smoothstep(0.8, 0.86, t)
        - 0.7 * smoothstep(0.86, 0.93, t)
        + 0.2 * smoothstep(0.93, 1.0, t)
    ;
}

void set_hue( inout vec4 fragColor, in vec2 uv)
{
    float hue = atan(uv.y, uv.x);

    fragColor = vec4(
        0.5 + 0.5 * vec3(cos(hue - radians(0.0)), cos(hue - radians(120.0)), cos(hue - radians(240.0))),
        1.0
   	);
}

void texture_uv( inout vec4 fragColor, in vec2 uv)
{
    uv -= 0.5;

    float p = atan(uv.y, uv.x);
    float d = log(length(uv));

    float n = 1.0 + pingpong(steppy(iTime), 16.0);

    float a1 = (p + d) * n;
    float a2 = (p - d) * n;

    float f1 = 0.0;
    float f2 = 0.0;
    f1 = smoothstep(0.7, 1.0, cos(a1));
    f2 = smoothstep(0.7, 1.0, cos(a2));

    vec4 col;
    set_hue(col, uv);

    fragColor = mix(black, col, f1 + f2);
}

void dot_uv( inout vec4 fragColor, in vec2 uv, in float frq)
{
    frq *= PI;
    float dist = length(uv);

    vec4 col;
    set_hue(col, uv);

    float f = abs(sin(log(dist) * frq));
    if (log_dot)
    	f = smoothstep(0.0, 1.0, f / frq * win_size.y * 0.5);
   	else
    	f = smoothstep(0.0, 1.0, f * dist / frq * win_scale);

    fragColor = mix(col, fragColor, f);
}

void combined_uv( inout vec4 fragColor, in vec2 uv)
{
    if (ANIM_TURN)
    {
        uv -= win_mouse;
        float angle = sin(iTime * TURN_FREQ) * ANIM_AMPLITUDE;
        vec2 v1 = vec2(cos(angle), sin(angle));
        vec2 v2 = vec2(-v1.y, v1.x);
        uv = v1 * uv.x + v2 * uv.y;
        uv += win_mouse;
    }

    if (ANIM_SCALE)
    {
        uv -= win_mouse;
        float scale = exp(cos(iTime * SCALE_FREQ) * ANIM_AMPLITUDE);
        uv *= scale;
        win_scale = 1.0/scale;
        uv += win_mouse;
    }

    texture_uv(fragColor, uv);
    dot_uv(fragColor, (uv - win_mouse) * win_size, RING_FREQ);
}

vec2 log_polar( in vec2 pd )
{
    return vec2(cos(pd.x), sin(pd.x)) * exp(pd.y);
}

vec2 polar( in vec2 pd )
{
    return vec2(cos(pd.x), sin(pd.x)) * pd.y;
}

vec3 window( inout vec4 fragColor, in vec2 uv, in vec2 pos, in vec2 size, in bool enabled)
{
    vec3 mouse = vec3((iMouse.xy - pos) / size, 0.0);

    uv -= pos;
    uv /= size;

    if (iMouse.xy == vec2(0.0, 0.0))
        mouse.xy = vec2(0.5, 0.5);

    win_size = size;
    if (enabled)
	    win_mouse = mouse.xy;

    if (floor(uv) == vec2(0))
    {
        mouse.z = 1.0;
        combined_uv(fragColor, uv);

        vec2 grid = abs(uv - floor(uv + 0.5)) * size;
        if (min(grid.x, grid.y) < 1.0)
            fragColor = mix(black, red, enabled ? 1.0 : 0.5);

    }

    return mouse;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 size = max(min(vec2(150.0), vec2(iResolution.xx / 4.0)), vec2(iResolution.yy / 3.0));
    vec3 mouse = window(fragColor, fragCoord,
    	vec2(10, iResolution.y - size.y - 10.0),
        size,
        true
    );

    anim_on = true;
    if (mouse.z == 0.0)
    {
        vec2 uv = fragCoord;
        uv.x -= iResolution.x / 2.0;

        vec2 img = uv;
        img /= iResolution.xy / 2.0;
        img.x *= PI;
        img.y *= 1.0;

        #ifdef LOG_POLAR
        	log_dot = true;
        	img = log_polar(img * vec2(1.0, 2.0) - vec2(0.0, 2.0));
        #else
        	img = polar(img);
        #endif

        combined_uv(fragColor, win_mouse + img);
    }

    win_scale = 1.0;
    log_dot = false;
    window(fragColor, fragCoord,
    	vec2(iResolution.x - size.x - 10.0, iResolution.y - size.y - 10.0),
        size,
        false
    );
}

#define M_PI 3.1415926535897932384626433832795
void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    //Time multiplier
    float t = 4000.0;
