#define tau 6.28318
#define numBlobs 15

#define p0 0.5, 0.5, 0.5,  0.5, 0.5, 0.5,  1.0, 1.0, 1.0,  0.0, 0.33, 0.67	

// source: http://iquilezles.org/www/articles/palettes/palettes.htm
// cosine based palette, 4 vec3 params
vec3 palette( in float t, in float a0, in float a1, in float a2, in float b0, in float b1, in float b2,
              in float c0, in float c1, in float c2,in float d0, in float d1, in float d2)
{
    return vec3(a0,a1,a2) + vec3(b0,b1,b2)*cos( tau*(vec3(c0,c1,c2)*t+vec3(d0,d1,d2)) );
}


const float radius = .07;
const float edgeWidth = .01;
const int numTrailParticles = 10;
const float trailLength = .3;
const float metaballThreshold = .8;
const float metaballRadius = .00325;
const float speedMultiplier = 1.1;
vec2 getPos(float t, float aspect) {
    float tx = t / aspect;
    vec2 p = vec2(sin(2.2 * tx) + cos(1.4 * tx), cos(.3 * t) + sin(-1.9 * t));
    p.y *= 0.2;
    p.x *= 0.4;
    return p;
}
float metaballFunc(vec2 pos, vec2 uv, float radius)
{
    return radius / length(uv - pos);
}
void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord.xy / iResolution.xy - vec2(.5);
    uv.x *= iResolution.x / iResolution.y;
    vec3 color = vec3(.0, 0., .0);
    vec3 colorFinal = vec3(0.);
    float finalFactor = 0.;
    float aspect = iResolution.x / iResolution.y;
    float mb = 0.;
    float[numBlobs] closeness;
    float tot = 0.;

    for (int i = 0; i < numBlobs; i++)
    {
        float minDistance = 1.0;
        float colorMul = 1.0;
        for (int j = 0; j < numTrailParticles; j++) {
            float pct = float(j) / float(numTrailParticles);
            vec2 pos = getPos(iTime * speedMultiplier + float(i) * 1.5 - pct * trailLength, aspect);
            float segmentLength = length(pos - uv);
            minDistance = min(minDistance, segmentLength + pct * (radius + edgeWidth));
            mb += metaballFunc(pos, uv, metaballRadius * pct);
        }
        finalFactor += smoothstep(radius + edgeWidth, radius, minDistance);
    	closeness[i] = pow(1. / minDistance, 3.);
    	tot += closeness[i];
    }

    for (int i = 0; i < numBlobs; i++) {
    	color += (closeness[i] / tot) * palette(float(i) / 2., p0);
    }

    finalFactor += smoothstep(mb + .03, mb, metaballThreshold);

    fragColor = vec4(min(finalFactor, 1.) * color * 1.2, 1.0);
}

#define pi  3.14159
#define tau 6.28318

const float MAX_DISTANCE = 100.;
const float EPSILON = .0001;
const int NUM_MARCHES = 255;

vec3 translate(vec3 pos, vec3 offset)
{
    return pos - offset;
}

vec3 scale(vec3 pos, vec3 scale)
{
    return pos / scale;
}

// iq's smooth min
float smin( float a, float b, float k )
{
    float h = clamp( 0.5+0.5*(b-a)/k, 0.0, 1.0 );
    return mix( b, a, h ) - k*h*(1.0-h);
}

float mergeSDF(float d1, float d2, float k)
{
    // higher k = more merging
	return smin(d1, d2, k);
}

float sphereSDF(vec3 p) 
{
    return length(p) - 1.;
}

float blobEffectSDF(vec3 p)
{
	float res = sphereSDF(scale(p, vec3(.75))) * .75;
    for (int i = 1; i <= 8; i++) {
        for (int j = 1; j <= 3; j++) 
        {
        	float angle = float(i) * 3. * pi / 4. + float(j) * 3. * pi / 4.;
        	vec3 dir = normalize(vec3(cos(angle), sin(angle), 0.));
        	vec3 offset = dir * abs(sin(1.5 * iTime + float(i + j) * .07)
                                    *  float(j) * 2.35);
            float size = .5 * pow(1. / float(j), .75);
    		res = mergeSDF(res, sphereSDF(scale(translate(p, offset),
                                                vec3(size))) * size, .7);
        }
    }
    return res;
}

float sceneSDF(vec3 p)
{
    return blobEffectSDF(p);
}

vec3 phong(vec3 normal, vec3 light, vec3 view)
{
    const vec3 ambientColor = vec3(1., 0., 1.);
    const vec3 diffuseColor = vec3(0.9, 0., 0.7);
    const vec3 specularColor = vec3(1., 1., 1.);
    const float shininess = 16.;
    const float ambientStrength = .25;

    vec3 diffuse = max(dot(normal, light), 0.) * diffuseColor;
    // light is negated because the first argument to reflect is the incident vector
    vec3 specular = pow(max(dot(reflect(-light, normal), view), 0.), shininess) * specularColor;
    vec3 ambient = ambientStrength * ambientColor;

    return diffuse + specular + ambient;
}

vec3 gradient(vec3 pos)
{
    const vec3 dx = vec3(EPSILON, 0., 0.);
    const vec3 dy = vec3(0., EPSILON, 0.);
    const vec3 dz = vec3(0., 0., EPSILON);

    return normalize(vec3(
    	sceneSDF(pos + dx) - sceneSDF(pos - dx),
        sceneSDF(pos + dy) - sceneSDF(pos - dy),
        sceneSDF(pos + dz) - sceneSDF(pos - dz)
    ));
}

float distanceToShape(vec3 pos, vec3 dir)
{
    float tot = 0.;
    for(int i = 0; i < NUM_MARCHES; i++) {
        float dist = sceneSDF(pos);
        if (dist < EPSILON || tot > MAX_DISTANCE) {
        	return tot;
        }

        tot += dist;
        pos += dir * dist;
    }
    return MAX_DISTANCE;
}

mat4 lookAt(vec3 eye, vec3 target, vec3 up)
{
    vec3 f = normalize(target - eye);
    vec3 r = normalize(cross(f, up));
    vec3 u = normalize(cross(r, f));

    return mat4(
        vec4(r, 0.),
        vec4(u, 0.),
        vec4(-f, 0.),
        vec4(0., 0., 0., 1.));
}


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (2. * fragCoord - iResolution.xy) / iResolution.y;

    vec3 eye = vec3(0., 0., 7.5);
    vec3 light = vec3(0., 3., 3.);
    vec3 ray = normalize(vec3(uv, -1.));

    mat4 viewMat = lookAt(eye, vec3(0.), vec3(0., 1., 0.));
    ray = (viewMat * vec4(ray, 1.)).xyz;

    float dist = distanceToShape(eye, ray);
    vec3 pos = eye + ray * dist;

    fragColor = vec4(0.);

    if (dist < MAX_DISTANCE) {
    	fragColor.rgb = phong(normalize(gradient(pos)), normalize(light - pos), normalize(eye - pos));
    }
}

// Uses similar ideas to continuous level of detail clipmaps: https://www.shadertoy.com/view/MstGRn .
// Emulates a basic implementation of 2D marching cubes (without any fanciness with Hermite data).
// 
// Varies sample points smoothly to achieve continuous LOD. Just moving points seems to be enough to
// get pretty good results. There are some artifacts that need polishing and could use more work.
//
// I think this could work well in full 3D with generated geometry, and is an interesting direction
// for future work.
//
// Some relevant links:
//  http://catlikecoding.com/unity/tutorials/marching-squares-3/
//  https://github.com/Scrawk/Marching-Cubes-On-The-GPU
//
// Huw Bowles 2017

#define VEL 20.

// the function that is being sampled. geometry will be generate along the isoline
// = 0.5 of this function
float ImplicitFunction( vec2 x, float sep, float lod )
{
    vec2 y = x;
    y.y -= iTime*VEL;

    // hacky form of prefiltering - blend out stuff that is too high res for the lod
    float result = 0.;
    result += (texture( iChannel0, y/32000. ).x-0.5) * .7;
    result += (texture( iChannel0, y/16000. ).x-0.5) * .5;
    result += smoothstep( 64., 32., sep*(1.+lod) ) * (texture( iChannel0, y/ 8000. ).x-0.5) * .25;
    result += smoothstep( 32., 16., sep*(1.+lod) ) * (texture( iChannel0, y/ 4000. ).x-0.5) * .125;
    result += smoothstep( 8., 4., sep*(1.+lod) ) * (texture( iChannel0, y/ 1000. ).x-0.5) * .0625;

    result += .7;
    result *= max(0., 1. - x.y/iResolution.y);

    if( iMouse.z > 0. )
    {
        float r = length(iMouse.xy-x);
        //dens += 16.*cos(.05*r)/r;
        result += 1.-smoothstep(0., 40., r);
    }

    return result;
}

struct Edge
{
    bool valid;
    vec2 s; // start
    vec2 e; // end
};
struct EdgePair
{
    Edge e0;
    Edge e1;
};
#if 0
;
#endif

EdgePair g_EdgeTable[8];

void InitMarchingCubeLUT()
{
    // the look up table copied from here:
    // https://en.wikipedia.org/wiki/Marching_squares

    // no edges
    g_EdgeTable[0].e0.valid = false;
    g_EdgeTable[0].e1.valid = false;

    // one edge
    g_EdgeTable[1].e0.valid = true;
    g_EdgeTable[1].e0.s = vec2(0., .5);
    g_EdgeTable[1].e0.e = vec2(.5, 0.);
    g_EdgeTable[1].e1.valid = false;

    g_EdgeTable[2].e0.valid = true;
    g_EdgeTable[2].e0.s = vec2(1., .5);
    g_EdgeTable[2].e0.e = vec2(.5, 0.);
    g_EdgeTable[2].e1.valid = false;

    g_EdgeTable[3].e0.valid = true;
    g_EdgeTable[3].e0.s = vec2(0., .5);
    g_EdgeTable[3].e0.e = vec2(1., .5);
    g_EdgeTable[3].e1.valid = false;

    g_EdgeTable[4].e0.valid = true;
    g_EdgeTable[4].e0.s = vec2(.5, 1.);
    g_EdgeTable[4].e0.e = vec2(1., .5);
    g_EdgeTable[4].e1.valid = false;

    // two edges
    g_EdgeTable[5].e0.valid = true;
    g_EdgeTable[5].e1.valid = true;
    g_EdgeTable[5].e0.s = vec2(0., .5);
    g_EdgeTable[5].e0.e = vec2(.5, 1.);
    g_EdgeTable[5].e1.s = vec2(.5, 0.);
    g_EdgeTable[5].e1.e = vec2(1., .5);

    g_EdgeTable[6].e0.valid = true;
    g_EdgeTable[6].e0.s = vec2(.5, 0.);
    g_EdgeTable[6].e0.e = vec2(.5, 1.);
    g_EdgeTable[6].e1.valid = false;

    g_EdgeTable[7].e0.valid = true;
    g_EdgeTable[7].e0.s = vec2(.0, .5);
    g_EdgeTable[7].e0.e = vec2(.5, 1.);
    g_EdgeTable[7].e1.valid = false;
}

EdgePair LookupEdges(int mask)
{
    // symmetry! https://en.wikipedia.org/wiki/Marching_squares
    if( mask > 7 ) mask = 15 - mask;

    return g_EdgeTable[mask];
}

// adjusts the vertex position to approximate the isosurface, given values
// sampled at cube corners
vec2 MakeVertexLerpData( vec2 p, float v00, float v01, float v10, float v11 )
{
    vec2 result = p;

	if		( p.y == 0. ) result.x = p.x * 2. * (0.5-v00) / (v10-v00);
	else if ( p.y == 1. ) result.x = p.x * 2. * (0.5-v01) / (v11-v01);
	if		( p.x == 0. ) result.y = p.y * 2. * (0.5-v00) / (v01-v00);
	else if ( p.x == 1. ) result.y = p.y * 2. * (0.5-v10) / (v11-v10);

    // could do a second sample at new position and do a secant method step,
    // but this may be too expensive in practive.

    return result;
}

// compute the 1 or 2 edges for this cube
EdgePair GenerateEdges( vec2 x00, vec2 cubeSize, float sep, float lod )
{
    vec2 x10 = x00 + vec2(cubeSize.x,0.);
    vec2 x01 = x00 + vec2(0.,cubeSize.y);
    vec2 x11 = x00 + cubeSize;

    // minus 0.5 baked into the uses of vXY
    float v00 = ImplicitFunction(x00, sep, lod);// - 0.5;
    float v01 = ImplicitFunction(x01, sep, lod);// - 0.5;
    float v10 = ImplicitFunction(x10, sep, lod);// - 0.5;
    float v11 = ImplicitFunction(x11, sep, lod);// - 0.5;

    int mask = 0;

    if( v00 < 0.5 ) mask += 1;
    if( v10 < 0.5 ) mask += 2;
    if( v11 < 0.5 ) mask += 4;
    if( v01 < 0.5 ) mask += 8;

    EdgePair e = LookupEdges(mask);

    if( e.e0.valid )
    {
        e.e0.s = MakeVertexLerpData( e.e0.s, v00, v01, v10, v11 );
        e.e0.e = MakeVertexLerpData( e.e0.e, v00, v01, v10, v11 );
    }
    if( e.e1.valid )
    {
        e.e1.s = MakeVertexLerpData( e.e1.s, v00, v01, v10, v11 );
        e.e1.e = MakeVertexLerpData( e.e1.e, v00, v01, v10, v11 );
    }

    return e;
}

// compute grid resolution, and interpolation factor between lods.
// the resolution could be seeded directly by geometry.
void ComputeSepLod(in vec2 x, in vec2 detailCenter, out float sep, out float lod )
{
    // detail driven by taxicab norm, this gives square isolines that can be a good
    // fit to rectilinear world space textures. we use this in our Crest ocean renderer
    // see slides: https://github.com/huwb/crest-oceanrender
    float taxi = max(abs(x.x-detailCenter.x),abs(x.y-detailCenter.y));
    float sepLin = taxi / 20.;
    float sepLog = log2(sepLin);
    float sepLogMin = 2.; // min spacing: 4
    sepLog = max( sepLog, sepLogMin );
    float sepLogFloor = floor(sepLog);

    sep = exp2( sepLogFloor );
    lod = sepLog - sepLogFloor;
}

// compute the marching cube around the current pixel. ordinarily the cube
// geometry would be submitted as vertices to a geometry shader explicitly. there is
// currently some error in this function, especially visible at low lods.
void ComputeCubeFromPixel(vec2 x, float sep, float lod, out vec2 x00, out vec2 cubeSize)
{
    cubeSize = vec2(sep);

    vec2 xrem = mod(x+vec2(0.,-iTime*VEL*.7-iMouse.y*0.), 2.*cubeSize);
    x00 = x - xrem;

    // hackery to emulate smoothly changing cube sizes

    if( xrem.x < (1. - lod)*cubeSize.x )
    {
	    cubeSize.x *= 1. - lod;
    }
    else
    {
        x00.x += (1. - lod)*cubeSize.x;
        cubeSize.x *= 1. + lod;
    }
    if( xrem.y < (1. - lod)*cubeSize.y )
    {
	    cubeSize.y *= 1. - lod;
    }
    else
    {
        x00.y += (1. - lod)*cubeSize.y;
        cubeSize.y *= 1. + lod;
    }
}

float Dot(vec2 x, vec2 c);
float Line(vec2 x, vec2 a, vec2 b);

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    InitMarchingCubeLUT();

    vec2 x = fragCoord;

    // detail center
    vec2 detailCenter = vec2(.5-0.5*cos(.4*iTime),0.5) * iResolution.xy;
    if( iMouse.z > 0. )
        detailCenter = iMouse.xy;

    // compute grid resolution, and interpolation factor between lods.
    float lod, sep;
    ComputeSepLod( x, detailCenter, sep, lod );

    // compute the marching cube around the current pixel.
    vec2 x00, cubeSize;
	ComputeCubeFromPixel( x, sep, lod, x00, cubeSize);

    // compute 1 or 2 edges that pass through this cube
    EdgePair e = GenerateEdges(x00, cubeSize, sep, lod);

    // draw the lines
    if( e.e0.valid ) fragColor.xy += Line(x, x00 + e.e0.s * cubeSize, x00 + e.e0.e * cubeSize);
    if( e.e1.valid ) fragColor.xy += Line(x, x00 + e.e1.s * cubeSize, x00 + e.e1.e * cubeSize);

    // draw the value at the center of the cube for debug vis
    fragColor.b += clamp(.5+20.*(ImplicitFunction(x00+.5*cubeSize,max(cubeSize.x,cubeSize.y),0.)-.5),0.,.4);
    // draw the cube corners for debug vis
    if(iMouse.z > 0. || true)
    {
        float v = 0.;
        v = max( v, Dot(x00,x) );
        v = max( v, Dot(x00+cubeSize,x) );
        v = max( v, Dot(x00+vec2(cubeSize.x,0.),x) );
        v = max( v, Dot(x00+vec2(0.,cubeSize.y),x) );
        fragColor += .6*v;
    }

    fragColor.rgb += .1;
}

// Utility functions...

// Return minimum distance between line segment vw and point p
// HB: This code is not mine, I lost the reference, sorry original author!
float line_d(vec2 p, vec2 v, vec2 w)
{
  float l2 = dot(v-w, v-w);  // i.e. |w-v|^2 -  avoid a sqrt
  if (l2 == 0.0) return distance(p, v);   // v == w case
  // Consider the line extending the segment, parameterized as v + t (w - v).
  // We find projection of point p onto the line. 
  // It falls where t = [(p-v) . (w-v)] / |w-v|^2
  float t = dot(p - v, w - v) / l2;
  if (t < 0.0) return distance(p, v);       // Beyond the 'v' end of the segment
  else if (t > 1.0) return distance(p, w);  // Beyond the 'w' end of the segment
  vec2 projection = v + t * (w - v);  // Projection falls on the segment
  return distance(p, projection);
}

float Dot(vec2 x, vec2 c)
{
    float R = 1.5;
    return smoothstep(R,0.,length(x-c));
}

float Line(vec2 x, vec2 a, vec2 b)
{
    float R = 2.;
    return smoothstep(R,0.,line_d(x,a,b));
}

