// "Buoyant Balls" by dr2 - 2017
// License: Creative Commons Attribution-NonCommercial-ShareAlike 3.0 Unported License

float Hashfv2 (vec2 p);
vec3 Hashv3v3 (vec3 p);

vec3 ltDir;
float tCur, dstFar;
const vec3 bGrid = vec3 (2.);
const float pi = 3.14159;

float ObjDf (vec3 p, vec3 cId)
{
  vec3 h;
  float s, d, r, a;
  d = dstFar;
  h = Hashv3v3 (cId);
  if (h.x * step (2., length (cId.xz)) > 0.5) {
    p -= bGrid * (cId + 0.5);
    s = fract (64. * length (h));
    s *= s;
    r = 0.2 + 0.2 * bGrid.x * h.x * (1. - s) * abs (sin (3. * pi * h.y * (1. - s)));
    a = h.z * tCur + h.x;
    d = length (p - r * vec3 (cos (a), 0., sin (a))) - 0.4 + 0.3 * s;
  }
  return d;
}

float ObjRay (vec3 ro, vec3 rd)
{
  vec3 p, cId, s;
  float dHit, d, eps;
  eps = 0.0005;
  if (rd.x == 0.) rd.x = 0.001;
  if (rd.y == 0.) rd.y = 0.001;
  dHit = eps;
  for (int j = 0; j < 120; j ++) {
    p = ro + rd * dHit;
    cId.xz = floor (p.xz / bGrid.xz);
    p.y -= tCur * (1. + Hashfv2 (cId.xz));
    cId.y = floor (p.y / bGrid.y);
    d = ObjDf (p, cId);
    s = (bGrid * (cId + step (0., rd)) - p) / rd;
    d = min (d, abs (min (min (s.x, s.y), s.z)) + eps);
    if (d < eps || dHit > dstFar) break;
    dHit += d;
  }
  if (d >= eps) dHit = dstFar;
  return dHit;
}

float ObjDfN (vec3 p)
{
  vec3 cId;
  cId.xz = floor (p.xz / bGrid.xz);
  p.y -= tCur * (1. + Hashfv2 (cId.xz));
  cId.y = floor (p.y / bGrid.y);
  return ObjDf (p, cId);
}

vec3 ObjNf (vec3 p)
{
  vec4 v;
  vec3 e = vec3 (0.001, -0.001, 0.);
  v = vec4 (ObjDfN (p + e.xxx), ObjDfN (p + e.xyy), ObjDfN (p + e.yxy), ObjDfN (p + e.yyx));
  return normalize (vec3 (v.x - v.y - v.z - v.w) + 2. * v.yzw);
}

vec3 BgCol (vec3 rd)
{
  float t, gd, b;
  t = tCur * 4.;
  b = dot (vec2 (atan (rd.x, rd.z), 0.5 * pi - acos (rd.y)), vec2 (2., sin (rd.x)));
  gd = clamp (sin (5. * b + t), 0., 1.) * clamp (sin (3.5 * b - t), 0., 1.) +
     clamp (sin (21. * b - t), 0., 1.) * clamp (sin (17. * b + t), 0., 1.);
  return mix (vec3 (0.25, 0.5, 1.), vec3 (0., 0.4, 0.3), 0.5 * (1. - rd.y)) *
     (0.24 + 0.44 * (rd.y + 1.) * (rd.y + 1.)) * (1. + 0.15 * gd);
}

vec3 ShowScene (vec3 ro, vec3 rd)
{
  vec3 col, bgCol, vn;
  float dstObj;
  bgCol = BgCol (rd);
  dstObj = ObjRay (ro, rd);
  if (dstObj < dstFar) {
    ro += dstObj * rd;
    vn = ObjNf (ro);
    col = mix (vec3 (0.15, 0.35, 0.7), BgCol (reflect (rd, vn)), 0.8);
    col = col * (0.4 + 0.1 * max (vn.y, 0.) + 0.5 * max (dot (vn, ltDir), 0.)) +
       0.05 * pow (max (dot (normalize (ltDir - rd), vn), 0.), 32.);
    col *= 0.3 + 0.7 * min (rd.y + 1., 1.5);
    col = mix (col, bgCol, smoothstep (0.5 * dstFar, dstFar, dstObj));
  } else col = bgCol;
  return clamp (col, 0., 1.);
}

void mainImage (out vec4 fragColor, vec2 fragCoord)
{
  mat3 vuMat;
  vec4 mPtr;
  vec3 ro, rd;
  vec2 canvas, uv, ori, ca, sa;
  float el, az;
  canvas = iResolution.xy;
  uv = 2. * fragCoord.xy / canvas - 1.;
  uv.x *= canvas.x / canvas.y;
  tCur = iTime;
  mPtr = iMouse;
  mPtr.xy = mPtr.xy / canvas - 0.5;
  az = 0.03 * pi * tCur;
  el = 0.2 * pi * sin (0.02 * pi * tCur);
  if (mPtr.z > 0.) {
    az = 2. * pi * mPtr.x;
    el = 0.6 * pi * mPtr.y;
  }
  tCur += 100.;
  el = clamp (el, -0.3 * pi, 0.3 * pi);
  ori = vec2 (el, az);
  ca = cos (ori);
  sa = sin (ori);
  vuMat = mat3 (ca.y, 0., - sa.y, 0., 1., 0., sa.y, 0., ca.y) *
          mat3 (1., 0., 0., 0., ca.x, - sa.x, 0., sa.x, ca.x);
  ro = vec3 (0.5);
  rd = vuMat * normalize (vec3 (uv, 3.));
  ltDir = normalize (vec3 (0.2, 1., -0.2));
  dstFar = 50.;
  fragColor = vec4 (pow (ShowScene (ro, rd), vec3 (0.8)), 1.);
}

const float cHashM = 43758.54;

float Hashfv2 (vec2 p)
{
  return fract (sin (dot (p, vec2 (37., 39.))) * cHashM);
}

vec3 Hashv3v3 (vec3 p)
{
  vec3 cHashVA3 = vec3 (37., 39., 41.);
  return fract (sin (vec3 (dot (p, cHashVA3), dot (p + vec3 (1., 0., 0.), cHashVA3),
     dot (p + vec3 (0., 1., 0.), cHashVA3))) * cHashM);
}

                                                    // --- find ellipse geometry
void ellips(in mat2 M, out vec2 A0, out vec2 A1, out float r0, out float r1) {
    vec2 I = M[0], J = M[1];                        // ellipse: P = cos(u).I + sin(u).J 
    float a = ( dot(J,J) - dot(I,I) ) / dot (I,J),  // -> search for extrema of length(P) 
          D = sqrt(a*a+4.),                         // get tan(u) -> s, c, axis*radii
          t0 = (a+D)/2., t1 = (a-D)/2.;
    A0 = (I + t0*J) / sqrt(1.+t0*t0);               // c = 1/sqrt(1+t), s = t/sqrt(1+t)
    A1 = (I + t1*J) / sqrt(1.+t1*t1);               // axis also encodes radius
    r0 = length(A0); A0 /= r0;
    r1 = length(A1); A1 /= r1;

}

float square(vec2 U,vec2 r, float e) {             // --- draw framed square
    U = abs(U) - r/2.;
    return abs(max(U.x,U.y)) - e/iResolution.y;
}
float square(vec2 U,float r, float e) { return square(U,vec2(r),e); }
float squareF(vec2 U,float r, float e) {           // --- draw filled square
    U = abs(U) - r/2.;
    return step( max(U.x,U.y),0.);
}

float segment(vec2 u, vec2 a, vec2 b)  {           // --- draw line segment
    b -= a, u -= a;
    return length( u - b * clamp(dot(b, u) / dot(b, b), 0., 1.));
}

float checker(vec2 U, float n) {                   // --- draw checker   
    ivec2 V = ivec2( U * n ) % 2;        
    return float( V.x == V.y );  
}    

#define rot(a)      mat2(cos(a),sin(a),-sin(a),cos(a))
#define S(v)        smoothstep( 1./R.y, 0., v )
#define blend(v,C)  O = mix(O, C, S(v) )
#define spot(v,C)   blend ( 2.*(length(v) - r/R.y) , C )  

void mainImage( out vec4 O, vec2 U )               // === draw MIPmap footprint
{
    vec2 R = iResolution.xy,
         P = iMouse.xy,
         e = vec2(1./R.y,0);
    float t = iTime;
    if (length(P)<10.) P = R*.5 + .15*R*vec2(cos(t),sin(t)); 
    U /= R.y; P /= R.y; 

    O = vec4(.1+.1*checker(U,32.));                // --- (fake) texture   
    O.a = 0.;

                                                   // --- drawing footprints
    float r = .03*R.y;                             // "pixel" radius
    vec4 red = vec4(1,0,0,1), blue = vec4(0,0,1,1), green = vec4(0,1,0,1),
      orange = vec4(1,.6,.3,1), white = vec4(1), grey = vec4(.7,.7,.7,1), black = vec4(0,0,0,1);

    mat2 J = mat2(3,6, 3.+3.*sin(t*.7),-5)/R.y;   // Jacobian of some pixel projection on texture
	J = rot(.3*sin(t)) * J * (1.+.3*cos(t/2.));

    vec2 A,a, Q,q; float M,m, k,f,T;              // get ellipse geomtry for texture footprint
    ellips(J, A,a,M,m); 
    Q = (U-P) * mat2(A/M,a/m)/R.y;
    spot( Q , grey);                              // draw ellipse
	blend( segment(U, P,P+J[0]*r)/2., blue);      // draw footprint frame = ( dFdx, dFdy )
	blend( segment(U, P,P+J[1]*r)/2., green);
    Q = (U-P) * mat2(A,a);
	blend( segment(U, P,P+A*M*r), red);           // draw ellipse axis
	blend( segment(U, P,P+a*m*r), red);
    blend( square( Q, vec2(M,m)*r*2., 1.), red);  // footprint fitting rectangle ( used for x16 aniso)

                                                  // --- MIPmap footprint      
    M = length(J[0]) *r*2.; 
    m = length(J[1]) *r*2.;
    // log2( M );                                 // MIPmap level corresponding to M
    // log2( m );                                 // MIPmap level corresponding to m
    blend( square( U-P, M, 1.), blue);            // axis-aligned box 
    blend( square( U-P, m, 1.), green);
    k = (M+m)/2.;
    blend( square( U-P, k, 1.), orange*checker(U-P,128.));   // mean (constructor choice. Maybe max).
    M = exp2(ceil(log2(k)));
    m = exp2(floor(log2(k)));
    f = fract(log2(k));
    blend( square( U-P, M, .5), orange);          // trilinear: up stored level
    blend( square( U-P, m, .5), orange);          // trilinear: bottom stored level
    Q = U-floor(P/M)*M,
    q = U-floor(P/m)*m;
    T = 1.-O.a;
    for( int i=0; i<4; i++ ) {                    // bilinear: 4 fetchs
        vec2 C = vec2(i%2,i/2),                   // 2x2 index
             G = abs(1.-C-fract(P/M)),            // weight for each
             g = abs(1.-C-fract(P/m));
        O += T* step(square( Q-M*C, M, .5), 0.);  // texels at upper level
        O += T* step(square( q-m*C, m, .5), 0.);  // texels at downer level
    	O.r += T* squareF( Q-M*C, M, .5) *G.x*G.y*f*2.;      // painted with weight
    	O.g += T* squareF( q-m*C, m, .5) *g.x*g.y*(1.-f)*2.;
    }
}

