#define effect1 ((mod(t, ct) > 15.0 && mod(t, ct) < 30.0) || (mod(t, ct) > 90.0 && mod(t,ct) < 105.0))
#define effect2 ((mod(t, ct) > 30.0 && mod(t, ct) < 45.0) || (mod(t, ct) > 105.0 && mod(t, ct) < 120.0))
#define effect3 ((mod(t, ct) > 45.0 && mod(t, ct) < 60.0) || (mod(t, ct) > 120.0 && mod(t, ct) < 135.0))
#define effect4 ((mod(t, ct) > 60.0 && mod(t, ct) < 75.0) || (mod(t, ct) > 135.0))
#define rtimer (mod(t, ct) < 30.0 || (mod(t, ct) > 75.0 && mod(t, ct) < 105.0))
#define txt1 (t > 8.5 && t < 8.65)
#define txt2 (t> 12.8 && t < 12.95)
#define txt3 (t> 15.7 && t < 15.85)
#define txt4 (t > 17.1 && t < 17.25)
#define txt5 (t > 18.1 && t < 18.25)
#define txt6 (t > 19.3 && t < 19.65)
#define txt7 (t > 150.0 && t < 160.0)
#define nastyTimes (txt1 || txt2 || txt3 || txt4 || txt5 || txt6 || txt7)
#define t iTime

float rand(vec2 co) {
  return fract(sin(dot(co.xy ,vec2(12.9898,78.233))) * 43758.5453);
}

float magic(int ch, vec2 fragCoord) {
    return mod(rand(vec2(fragCoord.y, iTime)), 1.0);
}
// txt encoding done with https://github.com/knarkowicz/ShadertoyText
float GlyphSDF( vec2 p, float glyph ) {
    p = abs( p.x - .5 ) > .5 || abs( p.y - .5 ) > .5 ? vec2( 0. ) : p;
    return 2. * ( texture( iChannel0, p / 16. + fract( vec2( glyph, 15. - floor( glyph / 16. ) ) / 16. ) ).w - 127. / 255. );
}	

void Text( inout vec3 color, vec2 p ) {    
    float glyphRatio = 2.0;
    vec2 glyphScale = 20. * vec2( 1., glyphRatio );
    if(txt7) { glyphScale = 7. * vec2(1., glyphRatio); }
    vec2 t0 = floor( p / glyphScale );

    uint v = 0u;
    if(txt1) {
        v = t0.y == 1. ? ( t0.x < 4. ? 538976288u : ( t0.x < 8. ? 538976288u : ( t0.x < 12. ? 1668248151u : 7823724u ) ) ) : v;
        v = t0.x >= 0. && t0.x < 16. ? v : 0u;
    } else if(txt2) {
        v = t0.y == 1. ? ( t0.x < 4. ? 538976288u : ( t0.x < 8. ? 1180114976u : ( t0.x < 12. ? 1465196609u : 114u ) ) ) : v;
  		v = t0.x >= 0. && t0.x < 16. ? v : 0u;
    } else if(txt3) {
        v = t0.y == 0. ? ( t0.x < 4. ? 538976288u : 1296900128u ) : v;
  		v = t0.x >= 0. && t0.x < 8. ? v : 0u;
    } else if(txt4) {
        v = t0.y == 2. ? ( t0.x < 4. ? 1195854163u : ( t0.x < 8. ? 1213219154u : ( t0.x < 12. ? 1634222880u : 1919251568u ) ) ) : v;
  		v = t0.x >= 0. && t0.x < 16. ? v : 0u;
    } else if(txt5) {
        v = t0.y == 0. ? ( t0.x < 4. ? 1869768779u : ( t0.x < 8. ? 544436078u : ( t0.x < 12. ? 1885431875u : 7497076u ) ) ) : v;
  		v = t0.x >= 0. && t0.x < 16. ? v : 0u;
    } else if(txt6) {
        v = t0.y == 2. ? ( t0.x < 4. ? 544303392u : ( t0.x < 8. ? 1867522086u : 108u ) ) : v;
        v = t0.y == 1. ? ( t0.x < 4. ? 1713381408u : ( t0.x < 8. ? 29295u : 0u ) ) : v;
        v = t0.y == 0. ? ( t0.x < 4. ? 1684105299u : ( t0.x < 8. ? 1867805285u : 8569u ) ) : v;
        v = t0.x >= 0. && t0.x < 12. ? v : 0u;
    } else if(txt7) {
        v = t0.y == 6. ? ( t0.x < 4. ? 1663069769u : ( t0.x < 8. ? 543519585u : ( t0.x < 12. ? 544567161u : ( t0.x < 16. ? 1936943469u : ( t0.x < 20. ? 975201381u : ( t0.x < 24. ? 41u : 0u ) ) ) ) ) ) : v;
        v = t0.y == 5. ? ( t0.x < 4. ? 1868854603u : ( t0.x < 8. ? 1869881459u : ( t0.x < 12. ? 541936928u : ( t0.x < 16. ? 544370534u : ( t0.x < 20. ? 543516788u : ( t0.x < 24. ? 1852143205u : ( t0.x < 28. ? 8564u : 0u ) ) ) ) ) ) ) : v;
        v = t0.y == 4. ? ( t0.x < 4. ? 1970235475u : ( t0.x < 8. ? 1953853300u : ( t0.x < 12. ? 1919903264u : ( t0.x < 16. ? 1195987744u : ( t0.x < 20. ? 1095911239u : ( t0.x < 24. ? 639649872u : ( t0.x < 28. ? 1919437600u : ( t0.x < 32. ? 1936682607u : 33u ) ) ) ) ) ) ) ) : v;
        v = t0.y == 3. ? 0u : v;
        v = t0.y == 2. ? ( t0.x < 4. ? 639660393u : ( t0.x < 8. ? 1819234336u : ( t0.x < 12. ? 1919903264u : ( t0.x < 16. ? 1634226976u : ( t0.x < 20. ? 1953654116u : ( t0.x < 24. ? 2193775u : 0u ) ) ) ) ) ) : v;
        v = t0.y == 1. ? ( t0.x < 4. ? 1630416430u : ( t0.x < 8. ? 1629512814u : ( t0.x < 12. ? 1948281964u : ( t0.x < 16. ? 1881171304u : ( t0.x < 20. ? 1819307877u : ( t0.x < 24. ? 101u : 0u ) ) ) ) ) ) : v;
        v = t0.y == 0. ? ( t0.x < 4. ? 538976288u : ( t0.x < 8. ? 1752637472u : ( t0.x < 12. ? 1952522351u : ( t0.x < 16. ? 1701733748u : ( t0.x < 20. ? 8548u : 0u ) ) ) ) ) : v;
        v = t0.x >= 0. && t0.x < 36. ? v : 0u;
    }
	float c = float( ( v >> uint( 8. * t0.x ) ) & 255u );

    p = ( p - t0 * glyphScale ) / glyphScale;
    p.x = ( p.x - .5 ) / glyphRatio + .5;

    float sdf = GlyphSDF( p, c );
    if ( c != 0. ) {
    	color = mix( vec3( .2 ), color, smoothstep( -.04, +.04, sdf ) );
    }
}

void mainImage( out vec4 fragColor, in vec2 fragCoord ) {
    float ct = 150.0;

    if(nastyTimes) {
        vec2 uv = fragCoord.xy / iResolution.xy;
        vec2 p = 2. * uv - 1.;
        p.x *= iResolution.x / iResolution.y;    
        p = p * 100. - vec2( -150, -78 );

        float woot = mod(t, ct);
        vec3 col = vec3(0.0);
        if(txt4) {
        col = vec3(tan(woot), woot/100.0, sin(woot) * tan(woot));
        } else {
        col = vec3(cos(woot), mod(exp(woot), 1.0), tan(cos(uv.x)) * woot/100.0);
        }
        if(txt7) {
            col = vec3(0.5);
        }


        Text( col, p );
        fragColor = vec4( col, 1. );
    }
    if(!nastyTimes) {

        int choice = 0;

        float t1 = 2.42;
        float rot = radians(t * 45.0);
        vec2 m = vec2(sin(rot), cos(rot));
        vec2 q = fragCoord.xy / iResolution.xy;
        vec2 p = (-1.0 + 2.0 * q) * vec2(iResolution.x / iResolution.y, 1.0) * m;
        //vec2 q = fragCoord.xy / iResolution.xy;
        //vec2 p = 2. * q - 1. * m;
        mat2 re = mat2(cos(rot), -sin(rot), sin(rot), cos(rot));

        if(mod(t, 15.0) > 7.5) {

            if(mod(floor(fragCoord.y), floor(2.0)) == floor(0.0)) {
                fragCoord.x += 40.0;
                choice = 1;
            } else {
                fragCoord.x -= 40.0;
                choice = 2;
            }
        }


        if(mod(t, ct) < 30.0 || mod(t, ct) > 60.0) {
            p = (-1.0 + 2.0 * q) * vec2(iResolution.x / iResolution.y, 1.0);
        } else if(mod(t, ct) > 45.0 && mod(t, ct) < 60.0) {
            m = vec2(tan(rot), sin(rot)/cos(rot));
            p = (-1.0 + 2.0 * q) * vec2(iResolution.x / iResolution.y, 1.0) * m / 2.0;
        }

        float d = abs(sin(length(p)*10.0+0.5)) + 0.6;
        vec2 uv = vec2(p)/d;

        if(rtimer || effect4) {
            uv *= re; 
        }
        uv = uv * (tan(t*t1));

        float woot = mod(t, ct);
        vec3 col = vec3(sin(woot), woot/100.0, tan(woot));

        if(effect1) { //15-30s 90-105
            col = vec3(woot/100.0, sin(woot), mod(exp(woot), 1.0) * tan(woot));
        } else if(effect2) { //30-45s 105-120
            woot = magic(choice, fragCoord.xy);
            uv.y -= 0.2;
            col = vec3(cos(woot), mod(exp(woot), 1.0), tan(cos(uv.x)) * woot/100.0);
        } else if(effect3) { //45-60s 120-135
            woot = magic(choice, fragCoord.xy);
            uv.y -= 0.2;
            col = vec3(tan(woot), woot/100.0, sin(woot) * tan(woot)); 
        } else if(effect4) { //60-75s 135-150
            woot = magic(choice, fragCoord.xy);
            col = vec3(mod(abs(exp(woot) - exp(fragCoord.x)) * sin(woot), 1.0),
                       woot/100.0 * magic(choice, uv.xy) * sin(uv.y),
                       sin(uv.x / 0.5 * uv.y));
        }


        if(mod(t, 30.0) < 15.0 || (mod(t, ct) > 60.0 && mod(t,ct) < 75.0)) { 
            col = col * 1.5 / d * (abs(sin(5.0 + mod(t, 4.0)))) * uv.x;
        } else {
            col = col * 1.5 / d * (abs(sin(5.0 + mod(t, 4.0)))) * uv.y;
        }
        col *= -1.0;
        if(choice == 2 ) {
            col *= -1.0;
        } else if(mod(t, 30.0) < 15.0 && mod(t, ct) < 45.0) {
            col *= mod(exp(mod(t, 15.0)), 1.0);
        } else if(effect1) {
            if(mod(t, 15.0) < 7.5)
                col *= cos(exp(fragCoord.y/ uv.x));
            else col *= cos(exp(fragCoord.y / uv.y)) * sin(uv.y / uv.x) * -1.0;
        } else if(effect4) {
            col *= mod(floor(exp(uv.x)) * uv.y * tan(rot), 1.0) * abs(sin(uv.x / uv.y));
        }
        fragColor = vec4(col, 1.0);
    }
}

