void mainImage(out vec4 O, vec2 u) {
    O.xy = asin( 4.* u / iResolution.xy - 2. ),
    O.zw = - O.xy;
    O =  O.xxzz / 2. - O.ywyw / 3. + iTime;
    O = fwidth(O) / abs( fract( O * 1.9 ) -.5 ); O += O.a; }




