const float INVERSE_MAX_UINT = 1.0 / 4294967295.0;

// The GPU version of rand()
float rand(uint seed, float range)
{
    uint i = (seed ^ 12345391u) * 2654435769u;
    i ^= (i << 6u) ^ (i >> 26u);
    i *= 2654435769u;
    i += (i << 5u) ^ (i >> 12u);
    return float(range * float(i)) * INVERSE_MAX_UINT;
}

// Helper function for rand()
float ra(uint seed)
{
    return rand(seed++, 2.0) - 1.0;
}

float sdSphereBlur(vec2 uv, vec2 p, float r, float b) {

    float d = length(uv-p);
    return smoothstep(r, r - b, d);
}

vec3 ColouringA(vec2 uv, vec2 p, float iMd, float mixer, float wave)
{
    uv = uv-p;

    uint seed = uint((iTime + iMd) * 10.);
	vec3 c = vec3(0);
    // generate random values -1 to 1
    vec3 r = vec3(ra(seed++), ra(seed++), ra(seed++));

    r = (r / .5) * (sin(10./iTime) + 1.0);

    // Warps perspective of colours
    c.r = r.r * (uv.x + sin(r.r + iTime));
    c.g = r.g * (uv.y + tan(r.b + iTime));
    c.b = c.r + r.b / sin(iTime);

    c /= length(c)  * sin(iTime);

    c.r /= mod(c.g, c.r);
    c.g /= mod(c.b, c.g);
    c.b /= mod(c.r, c.b);
    c -= c.r * sin(0.5/r.b);

    if(mixer==1.0) {
        r -= (r / .5) * (sin(1./iTime) + 1.0);

        // Warps perspective of colours
        c.r = r.r / length(sin(iTime)/uv.x) / sin(r.r) * wave;
        c.g = r.g * length(tan(iTime)/uv.y) / cos(r.g * length(uv*wave)) * wave;

        c = cos(-0.5/r);
        c *= 0.001;
    }
    return c;
}

float GenerateBalls(vec2 uv, float mixer, float wave)
{
    float m = 0.0;
    float maxS = 20.0 - (10.0 * mixer);
    for(float i = 0.0; i < maxS; i+=1.0) {

        float ang = (i/maxS) * (2. * 3.14) + mixer * (.1 * (tan(length(uv) / pow(iTime, wave))));
        ang += sin(iTime);
        float z =  1.5 * wave + (mixer * 1.0);
        float cen = (i-(maxS/2.)) * 0.1;

        float x = z * sin(ang);
        float y = z * cos(ang);
        vec2 p = vec2(x,y);
        m += sdSphereBlur(uv, p, 0.12 + mixer * z/1.0, 0.1 + (mixer * 0.01));
    }

    return m;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // UV definition
    float m = 0.0;
    vec3 c = vec3(0.0);

    //
	vec2 uv = CorrectAspectRatio(fragCoord.xy, 	iResolution.xy);
    vec2 cP = vec2(iResolution.x,iResolution.y)/2. + vec2(sin(iTime), cos(iTime));
    vec2 iM = CorrectAspectRatio(cP, iResolution.xy);
    float iMDelta = length(iM.xy - uv.xy);

    vec2 p = vec2(0.0);

    int tx = int(uv.x/512.0);
    float freq = texelFetch( iChannel0, ivec2(tx,1), 0 ).x;
    float wave = texelFetch( iChannel0, ivec2(tx,0), 0 ).x;
    float mixer = ((freq > 0.39) && (wave > 0.8)) ? 1.0 : 0.0;

    //wave = mix(wave, pow(wave, exp(wave)), mixer);


    c = ColouringA(uv, p, iMDelta, mixer, wave);
    m = GenerateBalls(uv, mixer, wave);

    for(float i = 0.; i < 10.; i+= 1.0)
   		m += GenerateBalls(uv * (i * .75), mixer, wave);

    m = m - 1.0;// - (wave + mixer) * 1.;
    //m += 1.0;
    c = (c*m) + 1.0;


	fragColor = vec4(c,1.0);
}

float hw(float a)
{
    float c = 0.0;

    for(int i = 0; i < 64; i++)
    {
        a = floor(a);
        if(a == 0.0) break;
        c += step(0.5, mod(a, 2.0));
        a = a / 2.0;
    }

    return c;
}

