void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    float wr = 128.0;
    float hr = 64.0;
    float np = 2.0;
    float tw = 1.0;

    float it = iTime * tw;
    it = floor(it) + smoothstep(0.5, 1.0, mod(it, 1.0));

	vec2 uv = fragCoord.xy / iResolution.xy;
    vec2 ouv = uv;
    uv.y = 1.0 - uv.y - 0.5;
    uv.x *= wr;
    float bw = hw(uv.x);

    uv.x += floor(uv.y * hr) * it;
    uv.x = hw(uv.x);
    uv.x += bw * step(0.5, ouv.x);
    uv.x = mod(uv.x, np);
    uv.x /= (np - 1.0);
	fragColor = vec4(uv.xxx, 1.0);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy / iResolution.xy;

    float f3 = 1.0 - abs(uv.y + sin(uv.x * 3.14159 * 4.0 + iTime)*0.5 - 0.5);
    float f2 = 1.0 - abs(uv.y + sin(uv.x * 3.14159 * 3.0 + iTime)*0.5 - 0.5);
    float f = 1.0 - abs(uv.y + sin(uv.x * 3.14159 * 2.0 + iTime)*0.5 - 0.5);
    vec4 color = vec4(pow(f,3.0)*pow(f2,30.0), f*f*f*f2, pow(f3,4.0) * f2 * f,1);

    fragColor = color;
}

