const float tau = 6.2831;

// Feel free to change these:
const float magnification = 3.0;
const float periodTimeInSeconds = 10.0;
const int waveCount = 15; // odd numbers are more interesting
const bool stepped = false;

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = (fragCoord.xy - iResolution.xy / 2.0) / iResolution.y * 1000.0;
    float totalIntensity = 0.0;
	float recipWaveCount = 1.0 / float(waveCount);
    float angle = tau * recipWaveCount;
    mat2 rotation = mat2(cos(angle), sin(angle), -sin(angle), cos(angle));
    vec2 direction = vec2(0, 1);
   	for (int i = 0; i < waveCount; i++)
    {
        float spacePattern = dot(uv, direction) / magnification;
        float timePattern = tau * iTime / periodTimeInSeconds;
       	totalIntensity += sin(timePattern + spacePattern);
        direction *= rotation;
    }
    totalIntensity *= recipWaveCount;
    float smoothing = 0.02;
    float steppedIntensity = smoothstep(-smoothing, smoothing, totalIntensity);
    vec3 finalColor = vec3(stepped ? steppedIntensity : totalIntensity);
	fragColor = vec4(finalColor,1.0);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 xy = fragCoord.xy / iResolution.xy; 
    vec4 texColor = texture(iChannel0,xy); 

    float timeBit = abs(sin(iTime / 4.0));

    float gray = dot(texColor.rgb, vec3(0.299, 0.587, 0.114));
    gray = (gray - 0.5) * 2.0;
    if (gray < 0.0) {
     	gray += 1.0;   
    }

    float redShift = gray + timeBit; 
    if (redShift > 1.0) {
     	redShift -= 1.0;   
    }

    texColor.r = 0.0;
    texColor.g = redShift;
    texColor.b = redShift;
        ;
    fragColor = texColor; 

}

#define mul(a, b) vec2(a.x * b.x - a.y * b.y, a.x * b.y + a.y * b.x)

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // zoom
    float z = 1.0 - (float(iTime) / 10.0);
    // slightly translate the view
    float t = -0.05* float(iTime);
    // map -2 to 1 on X Axis and 1 to -1 on Y Axis to iResolution
    vec2 coord = vec2(fragCoord.x / iResolution.x * (3.0 * z) - (2.0 * z) + t, fragCoord.y / iResolution.y * (2.0 * z) - (1.0 * z) + t);  
    // z0 = 0
    vec2 cm = vec2(0, 0);
    int j = 0;
    for(int i = 0; i < 25; i++){
        j++;
        // zn = zn-1 + c
        cm = mul(cm, cm);
    	cm = cm + coord;
        if (dot(cm,cm) > 4.0)
        	break;
    }
    // grayscale
    fragColor = vec4(j) / 24.0;
}

    #define lineRot(a) mat2( sin(a), cos(a), cos(a), -sin(a))

float line(vec2 uv)   
{
    float a = iTime;
    uv -= 1.0;
    uv =  lineRot(iTime*0.1) * uv;
   	float v = 1.5* abs(uv.y );

    return pow(v, 0.5);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy / iResolution.xy;
	vec2 nuv = fragCoord.xy / iResolution.xy;
    uv.x *= iResolution.x / iResolution.y;

    float n = 0.09;
    uv.x = n * round(uv.x /n);

    vec2 delta = lineRot(iTime*0.03) * vec2(0.2, 0.2);

    //uv.y += round(sin(uv.x * 4.0 + iTime) * 2.0 + sin(uv.x* 84.0+ iTime* 6.0)) / 10.0;
    uv.y = fract(uv.y/2.0  ) * 4.0 - 1.0;
    uv.y = round(uv.y / n)*n;


    uv.y += nuv.x;

    if( sin( 10.0 * lineRot(iTime*0.1) * (uv - 1.0) + iTime ). x > 0.0)
    {
     	delta = vec2(0.0);   
    }


    vec3 col;
    col.r = line(uv - delta);
    col.g = line(uv);
    col.b = line(uv + delta);

    col = pow(col, vec3(4.0));
    fragColor = vec4(col, 1.0);

}

#define WINGNUM 4.
#define CAVATURE 20.

void drawMisc(out vec4 fragColor, in vec2 uv ,vec2 center,float freq){

    vec2 p= uv-center;
    float radius=length(p);
    float angle=atan(p.y,p.x);

    float wings=sin(WINGNUM*angle+CAVATURE*radius*sin(iTime)+CAVATURE*cos(iTime));


    float wingMdf=cos(5.*radius);


    radius=0.3/radius*clamp(wings,.3,1.)*wingMdf;


	fragColor = vec4(radius*sin(iTime),radius*freq*cos(iTime),.2*radius,radius);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
