float AAradius=0.0f;
float AAstep=0.5f;
bool julia=false;

int calc(float Cre, float Cim, float Zr, float Zi, int maxiter, float bailout, out float col)
{
    //iter
    float Zre = Zr;
    float Zim = Zi;
    int iterCnt = 0;
    while (Zre * Zre + Zim * Zim < bailout && iterCnt < maxiter)
    {
        float ZreTmp = Zre * Zre - Zim * Zim + Cre;
        Zim = (2.0f * Zre * Zim + Cim);
        Zre = (ZreTmp);
        iterCnt++;
    }

    float fractIter = float(iterCnt);
    if (iterCnt < maxiter)
    {
        float log_zn = log(Zre * Zre + Zim * Zim) / 2.0f;
        float nu = log(log_zn / log(2.0f)) / log(2.0f);
        fractIter = fractIter + 1.0f - nu;
    }

    col = fractIter;
    return iterCnt;
}

