    float velocity = 20.0 * (1.0 + sin((M_PI * iTime) / 5.0));

    if(mod(Dist, 20.0) < 10.0 && Dist < 120.0)
        fragColor = clamp((mod(Angle + angle, M_PI) / 4.0) - (1.0 - velocity / 20.0), 0.0, 1.0) * (20.0 / velocity) * vec4(fragCoord / iResolution.xy, 0.5+0.5*sin(iTime), 1.0);
