    mat3 r = mat3(1., 0, 0, 0, c, -s, 0, s, c) * mat3(c, 0, s, 0, 1, 0, -s, 0, c);

    // ---- positions 
    float ptmp = mod(t, 5.) / 12. + 1.49;
