    for (int i = 0; i < 8; i++)
        ro += (length(
            (sin(ro * sin(ro) * r) - n) + (cos(t * .001) - .5) 
             / .5 + atan(ro.z * 1e-8 - exp(1e8 * t), -rd.z)) - 0.9) * rd;

