vec3 p1 = vec3(-1.2,0.0,0.0);
float r1 = 1.0;
float alpha1 = 0.9;
float F01 = 0.1;
vec3 diffC1 = vec3(1.0,0.2,0.2);
vec3 speC1 = vec3(1.0,1.0,1.0);

vec3 p2 = vec3(1.2,0.0,0.0);
float r2 = 1.0;
float alpha2 = 0.2;
float F02 = 1.95;
vec3 diffC2 = vec3(0.5,0.3,0.05);
vec3 speC2 = 0.5*vec3(0.8,0.6,0.1);

float floorH = -1.5;
float alphafloor = 0.3;
float F0floor = 0.2;
vec3 diffCfloor = vec3(0.2,0.5,0.7);
vec3 speCfloor = vec3(0.9,0.9,0.9);

float backZ = 5.0;
float alphaback = 0.5;
float F0back = 0.8;
vec3 diffCback = vec3(0.1,0.5,0.1);
vec3 speCback = vec3(0.2,0.2,0.2);


float D(vec3 wh, vec3 n, float alphap){
    float alpha2 = alphap*alphap;
    float nwh2 = pow(dot(n,wh),2.0);
    //return alpha2/(PI*pow(1.0+(alpha2-1.0)*pow(dot(n,wh),2.0),2.0));
    return exp((nwh2-1.0)/(alpha2*nwh2))/(PI*alpha2*pow(nwh2,2.0));
}

float F(vec3 wi, vec3 wh, float F0p){
    return F0p+(1.0-F0p)*pow(1.0-max(0.0,dot(wi,wh)),5.0);
}

float G(vec3 wo, vec3 wi, vec3 wh, vec3 wn){
 	return min(1.0,min(2.0*dot(wn,wh)*dot(wn,wi)/dot(wo,wh),2.0*dot(wn,wh)*dot(wn,wo)/dot(wo,wh)));
}

vec3 diffusemap(vec3 r){
    if(r.y-floorH<0.01){
     	return diffCfloor;   
    }
    if(backZ-r.z<0.01){
     	return diffCback;   
    }
    if(length(r-p1)<length(r-p2)){
        return diffC1;
    }else{
        return diffC2;
    }
}

vec3 specularmap(vec3 r){
    if(r.y-floorH<0.01){
     	return speCfloor;   
    }
    if(backZ-r.z<0.01){
     	return speCback;   
    }
    if(length(r-p1)<length(r-p2)){
        return speC1;
    }else{
        return speC2;
    }
}

float alphamap(vec3 r){
    if(r.y-floorH<0.01){
     	return alphafloor;   
    }
    if(backZ-r.z<0.01){
     	return alphaback;   
    }
    if(length(r-p1)<length(r-p2)){
        return alpha1;
    }else{
        return alpha2;
    }
}

float fmap(vec3 r){
    if(r.y-floorH<0.01){
     	return F0floor;   
    }
    if(backZ-r.z<0.01){
     	return F0back;   
    }
    if(length(r-p1)<length(r-p2)){
        return F01;
    }else{
        return F02;
    }
}

float map(vec3 r){
    return min(length(r-p1)-r1,
               min(length(r-p2)-r2,
               min(r.y-floorH,
               backZ-r.z)));
}

vec3 nmap(vec3 r){
    if(r.y-floorH<0.01){
     	return vec3(0.0,1.0,0.0);   
    }
    if(backZ-r.z<0.01){
     	return vec3(0.0,0.0,-1.0);   
    }
    if(length(r-p1)<length(r-p2)){
        return normalize(r-p1);
    }else{
        return normalize(r-p2);
    }
}

float trace(vec3 o, vec3 r){
    r=normalize(r);
    float t = 0.1;
    for(int i=0; i<96; i++){
        float d = map(o+t*r);
       if(abs(d)<0.005 || abs(d)>10.0) break;
        t+=d*0.95;
    }
    return t;
}


vec3 specdif(vec3 p, vec3 wo, vec3 lightp, vec3 lightc){
    vec3 col = vec3(0.0,0.0,0.0);

    vec3 wn = nmap(p);
    float alphap = alphamap(p);
    float F0p = fmap(p);
    vec3 diffC = diffusemap(p);
    vec3 specularC = specularmap(p);

    float d = trace(p,lightp-p);
    vec3 wi = normalize(lightp-p);
    vec3 wh = normalize(wi+wo);
    if(d>length(lightp-p) && dot(wi,wn)>0.0 && dot(wo,wn)>0.0){
        col+=dot(wi,wn)/3.0*lightc*diffC;
        col+=D(wh,wn,alphap)*F(wi,wh,F0p)*G(wo,wi,wh,wn)/(4.0*dot(wn,wi)*dot(wn,wo)+1.0)*lightc*specularC;
        //col=vec3(G(wo,wi,wh,wn));
    }
    return col;
}

