    float gearSDF = smoothstep(-0.556,0.8, cos(uvp.y*6. + impulsedSpeed))*0.15+0.5;

    // Determine gear body.
    float gearMask = 1.0 - smoothstep(rad-0.008, rad, gearSDF*uvp.x);

    // Cutout the center hole.
    float holeRad = rad * 0.95;
    float holeMask = 1.0 - smoothstep(holeRad-0.01, holeRad, uvp.x);
    float gearMaskMinHole = gearMask - holeMask; // Cut hole.
    col = mix(col, gearCol, gearMaskMinHole);

    // Determine gear outline.
    float outlineRad = rad + 0.012;
    float gearOutlineMask = abs(
        (1.0 - smoothstep(outlineRad-0.008, outlineRad, gearSDF*uvp.x)) - gearMask
    );
    float holeOutlineRad = holeRad - 0.03;
    float holeOutlineMask = abs(
        (1.0 - smoothstep(holeOutlineRad-0.01, holeOutlineRad, uvp.x)) - holeMask
    );
    col = mix(col, gearOutlineCol, gearOutlineMask + holeOutlineMask);

    //return mix(vec4(0.), gearOutlineCol, gearOutlineMask); // Debug outline..
    return col;
}

