    vec3 d =  abs(vec3(q.x,p.y-0.5,q.z))-b;
    //vec3 d = abs(fract(p)*2.0-1.0)-b; 
    return min(max(d.x,max(d.y,d.z)),0.0) + length(max(d,0.0));
