float map(vec3 p)
{
    float result = opU( plane(p), cube(p+vec3(0.0,-1.0,-4.0) , vec3(1.0,5.6,1.0))  );
          result = opU( result  , cube(p+vec3(4.0,-1.0,-4.0) , vec3(1.0,4.6,1.0))  );
    	  result = opU( result  , cube(p+vec3(-4.0,-1.0,-4.0), vec3(1.0,3.8,1.0)) );

    	  result = opU( result  , cube(p+vec3(0.0,-1.0,4.0) , vec3(1.0,6.6,1.0))  );
          result = opU( result  , cube(p+vec3(4.0,-1.0,4.0) , vec3(1.0,4.2,1.0))  );
    	  result = opU( result  , cube(p+vec3(-4.0,-1.0,4.0), vec3(1.0,5.2,1.0)) );

    	  result = opU( result  ,    fractalBox( p+vec3(0.0,0.5,0.0), vec3(0.480,0.015,0.480) ) );

    	  result = opS( result  , sphere(p+vec3(-sin(iTime*1.25)*4.0,-1.0,4.0), 1.0*-sin(iTime*5.+5.)+0.5) );
          result = opS( result  , sphere(p+vec3(sin(iTime*1.25)*4.0,-1.0,-4.0), 1.25) );

	return result;
}

float rayMarch(vec3 o, vec3 r)
{
    float t = 0.0;
    for(int i = 0; i<raySteps; i++)
    {
        float precis = 0.00001*t;
        vec3 p = o+r*t;
        float d  = map(p);
        //float d1 = map(p+vec3(0.0,0.0,4.0));
        //float f = opU(d,d1);
        if( d < precis || t>1000. ) break;
        t += d;
    }
    return t;
}
vec3 calcNormal( in vec3 pos )
{
    vec2 e = vec2(1.0,-1.0)*0.5773*0.0005;

    return normalize( e.xyy*map( pos + e.xyy ) + 
					  e.yyx*map( pos + e.yyx ) + 
					  e.yxy*map( pos + e.yxy ) + 
					  e.xxx*map( pos + e.xxx ) );
    /*
	vec3 eps = vec3( 0.0005, 0.0, 0.0 );
	vec3 nor = vec3(
	    map(pos+eps.xyy).x - map(pos-eps.xyy).x,
	    map(pos+eps.yxy).x - map(pos-eps.yxy).x,
	    map(pos+eps.yyx).x - map(pos-eps.yyx).x );
	return normalize(nor);
	*/
}
vec3 render( in vec3 ro, in vec3 rd )
{ 
    vec3 col = vec3(0.5, 0.5, 0.5);
    float res = rayMarch(ro,rd);

    vec3 pos = ro + res*rd;
    vec3 nor = calcNormal( pos );

    vec3  lig = normalize( sunDirection );
	float amb = clamp( 1.0*nor.y, 0.0, 1.0 );
    float dif = clamp( dot( nor, lig ), 0.0, 1.0 );
    float bac = clamp( dot( nor, normalize(vec3(-lig.x,0.0,-lig.z))), 0.0, 1.0 )*clamp( 1.0-pos.y,0.0,1.0);
    float dom = smoothstep( -0.1, 0.1, dif );

    vec3 lin = vec3(0.0);
         lin += 1.00*dif*sunColour;
    	 lin += 0.50*amb;
         lin += 0.50*dom;
         lin += 0.50*bac;
    	 col = col*lin;

    	col = mix( col, fogColour, 1.0-exp( -0.001*res*res*res ));

    return vec3( clamp(col,0.0,1.0) );
}
