vec4 plas( vec2 v, float time )
{
	float c = sin(v.y*50.0)/cos(v.x*100.0)*cos(iTime*1.0);
	c /= cos(v.y*50.0)/sin(v.x*50.0);
	float ci = cos(v.y*25.0)/sin(v.x*25.0);
	c += ci;

	return vec4( c*sin(iTime), c*cos(iTime)/2., c+sin(iTime), 1.0 );
}

vec4 grid( vec2 v, float time )
{
  float c =(v.x)/(v.y);

  return vec4( c*sin(iTime), c*cos(iTime)/2., c+sin(iTime), 1.0 );
}



vec2 rotate(vec2 v, float a)
{
	float s = cos(a*v.y/10.+iTime/2.);
	float c = sin(a+v.x*3.-iTime/2.);
	mat2 m = mat2(c, -s, s, c);
	return m * v;
}


