    add_field(surface2x2_eval(slice_x1, get_origin())*0.3);
    stroke();

    move_to(0.0, 0.0);
	line_to(2.0,v[1]*2.0);
    move_to(0.0, 0.0);
	line_to(2.0,v[0]*2.0);
    stroke();

#define DRAW_Z(N) move_to(N, v[0]*N); line_to(N, v[1]*N); stroke();
    DRAW_Z(range[0]);
    DRAW_Z(range[1]);
    set_source_rgba(1.0,1.0,1.0,0.5);
    DRAW_Z(range[2]);
    DRAW_Z(range[3]);

    set_source_rgb(hsl(0.06,1.0,0.6));
    circle(bounds.v0.zy, rdot * 2.0);
    fill();
    set_source_rgb(hsl(0.25,1.0,0.6));
    circle(bounds.eh0_0.zy, rdot * 2.0);
    circle(bounds.eh1_0.zy, rdot * 2.0);
    circle(bounds.ev0_0.zy, rdot * 2.0);
    circle(bounds.ev1_0.zy, rdot * 2.0);
    fill();
    set_source_rgb(hsl(0.6,1.0,0.6));
    circle(bounds.c00_0.zy, rdot * 2.0);
    circle(bounds.c01_0.zy, rdot * 2.0);
    circle(bounds.c10_0.zy, rdot * 2.0);
    circle(bounds.c11_0.zy, rdot * 2.0);
    fill();
    set_source_rgb(hsl(0.06,1.0,0.6));
    circle(bounds.v1.zy, rdot);
    fill();
    set_source_rgb(hsl(0.25,1.0,0.6));
    circle(bounds.eh0_1.zy, rdot);
    circle(bounds.eh1_1.zy, rdot);
    circle(bounds.ev0_1.zy, rdot);
    circle(bounds.ev1_1.zy, rdot);
    fill();
    set_source_rgb(hsl(0.6,1.0,0.6));
    circle(bounds.c00_1.zy, rdot);
    circle(bounds.c01_1.zy, rdot);
    circle(bounds.c10_1.zy, rdot);
    circle(bounds.c11_1.zy, rdot);
    fill();

}

