	vec3 rd = normalize(vec3(uv * r2d(iTime*.6), -1));

    // mode 2
    vec3 ro = vec3(cos(iTime), sin(iTime), -iTime*10.), p;
    //vec3 rd = normalize(vec3(uv, -1));


	p = ro;
	int ri;
	for (int i = 0; i < 100; i++) {
		ri = i;
		float d = map(p);
		if (d < .001) break;
		p += d * rd;
	}

    // b&w simple render
	//fragColor = vec4(length(ro - p) * .1);

