vec3 ltDir, qnBlk[2], bSize, vnBall;
float dstFar, tCur, tWav;
int idObj;
const float pi = 3.14159;

float ObjDf (vec3 p)
{
  vec3 q;
  float dMin, d;
  dMin = dstFar;
  q = p;  q.y -= bSize.y - 0.15;
  d = PrSphDf (q, 0.15);
  if (d < dMin) { dMin = d;  idObj = 1; }
  q = p;
  q.z = abs (q.z) - bSize.z;
  d = max (PrBoxDf (q, vec3 (bSize.xy - 0.1, 0.03)), - PrBox2Df (q.xy, bSize.xy - 0.15));
  if (d < dMin) { dMin = d;  idObj = 2; }
  q = p;
  q.x = abs (q.x) - bSize.x;
  d = max (PrBoxDf (q, vec3 (0.03, bSize.yz - 0.1)), - PrBox2Df (q.zy, bSize.zy - 0.15));
  if (d < dMin) { dMin = d;  idObj = 2; }
  return dMin;
}

float ObjRay (vec3 ro, vec3 rd)
{
  float dHit, d;
  dHit = 0.;
  for (int j = 0; j < 60; j ++) {
    d = ObjDf (ro + dHit * rd);
    dHit += d;
    if (d < 0.001 || dHit > dstFar) break;
  }
  return dHit;
}

vec3 ObjNf (vec3 p)
{
  vec4 v;
  vec3 e = vec3 (0.001, -0.001, 0.);
  v = vec4 (ObjDf (p + e.xxx), ObjDf (p + e.xyy), ObjDf (p + e.yxy), ObjDf (p + e.yyx));
  return normalize (vec3 (v.x - v.y - v.z - v.w) + 2. * v.yzw);
}

vec2 BlkHit (vec3 ro, vec3 rd, vec3 bSize)
{
  vec3 v, tm, tp;
  float dMin, dn, df;
  if (rd.x == 0.) rd.x = 0.001;
  if (rd.y == 0.) rd.y = 0.001;
  if (rd.z == 0.) rd.z = 0.001;
  v = ro / rd;
  tp = bSize / abs (rd) - v;
  tm = - tp - 2. * v;
  dn = max (max (tm.x, tm.y), tm.z);
  df = min (min (tp.x, tp.y), tp.z);
  dMin = dstFar;
  if (df > 0. && dn < df) {
    dMin = dn;
    qnBlk[0] = - sign (rd) * step (tm.zxy, tm) * step (tm.yzx, tm);
    qnBlk[1] = - sign (rd) * step (tp, tp.zxy) * step (tp, tp.yzx);
  }
  return vec2 (dMin, df);
}

float BallHit (vec3 ro, vec3 rd)
{
  vec3 p, u;
  float b, d, w, dMin, rad;
  dMin = dstFar;
  rad = 0.2 * bSize.y;
  p = vec3 (0., - bSize.y + 2. * rad, 0.);
  u = ro - p;
  b = dot (rd, u);
  w = b * b - dot (u, u) + rad * rad;
  if (w > 0.) {
    d = - b - sqrt (w);
    if (d > 0. && d < dMin) {
      dMin = d;
      vnBall = (u + d * rd) / rad;
    }
  }
  return dMin;
}

float WaveHt (vec2 p)
{
  mat2 qRot = mat2 (0.8, -0.6, 0.6, 0.8);
  vec4 t4, v4;
  vec2 t;
  float wFreq, wAmp, ht;
  wFreq = 1.;
  wAmp = 1.;
  ht = 0.;
  for (int j = 0; j < 3; j ++) {
    p *= qRot;
    t = tWav * vec2 (1., -1.);
    t4 = (p.xyxy + t.xxyy) * wFreq;
    t = vec2 (Noisefv2 (t4.xy), Noisefv2 (t4.zw));
    t4 += 2. * t.xxyy - 1.;
    v4 = (1. - abs (sin (t4))) * (abs (sin (t4)) + abs (cos (t4)));
    ht += wAmp * dot (pow (1. - sqrt (v4.xz * v4.yw), vec2 (8.)), vec2 (1.));
    wFreq *= 2.;
    wAmp *= 0.5;
  }
  return ht;
}

vec3 WaveNf (vec2 p)
{
  vec3 vn;
  vec2 e = vec2 (0.01, 0.);
  p *= 0.4;
  vn.xz = 0.2 * (WaveHt (p) - vec2 (WaveHt (p + e.xy), WaveHt (p + e.yx)));
  vn.y = e.x;
  return normalize (vn);
}

vec3 ShowScene (vec3 ro, vec3 rd)
{
  vec3 vn, vnb, col, ltDirB;
  vec2 dBlock, q;
  float dstObj, dstBall, spec, glit;
  tWav = 0.1 * tCur + 10.;
  ltDirB = ltDir;
  dstBall = BallHit (ro, rd);
  if (dstBall < dstFar) {
    ro += dstBall * rd;
    rd = reflect (rd, vnBall);
    ltDirB = reflect (ltDirB, vnBall);
    ro += 0.01 * rd;
  }
  dBlock = BlkHit (ro, rd, bSize);
  dstObj = ObjRay (ro, rd);
  glit = 0.;
  spec = 0.1;
  if (dstObj < dBlock.y) {
    ro += rd * dstObj;
    vn = ObjNf (ro);
    if (idObj == 1) col = vec3 (1., 1., 0.9) * (0.6 - 0.4 * dot (rd, vn));
    else if (idObj == 2) col = vec3 (0.3, 0.2, 0.);
  } else {
    vnb = qnBlk[1];
    ro += rd * dBlock.y;
    if (vnb.y > 0.) {
      vn = vnb;
      col = mix (vec3 (0.8, 0.4, 0.2), vec3 (0.5, 0.25, 0.1),
         Fbm2 (vec2 (50., 5.) * ro.xz)) *
         (0.6 + 0.4 * smoothstep (0.03, 0.08, mod (10. * ro.x, 1.)));
      col *= 0.6 + 0.4 * smoothstep (0.05, 0.15, length (ro.xz) / bSize.x);
    } else if (vnb.y < 0.) {
      col = vec3 (0.7, 0.7, 0.8) * (0.7 + 0.3 * smoothstep (0., 0.15, length (ro.xz) / bSize.x));
      vn = VaryNf (50. * ro, vnb, 1.);
    } else {
      q = (abs (vnb.z) == 1.) ? ro.xy : ro.zy;
      if (abs (q.x) > bSize.x - 0.15 || abs (q.y) > bSize.y - 0.15) {
        col = vec3 (0.6, 0.6, 0.55);
        vn = VaryNf (50. * ro, vnb, 1.);
      } else {
        col = vec3 (0.8, 0.6, 0.1);
        glit = 1.;
        if (abs (vnb.z) == 1.) {
          vn = WaveNf (10. * q).xzy;
          vn.z *= sign (vnb.z);
        } else {
          vn = WaveNf (10. * q).yxz;
          vn.x *= sign (vnb.x);
        }
      }
    }
    if (glit > 0.) {
      glit = 1000. * step (0.01, max (0., dot (vn, ltDirB))) *
         pow (max (0., dot (ltDirB, reflect (rd, vn))), 16.) *
         pow (1. - 0.6 * abs (dot (normalize (ltDirB - rd), VaryNf (1000. * ro, vn, 2.))), 8.);
      col = glit * vec3 (1., 1., 0.5) + col * (0.2 + 0.8 * max (dot (vn, ltDirB), 0.));
    } else {
      col = col * (0.5 + 0.5 * max (dot (vn, ltDir), 0.)) +
         spec * pow (max (0., dot (ltDir, reflect (rd, vn))), 32.);
    }
  }
  return clamp (col, 0., 1.);  
}

void mainImage (out vec4 fragColor, in vec2 fragCoord)
{
  mat3 vuMat;
  vec4 mPtr;
  vec3 ro, rd;
  vec2 canvas, uv, ori, ca, sa;
  float el, az;
  canvas = iResolution.xy;
  uv = 2. * fragCoord.xy / canvas - 1.;
  uv.x *= canvas.x / canvas.y;
  tCur = iTime;
  mPtr = iMouse;
  mPtr.xy = mPtr.xy / canvas - 0.5;
  dstFar = 5.;
  bSize = vec3 (2., 1., 2.);
  ro = vec3 (0., 0., -0.9 * bSize.z);
  rd = normalize (vec3 (uv, 3.));
  az = 0.;
  el = 0.;
  if (mPtr.z > 0.) {
    az += 2. * pi * mPtr.x;
    el += 0.7 * pi * mPtr.y;
  }
  el = clamp (el, -0.25 * pi, 0.25 * pi);
  ori = vec2 (el, az);
  ca = cos (ori);
  sa = sin (ori);
  vuMat = mat3 (ca.y, 0., - sa.y, 0., 1., 0., sa.y, 0., ca.y) *
          mat3 (1., 0., 0., 0., ca.x, - sa.x, 0., sa.x, ca.x);
  rd = vuMat * rd;
  ro = vuMat * ro;
  ltDir = normalize (vec3 (0., -0.5, -1.));
  ltDir = vuMat * ltDir;
  fragColor = vec4 (ShowScene (ro, rd), 1.);
}

float PrBoxDf (vec3 p, vec3 b)
{
  vec3 d;
  d = abs (p) - b;
  return min (max (d.x, max (d.y, d.z)), 0.) + length (max (d, 0.));
}

float PrBox2Df (vec2 p, vec2 b)
{
  vec2 d;
  d = abs (p) - b;
  return min (max (d.x, d.y), 0.) + length (max (d, 0.));
}

float PrSphDf (vec3 p, float r)
{
  return length (p) - r;
}

const float cHashM = 43758.54;

float Hashfv2 (vec2 p)
{
  return fract (sin (dot (p, vec2 (37., 39.))) * cHashM);
}

vec2 Hashv2v2 (vec2 p)
{
  vec2 cHashVA2 = vec2 (37., 39.);
  return fract (sin (vec2 (dot (p, cHashVA2), dot (p + vec2 (1., 0.), cHashVA2))) * cHashM);
}

float Noisefv2 (vec2 p)
{
  vec2 t, ip, fp;
  ip = floor (p);  
  fp = fract (p);
  fp = fp * fp * (3. - 2. * fp);
  t = mix (Hashv2v2 (ip), Hashv2v2 (ip + vec2 (0., 1.)), fp.y);
  return mix (t.x, t.y, fp.x);
}

float Fbm2 (vec2 p)
{
  float f, a;
  f = 0.;
  a = 1.;
  for (int i = 0; i < 5; i ++) {
    f += a * Noisefv2 (p);
    a *= 0.5;
    p *= 2.;
  }
  return f * (1. / 1.9375);
}

float Fbmn (vec3 p, vec3 n)
{
  vec3 s;
  float a;
  s = vec3 (0.);
  a = 1.;
  for (int i = 0; i < 5; i ++) {
    s += a * vec3 (Noisefv2 (p.yz), Noisefv2 (p.zx), Noisefv2 (p.xy));
    a *= 0.5;
    p *= 2.;
  }
  return dot (s, abs (n));
}

vec3 VaryNf (vec3 p, vec3 n, float f)
{
  vec3 g;
  vec3 e = vec3 (0.1, 0., 0.);
  g = vec3 (Fbmn (p + e.xyy, n), Fbmn (p + e.yxy, n), Fbmn (p + e.yyx, n)) - Fbmn (p, n);
  return normalize (n + f * (g - n * dot (n, g)));
}

