// Constants.
#define STEPS 64
#define EPS 0.01
#define FAR 10.0
#define PI 3.14159265359


mat2 rotate(float a) { 
    return mat2( cos(a), sin(a), -sin(a), cos(a) );
}


float sdBox( vec3 p, vec3 b )
{
  vec3 d = abs(p) - b;
  return min(max(d.x,max(d.y,d.z)),0.0) + length(max(d,0.0));
}

// Function of distance.
float map( vec3 p ) {
    p += vec3(0,-1,0); 
    p.xz *= rotate(iTime); 
    return sdBox(p,vec3(1.1));
   }

// Gradient (numeric) function of the distance function.
vec3 grad(vec3 p) {
    vec2 q = vec2(0.0, EPS);

    return vec3(map(p + q.yxx) - map(p - q.yxx),
                map(p + q.xyx) - map(p - q.xyx),
                map(p + q.xxy) - map(p - q.xxy));
}

vec3 shade(vec3 ro, vec3 rd, float t) {
	vec3 n = normalize(grad(ro + t*rd));
    return vec3(0.3, 0.8, 0.7)*pow(1.0-dot(-rd, n), 1.5);
}



void mainImage( out vec4 fragColor, in vec2 fragCoord ) {
    // Prepare the radius.
	vec2 uv = (-iResolution.xy + 2.0*fragCoord.xy) / iResolution.y;



    vec3 ro = vec3(0.0, 1.0, 3.0); // start of the radius.
    vec3 rd = normalize(vec3(uv, -1.0)); // direction of the radius.

    // Loop do raymarcher.
    float t = 0.0, d = EPS;
    for (int i = 0; i < STEPS; ++i) {
        d = map(ro + t*rd);
        if (d < EPS || t > FAR) break;
        t += d;
    }

    // Shading.
    vec3 col = d < EPS ? shade(ro, rd, t) : vec3(0.3, 0.6, 0.7)*(2.0-length(uv));

    // Post-processing. 
    col = smoothstep(0.0, 1.0, col);
    col = pow(col, vec3(0.45));

	fragColor = vec4(col,1.0);
}

float circle(vec2 uv, vec2 pos, float r){
	return smoothstep(r, r-.01, length(uv -pos) );
}

float band(float uv, float start, float end){
   return smoothstep(start, start, uv)*(1.-smoothstep(end, end, uv) );
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy / iResolution.xy;
    uv -= .5;
    uv.x *= iResolution.x/iResolution.y;



    float mask1 = circle(uv, vec2(uv.x*abs(-.7*sin(2.*iTime) ), -abs(.5*sin(2.*iTime) )+.2 ), .2);
    float rect = band(uv.x, -1., 1.) * band(uv.y, -abs(.5*sin(2.*iTime) )+.2, 1.);
    mask1 *= 1.-rect;

    float mask2 = circle(uv, vec2(uv.x*abs(-.7*sin(2.*iTime) ), -abs(.5*sin(2.*iTime) )+.2 ), .2);
    rect = 1.0-band(uv.x, -1., 1.) * band(uv.y, -abs(.5*sin(2.*iTime) )+.2, 1.);
    mask2 *= 1.-rect;

    vec3 c1 = vec3(1., 0., 0.) * mask2;
    vec3 c2 = vec3(1., 1., 1.) * mask1;

    vec3 c = c1 + c2;
	fragColor = vec4(vec3(c),1.0);
}

//	Simplex 3D Noise 
//	by Ian McEwan, Ashima Arts
//
vec4 permute(vec4 x){return mod(((x*34.0)+1.0)*x, 289.0);}
vec4 taylorInvSqrt(vec4 r){return 1.79284291400159 - 0.85373472095314 * r;}

float snoise(vec3 v){ 
  const vec2  C = vec2(1.0/6.0, 1.0/3.0) ;
  const vec4  D = vec4(0.0, 0.5, 1.0, 2.0);

// First corner
  vec3 i  = floor(v + dot(v, C.yyy) );
  vec3 x0 =   v - i + dot(i, C.xxx) ;

// Other corners
  vec3 g = step(x0.yzx, x0.xyz);
  vec3 l = 1.0 - g;
  vec3 i1 = min( g.xyz, l.zxy );
  vec3 i2 = max( g.xyz, l.zxy );

  //  x0 = x0 - 0. + 0.0 * C 
  vec3 x1 = x0 - i1 + 1.0 * C.xxx;
  vec3 x2 = x0 - i2 + 2.0 * C.xxx;
  vec3 x3 = x0 - 1. + 3.0 * C.xxx;

// Permutations
  i = mod(i, 289.0 ); 
  vec4 p = permute( permute( permute( 
             i.z + vec4(0.0, i1.z, i2.z, 1.0 ))
           + i.y + vec4(0.0, i1.y, i2.y, 1.0 )) 
           + i.x + vec4(0.0, i1.x, i2.x, 1.0 ));

// Gradients
// ( N*N points uniformly over a square, mapped onto an octahedron.)
  float n_ = 1.0/7.0; // N=7
  vec3  ns = n_ * D.wyz - D.xzx;

  vec4 j = p - 49.0 * floor(p * ns.z *ns.z);  //  mod(p,N*N)

  vec4 x_ = floor(j * ns.z);
  vec4 y_ = floor(j - 7.0 * x_ );    // mod(j,N)

  vec4 x = x_ *ns.x + ns.yyyy;
  vec4 y = y_ *ns.x + ns.yyyy;
  vec4 h = 1.0 - abs(x) - abs(y);

  vec4 b0 = vec4( x.xy, y.xy );
  vec4 b1 = vec4( x.zw, y.zw );

  vec4 s0 = floor(b0)*2.0 + 1.0;
  vec4 s1 = floor(b1)*2.0 + 1.0;
  vec4 sh = -step(h, vec4(0.0));

  vec4 a0 = b0.xzyw + s0.xzyw*sh.xxyy ;
  vec4 a1 = b1.xzyw + s1.xzyw*sh.zzww ;

  vec3 p0 = vec3(a0.xy,h.x);
  vec3 p1 = vec3(a0.zw,h.y);
  vec3 p2 = vec3(a1.xy,h.z);
  vec3 p3 = vec3(a1.zw,h.w);

//Normalise gradients
  vec4 norm = taylorInvSqrt(vec4(dot(p0,p0), dot(p1,p1), dot(p2, p2), dot(p3,p3)));
  p0 *= norm.x;
  p1 *= norm.y;
  p2 *= norm.z;
  p3 *= norm.w;

// Mix final noise value
  vec4 m = max(0.6 - vec4(dot(x0,x0), dot(x1,x1), dot(x2,x2), dot(x3,x3)), 0.0);
  m = m * m;
  return 42.0 * dot( m*m, vec4( dot(p0,x0), dot(p1,x1), 
                                dot(p2,x2), dot(p3,x3) ) );
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy / iResolution.xy;
    float scale = .2;
    float rate = 5.0;

    float t = iTime/rate;

    float result = 0.0;

    //octaves
    for (float i = 0.0; i < 5.0; i++){
    	result += snoise(vec3((uv.x*2.0)/scale, (uv.y)/scale, t*2.0))/pow(2.0, i);
        scale /= 2.0;
    }
    result = (result + 1.0)/4.0;
    result += .5;

    result = pow(result, 2.0);


    //powers for steeper curves
    //float p1 = pow(fragCoord.y/iResolution.y, 1.7);
    //float p2 = 8.0*(1.0 - p1);
    //result = pow(result, 8.0 - p2);

    //power for coloring
    float g = pow(result, 4.0);
    fragColor = vec4(0, g, result, 1.0);
}

const float tau = 6.28318;
const float inf = 99999.0;

const bool curves = true;
