float qbCurveHit(vec3[curveLen] pts, int n, float r, vec3 ro, vec3 rd, mat3x3 cam) {
    float minT = inf;
    vec3 a, c;
    for (int i = 2; i < n; i++) {
        a = 0.5 * (pts[i-2] + pts[i-1]);
        c = 0.5 * (pts[i-1] + pts[i]);
        float t = qbHit(a, pts[i-1], c, r, ro, rd, cam);
        if (t >= 0.0 && t < minT) {
            minT = t;
            return minT;// debug -- bail early on first hit. remove when t is real
        }
    }
    return minT;
}

vec3 trace1( mat3x3 cam, vec3 ro, vec3 rd, float time )
{
    vec3 color;
    const int ns = 10;
    float fns = float(ns);
    float mint = 9999.0;
    float sr = 1.0;

    for (int i = 0; i < ns; i++) 
    {
        float fi = float(i);
        float fni = fi / fns;
        float d = 3.0 * sin(time * fni + fi);
        vec3 sc=vec3(d*cos(tau*fni+time*10.0*fni)+sin(time*10.0), 
                     d*sin(tau*fni+time*10.0*fni), 
                     d*sin(0.5*tau*fni+time*fni*1.4142)+cos(time*10.0));

        float t = sphereHit(sc, sr, ro, rd);
        if (t > mint) continue;
        mint = t;

        vec3 hit = rd * t + ro;
        vec3 normal = sphereNormal(sc, sr, hit);

        float dup    = 1.0 * dot(normal, cam[1]) + 0.0;
        float ddown  = 1.0 * dot(normal, -cam[1]) + 0.0;
        float dright = 1.0 * dot(normal, cam[0]) + 0.0;
        float dfwd   = 1.0 * dot(normal, -cam[2] ) + 0.0;
        float z = mod(2.0 * /*length(hit)*/hit.z, 1.0);

        vec3 refl = reflect(rd, normal);

        color = vec3(dright, dup, dfwd);
        //color = vec4(z, z, z);

        // specular
        float spec = pow(dot(refl, cam[1]), 20.0);
        if (spec > 0.0) color += vec3(spec, spec, spec);

        //color = normal;
        //float j = t/10.0; color = vec3(j, j, j);
        //color = vec3(mod(hit.x, 1.0), mod(hit.y, 1.0), 0.0);
    }

    if (mint > 999.0)
    {
        color = vec3(0.0, 0.75+0.25*sin(iTime), 1.0);
    }
    return color;
}

vec3 trace0( mat3x3 cam, vec3 ro, vec3 rd, float time )
{
    vec3 color;
    const int ns = 10;
    float fns = float(ns);
    float mint = 9999.0;
    float sr = 1.0;

    if (spheres) for (int i = 0; i < ns; i++) 
    {
        float fi = float(i);
        float fni = fi / fns;
        float d = 3.0 * sin(time * fni + fi);
        vec3 sc=vec3(d*cos(tau*fni+time*10.0*fni)+sin(time*10.0), 
                     d*sin(tau*fni+time*10.0*fni), 
                     d*sin(0.5*tau*fni+time*fni*1.4142)+cos(time*10.0));

        float t = sphereHit(sc, sr, ro, rd);
        if (t > mint) continue;
        mint = t;

        vec3 hit = rd * t + ro;
        vec3 normal = sphereNormal(sc, sr, hit);

        float dup    = 1.0 * dot(normal, cam[1]) + 0.0;
        float ddown  = 1.0 * dot(normal, -cam[1]) + 0.0;
        float dright = 1.0 * dot(normal, cam[0]) + 0.0;
        float dfwd   = 1.0 * dot(normal, -cam[2] ) + 0.0;
        float z = mod(2.0 * /*length(hit)*/hit.z, 1.0);

        vec3 refl = reflect(rd, normal);

        color = vec3(dright, dup, dfwd);
        //color = vec4(z, z, z);
        color = 0.5 * color + 0.5 * trace1(cam, hit, -refl, time);

        // specular
        float spec = pow(dot(refl, cam[1]), 20.0);
        if (spec > 0.0) color += vec3(spec, spec, spec);

        //color = normal;
        //float j = t/10.0; color = vec3(j, j, j);
        //color = vec3(mod(hit.x, 1.0), mod(hit.y, 1.0), 0.0);
    }

    if (curves) {
        float db = 0.0;
        float dd = 1.0;

        for (int i = 0; i < curveLen; i++) {
            float fi = float(i);
            pts[i] = vec3(1.5 * sin(fi), fi*1.0 - 6.0, 1.5 * cos(fi));
        }

        int i = 0;
        for (float ox = -db; ox <= db + dd/2.0; ox += dd) {
        for (float oy = -db; oy <= db + dd/2.0; oy += dd) {
        for (float oz = -db; oz <= db + dd/2.0; oz += dd, i++) {
#if 0
            float qbt = qbHit(
                vec3(-2.0+ox, -2.0+oy, 0.0+oz), 
                vec3(-1.0+ox, 2.0+oy+2.0*sin(10.0*tau*time+float(i)), 0.0+oz), 
                vec3(2.0+ox, -2.0+oy, 0.0+oz), 
                0.05, ro, rd, cam);
#else
            float qbt = qbCurveHit(pts, curveLen, 0.05, ro, rd, cam);
            //float qbCurveHit(vec3[] pts, int n, float r, vec3 ro, vec3 rd, mat3x3 cam) {
#endif
            if (qbt < mint) {
                mint = qbt;
                color = vec3(0.0, 0.0, 0.0);
            }
        }
        }
        }
    }

    if (mint > 999.0)
    {
        color = vec3(0.0, 0.75+0.25*sin(iTime), 1.0);
    }
    return color;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    float time = (iMouse.z > 0.0) ? iMouse.x / iResolution.x :  iTime / 10.0;
    vec3 ro = vec3(10.0*sin(tau * time), 0.0, 10.0*cos(tau * time));
    vec2 uv = 2.0 * fragCoord.xy / iResolution.xy - vec2(1.0, 1.0);
    uv.x *= iResolution.x / iResolution.y;
    vec3 up = vec3(0.0, 1.0, 0.0);
    vec3 lookat = vec3(0.0, 0.0, 0.0);
    vec3 fwd   = normalize(lookat - ro);
    vec3 right = normalize(cross(fwd, up));
    vec3 camup = normalize(cross(right, fwd));
    vec3 rd = normalize(fwd + 0.5 * (uv.x * right + uv.y * up));

    fragColor = vec4(trace0(mat3x3(right, camup, fwd), ro, rd, time), 1.0);
}

float getAspectRatio()
{
    return iResolution.x / iResolution.y;
}

vec2 getUVCoords(in vec2 fragCoords)
{
    return ((fragCoords - 0.5) / (iResolution.xy - 1.0) - 0.5) * vec2(getAspectRatio(), 1.0);
}

void drawSky(in vec2 uvCoords, inout vec4 canvas)
{
    vec4 sky = vec4(1.0 - 1.4 * sqrt(uvCoords.y),
                  	0.5 - 3.7 * uvCoords.y,
                  	0.5 - 0.8 * uvCoords.y,
                  	1.0);

    canvas = clamp(sky, 0.0, 1.0); //needed due to sqrt() of negative values
}

void drawSun(in vec2 uvCoords, inout vec4 canvas)
{
    vec2 origin = vec2(0.0, 0.0);
    float radius = 0.25;
    float blurAmount = 0.4;
    vec3 color = vec3(1.0, 1.0, 0.2);
    origin.x *= getAspectRatio();
    float color_amount = 1.0 - smoothstep(radius - blurAmount, radius + blurAmount, distance(origin, uvCoords));
    canvas += vec4(color_amount * color, 0.0);
}

void drawRoad(in vec2 uvCoords, inout vec4 canvas)
{
    float s = step(0.0, uvCoords.y);
    float t = mod(iTime, 60.0); //needed hack for long-running scenes
    vec4 road = vec4(vec3(sign(sin(70.0 * pow(-uvCoords.y, 0.3) - 25.0 * t))), 1.0);

    road = clamp(road, 0.0, 1.0); //needed due to sqrt() of negative values
    road *= vec4(2.0 * (0.5 + uvCoords.y),
                 1.6 * (0.5 + uvCoords.y),
                 1.8 * (0.5 + uvCoords.y),
                 1.0);
    canvas = mix(road, canvas, s);
}

void drawSidewalk(in vec2 uvCoords, inout vec4 canvas)
{
    float s = step(0.85 * (0.015 - uvCoords.y), abs(uvCoords.x) / getAspectRatio()) * step(0.0, -uvCoords.y);
    vec4 sidewalk = vec4(0.8 * (0.5 + uvCoords.y),
                 		 0.6 * (0.5 + uvCoords.y),
                 		 1.3 * (0.5 + uvCoords.y),
                 		 1.0);

    canvas = mix(canvas, sidewalk, s);
}

void mainImage(out vec4 fragColor, in vec2 fragCoord)
{
	vec2 uv = getUVCoords(fragCoord);

    drawSky(uv, fragColor);
    drawSun(uv, fragColor);
    drawRoad(uv, fragColor);
    drawSidewalk(uv, fragColor);
}

vec2 currentCoords;

struct circle{
    vec2 m_Center;
    float m_Radius;
};

bool IsInside(circle _circle){
    float _magnitude = length(currentCoords.xy - _circle.m_Center);
    return _magnitude <= _circle.m_Radius;
}
bool IsLeft(vec2 _line){
    return cross(vec3(currentCoords, 0.0), vec3(_line, 0.0)).z >= 0.0;
}


vec2 Rotate(vec2 _vec, float _angle){
    return mat2(cos(_angle), -sin(_angle), sin(_angle), cos(_angle)) * _vec;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    currentCoords = fragCoord * 2.0 - iResolution.xy;
    vec2 nResolution = iResolution.xy * 2.0;
    vec2 upVector = vec2(0.0, 1.0);
    float gradient = -1.0 * ((length(currentCoords) / (length(nResolution) / 2.0)) - 1.0);

    circle yinYang = circle(vec2(0.0, 0.0), 0.9 * nResolution.y / 2.0);
    circle bigYin = circle(vec2(0.0, yinYang.m_Radius / 2.0), yinYang.m_Radius / 2.0);
    circle smallYin = circle(bigYin.m_Center, bigYin.m_Radius * 0.3);
    circle bigYang = circle(vec2(0.0, -yinYang.m_Radius / 2.0), yinYang.m_Radius / 2.0);
    circle smallYang = circle(bigYang.m_Center, bigYang.m_Radius * 0.3);

    bigYin.m_Center = Rotate(bigYin.m_Center, -iTime);
    smallYin.m_Center = Rotate(smallYin.m_Center, -iTime);
    bigYang.m_Center = Rotate(bigYang.m_Center, -iTime);
    smallYang.m_Center = Rotate(smallYang.m_Center, -iTime);
    upVector = Rotate(upVector, -iTime);

    if(!IsInside(yinYang)){
        fragColor = vec4(gradient, 0.0, 0.0, 1.0);
    }else{
        if((IsInside(bigYin) && !IsInside(smallYin)) ||
           (!IsInside(bigYin) && !IsInside(bigYang) && IsLeft(upVector)) ||
           IsInside(smallYang)){
            fragColor = vec4(0.0, 0.0, 0.0, 1.0);
        }else{
            fragColor = vec4(1.0, 1.0, 1.0, 1.0);
        }
    }
}

#define PI 3.14159265
#define PHI 1.61803398875
#define EPSILON .0001
//If MOUSE_INPUT is set to true, the x coordinate of the mouse determines the 
// number of points drawn between the verticies. If false, the number of points
// will cyclically change over time.
#define MOUSE_INPUT false

#define DrawPoint(A,D,P,r) smoothstep( r, r - .01, length(cross(P-A, D)) / length(D) )
#define DistToLine(A1,D1,A2,D2) abs( dot(A2 - A1, cross(D1, D2))) / length(cross(D1, D2))


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    const int CUBE_SIZE = 20; 

    vec3 cube[] = vec3[](
    	vec3(.5, .5, .5),
    	vec3(.5, .5, -.5),
    	vec3(.5, -.5, .5),
    	vec3(.5, -.5, -.5),
    	vec3(-.5, .5, .5),
    	vec3(-.5, .5, -.5),
    	vec3(-.5, -.5, .5),
    	vec3(-.5, -.5, -.5),
    	vec3(0., .5 * PHI, .5 / PHI),
    	vec3(0., .5 * PHI, -.5 / PHI),
    	vec3(0., -.5 * PHI, .5 / PHI),
    	vec3(0., -.5 * PHI, -.5 / PHI),
    	vec3(.5 / PHI, 0, .5 * PHI),
    	vec3(.5 / PHI, 0, -.5 * PHI),
    	vec3(-.5 / PHI, 0, .5 * PHI),
    	vec3(-.5 / PHI, 0, -.5 * PHI),
    	vec3(.5 * PHI, .5 / PHI, 0),
    	vec3(.5 * PHI, -.5 / PHI, 0),
    	vec3(-.5 * PHI, .5 / PHI, 0),
    	vec3(-.5 * PHI, -.5 / PHI, 0)
    );

	vec2 uv = ( fragCoord - .5* iResolution.xy ) / iResolution.y;

    float t = iTime;

    vec3 origin = 7. * vec3(2.5 * sin(t / 2.), 1.5 + sin(t / .8) / 2., -2.5 * sin(t / (PI / 3.)));

    vec3 cameraFocus = vec3(0.);

    float zoom = 5. + sin(t) / 3.;

    vec3 f = normalize(cameraFocus - origin);
    vec3 r = vec3(f.z, 0., -f.x);
    vec3 u = cross(f, r);

    vec3 center = origin + f * zoom;
    vec3 i = center + uv.x * r + uv.y * u;
    vec3 direction = i - origin;

    vec3 point = vec3(0. + cos(t), 0., 1. + sin(t));

    float radius = .03;

    float d = 0.;

    float numDots;
    if (MOUSE_INPUT)
        numDots = floor(iMouse.x * .06);
    else
        numDots = 26. + round(25. * sin(t / 5.));

    for (int i = 0; i < CUBE_SIZE; ++ i)
        for (int j = 0; j < CUBE_SIZE; ++ j)
            if (i != j && (abs(length(cube[i] - cube[j]) - 1. / PHI) < EPSILON) && DistToLine(origin, direction, cube[i], cube[j] - cube[i]) < radius)
                for (float k = 0.; k < numDots; ++ k)
                    if (d < 1.)
                        d += DrawPoint(origin, direction, (k * cube[i] + (numDots - k) * cube[j]) / numDots, radius) / 15.;

    d += .25;

    uv *= 15. / zoom;
    fragColor = vec4(vec3(d), 0.) / 2.;

    if (d > .3)
    	fragColor += d * vec4(uv,0.5+0.5*sin(iTime),1.0) / 2.;
}

float circle(vec2 uv, float r, vec2 pos){
    uv += pos;

    float d = length(uv);

    float c = smoothstep(r, r-.02, d);
    return c;   
}

#define polar2xy(r,a) ( (r) * vec2( cos(a), sin(a) ) )

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv =  ( fragCoord - .5* iResolution.xy ) / iResolution.y;

	float mask = circle(uv, abs(.9*sin(.3*iTime)), vec2(0,0) );

    vec3 c = vec3(uv.x+.5, uv.y+.5, 0.5+0.5*sin(iTime));

    c *= mask;

    mask = circle( uv, .2, polar2xy( .1, 4.*iTime ) );
    vec3 c2 = vec3(5., 2.+0.5*sin(2.37*iTime), 1.);
    c2 *= mask;

    mask = circle( uv, .1, polar2xy( .1, 4.*iTime) );
    c2 *= 1.-mask;

    mask = circle( uv, .1, polar2xy( .45, 4.*iTime ) );
    c2 += mask;

    c *= mix(c, c2, .5);

	fragColor = vec4(c,1.0);
}

