const float pi = 3.14159265359;
const float triangleScale = 0.816497161855865; // ratio of edge length and height
const float period = 30.0;

float rand(vec2 co){
    return fract(sin(dot(co, vec2(12.9898, 78.233))) * 43758.5453);
}

vec2 getTriangleCoords(vec2 uv) {
    uv.y /= triangleScale;
    vec2 triangleCoords = floor(uv);
    triangleCoords.x *= 2.0;
    float mody = floor(mod(triangleCoords.y, 2.0));
    vec2 local = vec2(fract(uv.x + mody * 0.5) - 0.5, fract(uv.y));
    if (local.y > abs(local.x) * 2.0) {
        triangleCoords.x += local.x < 0.0 ? 1.0 : -1.0;
    }
    if (local.x >= 0.0 && mody == 0.0) {
        triangleCoords.x += 2.0;
    }
    return triangleCoords;
}

float getColor(vec2 uv) {
    uv /= 40.0;
    vec2 triangle = getTriangleCoords(uv);

    float angle = floor(6.0 * rand(triangle + 0.1)) / 3.0 * pi;
    vec2 direction = vec2(sin(angle), cos(angle));
    float projected = dot(direction, uv);    
    float progress = fract(iTime / period + rand(triangle + 0.2) + projected * 0.8 / period);

    return progress > 0.5 ? rand(triangle) : rand(triangle + 0.3);
}

void mainImage(out vec4 fragColor, in vec2 fragCoord ) {
	const float samples = 2.0;
    float value = 0.0;

    for (float x = 0.0; x < 1.0; x += 1.0 / samples) {
        for (float y = 0.0; y < 1.0; y += 1.0 / samples) {
			value += getColor(fragCoord + vec2(x, y));
        }
    }    
    value /= pow(samples, 2.0);

    value *= 0.4;

	fragColor = vec4(value);
}

/*
Info on curves:
http://jogamp.org/doc/gpunurbs2011/p70-santina.pdf

Implem in jogl:
https://github.com/sgothel/jogl/blob/master/src/jogl/classes/jogamp/graph/curve/opengl/shader/curverenderer01-pass1-curve_weight.glsl

Thanks to bezier curve shadertoys:
https://www.shadertoy.com/view/4tj3Dy
*/

#define EDGE   0.005
#define SMOOTH 0.0025

// signed distance function for Circle, for control points
float SDFCircle( in vec2 coords, in vec2 offset )
{
    coords -= offset;
    float v = coords.x * coords.x + coords.y * coords.y - EDGE*EDGE;
    vec2  g = vec2(2.0 * coords.x, 2.0 * coords.y);
    return v/length(g);
}

