void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec3 color = vec3(1.0,1.0,1.0);

    float aspectRatio = iResolution.x / iResolution.y;
    vec2 percent = ((fragCoord.xy / iResolution.xy) - vec2(0.25,0.5));
    percent.x *= aspectRatio;

    vec2 mouse = (iMouse.xy / iResolution.xy) - vec2(0.25,0.5);
    mouse.x *= aspectRatio;
    vec2 A = vec2(0.0,-0.4);
    vec2 B = length(iMouse.xy) > 0.0 ? mouse : vec2(0.3,0.4);
    vec2 C = vec2(1.0,-0.4);
    float w0 = 1.;
    float w1 = (sin(iTime)+1.)*0.5;
    float w2 = 1.;

    // Compute vectors
    vec2 v0 = C - A;
    vec2 v1 = B - A;
    vec2 v2 = percent - A;

    // Compute dot products
    float dot00 = dot(v0, v0);
    float dot01 = dot(v0, v1);
    float dot02 = dot(v0, v2);
    float dot11 = dot(v1, v1);
    float dot12 = dot(v1, v2);

    // Compute barycentric coordinates
    float invDenom = 1.0 / (dot00 * dot11 - dot01 * dot01);
    float u = (dot11 * dot02 - dot01 * dot12) * invDenom;
    float v = (dot00 * dot12 - dot01 * dot02) * invDenom;

    // NURBS calculations
    float w = (1.0 - u - v);
    vec2 rtex = u * vec2(0.0,0.0) + v * vec2(0.5,0.5) + w * vec2(1.0,0.0);
    float nu = rtex.x;
    float nv = rtex.y;

    // Basic position
    float pd = ( (w0 - 2.*w1 + w2)*nu*nu + 2.*(w1-w0)*nu + w0 );
    float pos = nv - (w1 * nu * (1.- nu)) / pd ;

    // Smooth interpolation, no aliasing
    vec2 dtx = dFdx(rtex);
    vec2 dty = dFdy(rtex);
    float aph = 2.0 - 2.0*w1;
    float gd = (aph*rtex.x*rtex.x + 2.0*rtex.x + 1.0)*(aph*rtex.x*rtex.x + 2.0*rtex.x + 1.0);
    vec2 f = vec2((dtx.y - (w*dtx.x*(1.0 - 2.0*rtex.x))/gd), (dty.y - (w*dty.x*(1.0 - 2.0*rtex.x))/gd));
    float a = clamp(0.5 - ( pos/length(f) ) * sign(w2), 0.0, 1.0);
    color = vec3(mix(1.,0.5,a))*0.5;

    // if it's outside the triangle, lighten it a bit
    color += ((u >= 0.0) && (v >= 0.0) && (u + v < 1.0)) ? 0.0 : 0.7;

    // render control points
    float dist = SDFCircle(percent, A);
    dist = max(dist, 0.0);
    dist = smoothstep(EDGE,EDGE + SMOOTH,dist);
    color *= mix(vec3(1.0,0.0,0.0),vec3(1.0,1.0,1.0),dist);
    dist = SDFCircle(percent, B);
    dist = max(dist, 0.0);
    dist = smoothstep(EDGE,EDGE + SMOOTH,dist);
    color *= mix(vec3(0.0,1.0,0.0),vec3(1.0,1.0,1.0),dist);
    dist = SDFCircle(percent, C);
    dist = max(dist, 0.0);
    dist = smoothstep(EDGE,EDGE + SMOOTH,dist);
    color *= mix(vec3(0.0,0.0,1.0),vec3(1.0,1.0,1.0),dist);

    fragColor = vec4(clamp(color,0.0,1.0),1.0);
}
