    uv.x *= iResolution.x / iResolution.y; // aspect ratio

    float time = iTime;

    float x = uv.x;
    float coef = sin(x + time) * 0.5;

    float y = uv.y + coef;



    float color;
    float blur = 0.1;
    color = Rectangle(vec2(x, y), -3., 3., -.5, .5, blur);

    fragColor = vec4(vec3(color), 1.);
}

