float lineSign(vec3 p1, vec3 p2, vec3 p3)
{
    return (p1.x - p3.x) * (p2.y - p3.y) - (p2.x - p3.x) * (p1.y - p3.y);
}

bool pointInTriangle(vec3 pt, vec3 v1, vec3 v2, vec3 v3)
{
    bool b1, b2, b3;

    b1 = lineSign(pt, v1, v2) < 0.0;
    b2 = lineSign(pt, v2, v3) < 0.0;
    b3 = lineSign(pt, v3, v1) < 0.0;

    return ((b1 == b2) && (b2 == b3));
}

